    "contracts/Replicator.sol": {
      "content": "// SPDX-License-Identifier: Unlicense

pragma solidity ^0.8.0;

import { NonblockingLzApp } from "@layerzerolabs/solidity-examples/contracts/lzApp/NonblockingLzApp.sol";
import { IERC721A } from "erc721a/contracts/IERC721A.sol";
import { ERC721A } from "erc721a/contracts/ERC721A.sol";
import { ERC721AQueryable } from "erc721a/contracts/extensions/ERC721AQueryable.sol";

import { ICassette } from "./interfaces/ICassette.sol";

error Replicator_IncorrectValue();
error Replicator_InvalidValue();
error Replicator_InvalidMintState();
error Replicator_SenderNotAllowed();
error Replicator_Soulbound();
error Replicator_WithdrawFailed();

/**
                        .             :++-
                       *##-          +####*          -##+
                       *####-      :%######%.      -%###*
                       *######:   =##########=   .######*
                       *#######*-#############*-*#######*
                       *################################*
                       *################################*
                       *################################*
                       *################################*
