    "contracts/FoundersStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@thirdweb-dev/contracts/extension/ContractMetadata.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract OfficialFoundersCollectionStaking is
	ReentrancyGuard,
	ContractMetadata,
	Ownable
{
	using SafeERC20 for IERC20;

	IERC20 public rewardsToken;
	IERC721 public nftCollection;

	address public deployer;

	// Date Start and Finish
	uint256 public startDate = 1665018000; //  2022-10-05 22:00:00 GMT-03:00
	uint256 public endDate = 1668560400; // 2022-11-10 22:00:00 GMT-03:00

	// Rewards per hour per token deposited in wei.
	uint256 public rewardsPerHour = 3339120370370370000;
	uint256 public legendaryRewardsPerHour = 3338675213675210000;
	uint256 public epicRewardsPerHour = 806846509971510000;
	uint256 public rareRewardsPerHour = 500801282051282000;

	function setRewardsToken(IERC20 _rewardsToken) public onlyOwner {
		rewardsToken = _rewardsToken;
	}

	function setNftCollection(IERC721 _nftCollection) public onlyOwner {
		nftCollection = _nftCollection;
	}

	function setRewardsPerHour(uint256 _rewardsPerHour) public onlyOwner {
		rewardsPerHour = _rewardsPerHour;
	}

	function setLegendaryRewardsPerHour(uint256 _legendaryRewardsPerHour)
		public
		onlyOwner
	{
		legendaryRewardsPerHour = _legendaryRewardsPerHour;
	}

	function setEpicRewardsPerHour(uint256 _epicRewardsPerHour)
		public
		onlyOwner
	{
		epicRewardsPerHour = _epicRewardsPerHour;
	}

	function setRareRewardsPerHour(uint256 _rareRewardsPerHour)
		public
		onlyOwner
	{
		rareRewardsPerHour = _rareRewardsPerHour;
	}

	function setStartDate(uint256 _startDate) public onlyOwner {
		startDate = _startDate;
	}

	function setEndDate(uint256 _endDate) public onlyOwner {
		endDate = _endDate;
	}

	// Withdraw ERC20 tokens from the contract

	function withdrawToken(address _tokenContract, uint256 _amount)
		external
		onlyOwner
	{
		IERC20 tokenContract = IERC20(_tokenContract);

		// transfer the token from address of this contract
		// to address of the user (executing the withdrawToken() function)
		tokenContract.transfer(msg.sender, _amount);
	}

	// Withdraw ERC20 tokens from the contract

	function withdrawNFT(address _tokenContract, uint256 _tokenId)
		external
		onlyOwner
	{
		IERC721 nftContract = IERC721(_tokenContract);

		// transfer the token from address of this contract
		// to address of the user (executing the withdrawToken() function)
		nftContract.transferFrom(address(this), msg.sender, _tokenId);
	}

	// Constructor function to set the rewards token and the NFT collection addresses
	constructor(IERC721 _nftCollection, IERC20 _rewardsToken) {
		nftCollection = _nftCollection;
		rewardsToken = _rewardsToken;

		deployer = msg.sender;
	}

	struct StakedToken {
		address staker;
		uint256 tokenId;
		string rarity;
	}

	// Staker info
	struct Staker {
		// Amount of tokens staked by the staker
		uint256 amountStaked;
		// Staked token ids
		StakedToken[] stakedTokens;
		// Last time of the rewards were calculated for this user
		uint256 timeOfLastUpdate;
		// Calculated, but unclaimed rewards for the User. The rewards are
		// calculated each time the user writes to the Smart Contract
		uint256 unclaimedRewards;
	}

	// Mapping of User Address to Staker info
	mapping(address => Staker) public stakers;

	// Mapping of Token Id to staker. Made for the SC to remeber
	// who to send back the ERC721 Token to.
	mapping(uint256 => address) public stakerAddress;

	// If address already has ERC721 Token/s staked, calculate the rewards.
	// Increment the amountStaked and map msg.sender to the Token Id of the staked
	// Token to later send back on withdrawal. Finally give timeOfLastUpdate the
	// value of now.
	function stake(uint256 _tokenId) external nonReentrant {
		// Wallet must own the token they are trying to stake
		require(
			nftCollection.ownerOf(_tokenId) == msg.sender,
			"You don't own this token!"
		);

		// If wallet has tokens staked, calculate the rewards before adding the new token
		if (stakers[msg.sender].amountStaked > 0) {
			uint256 rewards = calculateRewards(msg.sender);
			stakers[msg.sender].unclaimedRewards += rewards;
		}

		// Transfer the token from the wallet to the Smart contract
		nftCollection.transferFrom(msg.sender, address(this), _tokenId);
		string memory nRarity = _tokenId <= 14 ? "Legendary" : _tokenId <= 1126
			? "Epic"
			: "Rare";

		// Create StakedToken
		StakedToken memory stakedToken = StakedToken(
			msg.sender,
			_tokenId,
			nRarity
		);

		// Add the token to the stakedTokens array
		stakers[msg.sender].stakedTokens.push(stakedToken);

		// Increment the amount staked for this wallet
		stakers[msg.sender].amountStaked++;

		// Update the mapping of the tokenId to the staker's address
		stakerAddress[_tokenId] = msg.sender;

		// Update the timeOfLastUpdate for the staker
		stakers[msg.sender].timeOfLastUpdate = block.timestamp;
	}

	// Check if user has any ERC721 Tokens Staked and if they tried to withdraw,
	// calculate the rewards and store them in the unclaimedRewards
	// decrement the amountStaked of the user and transfer the ERC721 token back to them
	function withdraw(uint256 _tokenId) external nonReentrant {
		// Make sure the user has at least one token staked before withdrawing
		require(
			stakers[msg.sender].amountStaked > 0,
			"You have no tokens staked"
		);

		// Wallet must own the token they are trying to withdraw
		require(
			stakerAddress[_tokenId] == msg.sender,
			"You don't own this token!"
		);

		// Update the rewards for this user, as the amount of rewards decreases with less tokens.
		uint256 rewards = calculateRewards(msg.sender);
		stakers[msg.sender].unclaimedRewards += rewards;

		// Find the index of this token id in the stakedTokens array
		uint256 index = 0;
		for (uint256 i = 0; i < stakers[msg.sender].stakedTokens.length; i++) {
			if (
				stakers[msg.sender].stakedTokens[i].tokenId == _tokenId &&
				stakers[msg.sender].stakedTokens[i].staker != address(0)
			) {
				index = i;
				break;
			}
		}

		// Set this token's .staker to be address 0 to mark it as no longer staked
		stakers[msg.sender].stakedTokens[index].staker = address(0);

		// Decrement the amount staked for this wallet
		stakers[msg.sender].amountStaked--;

		// Update the mapping of the tokenId to the be address(0) to indicate that the token is no longer staked
		stakerAddress[_tokenId] = address(0);

		// Transfer the token back to the withdrawer
		nftCollection.transferFrom(address(this), msg.sender, _tokenId);

		// Update the timeOfLastUpdate for the withdrawer
		stakers[msg.sender].timeOfLastUpdate = block.timestamp;
	}

	// Calculate rewards for the msg.sender, check if there are any rewards
	// claim, set unclaimedRewards to 0 and transfer the ERC20 Reward token
	// to the user.
	function claimRewards() external {
		uint256 rewards = calculateRewards(msg.sender) +
			stakers[msg.sender].unclaimedRewards;

		require(rewards > 0, "You have no rewards to claim");
		require(
			rewardsToken.balanceOf(address(this)) > rewards,
			"There's not enough funds to complete the claim or Claim phase hasn't started"
		);

		stakers[msg.sender].timeOfLastUpdate = block.timestamp;
		stakers[msg.sender].unclaimedRewards = 0;
		rewardsToken.safeTransfer(msg.sender, rewards);
	}

	//////////
	// View //
	//////////

	function availableRewards(address _staker) public view returns (uint256) {
		uint256 rewards = calculateRewards(_staker) +
			stakers[_staker].unclaimedRewards;
		return rewards;
	}

	function getStakedTokens(address _user)
		public
		view
		returns (StakedToken[] memory)
	{
		// Check if we know this user
		if (stakers[_user].amountStaked > 0) {
			// Return all the tokens in the stakedToken Array for this user that are not -1
			StakedToken[] memory _stakedTokens = new StakedToken[](
				stakers[_user].amountStaked
			);
			uint256 _index = 0;

			for (uint256 j = 0; j < stakers[_user].stakedTokens.length; j++) {
				if (stakers[_user].stakedTokens[j].staker != (address(0))) {
					_stakedTokens[_index] = stakers[_user].stakedTokens[j];
					_index++;
				}
			}

			return _stakedTokens;
		}
		// Otherwise, return empty array
		else {
			return new StakedToken[](0);
		}
	}

	/////////////
	// Internal//
	/////////////

	// Calculate rewards for param _staker by calculating the time passed
	// since last update in hours and mulitplying it to ERC721 Tokens Staked
	// and rewardsPerHour.
	function calculateRewards(address _staker)
		internal
		view
		returns (uint256 _rewards)
	{
		uint256 endD = 0;
		uint256 iniD = 0;
		uint256 _rewardsCalc = 0;
		uint256 _timeAmount = 0;

		uint256 legNfts = 0;
		uint256 epiNfts = 0;
		uint256 rarNfts = 0;

		endD = block.timestamp >= endDate ? endDate : block.timestamp;
		iniD = stakers[_staker].timeOfLastUpdate <= startDate
			? startDate
			: stakers[_staker].timeOfLastUpdate;

		_timeAmount = iniD > endD ? 0 : endD - iniD;

		if (_timeAmount > 0) {
			if (stakers[_staker].amountStaked > 0) {
				for (
					uint256 j = 0;
					j < stakers[_staker].stakedTokens.length;
					j++
				) {
					if (
						stringsEquals(
							stakers[_staker].stakedTokens[j].rarity,
							"Legendary"
						) && stakers[_staker].stakedTokens[j].staker == _staker
					) legNfts++;
					if (
						stringsEquals(
							stakers[_staker].stakedTokens[j].rarity,
							"Epic"
						) && stakers[_staker].stakedTokens[j].staker == _staker
					) epiNfts++;
					if (
						stringsEquals(
							stakers[_staker].stakedTokens[j].rarity,
							"Rare"
						) && stakers[_staker].stakedTokens[j].staker == _staker
					) rarNfts++;
				}
			}

			_rewardsCalc =
				(_timeAmount *
					(legNfts *
						legendaryRewardsPerHour +
						epiNfts *
						epicRewardsPerHour +
						rarNfts *
						rareRewardsPerHour)) /
				3600;
		}

		return _rewardsCalc;
	}

	function _canSetContractURI()
		internal
		view
		virtual
		override
		returns (bool)
	{}

	function stringsEquals(string memory s1, string memory s2)
		private
		pure
		returns (bool)
	{
		bytes memory b1 = bytes(s1);
		bytes memory b2 = bytes(s2);
		uint256 l1 = b1.length;
		if (l1 != b2.length) return false;
		for (uint256 i = 0; i < l1; i++) {
			if (b1[i] != b2[i]) return false;
		}
		return true;
	}
}
