      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


/**
 * @title Proxy Contract
 */
contract UpgradeableProxy {
    address public currentImplementation;
    address public admin;

    constructor(address _initialImplementation) {
        currentImplementation = _initialImplementation;
        admin = msg.sender;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only the admin can call this function");
        _;
    }

    function upgradeImplementation(address _newImplementation) external onlyAdmin {
        currentImplementation = _newImplementation;
    }

    fallback() external payable {
        address _impl = currentImplementation;

        assembly {
            calldatacopy(0, 0, calldatasize())
            let result := delegatecall(gas(), _impl, 0, calldatasize(), 0, 0)

            returndatacopy(0, 0, returndatasize())

            switch result
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }
}"
