            Dragon memory currentDragon = dragons[_id];
            return string(abi.encodePacked(
                'data:application/json;base64,', Base64.encode(bytes(abi.encodePacked(
                   '{"name":"',
                   currentDragon.name,
                   '", "image":"',
                   currentDragon.imageURI,
                   '", "attributes":['
                   '{"trait_type":"',
                   'parentId',
                   '", "value":"',
                   Strings.toString(currentDragon.parentId),
                   '"},'
                   '{"trait_type":"',
                   'level',
                   '", "value":"',
                   Strings.toString(currentDragon.level),
                   '"},'
                   '{"trait_type":"',
                   'xp',
                   '", "value":"',
                   Strings.toString(currentDragon.xp),
                   '"}'
                   ']'
                   '}'
                   )))));
         }

         // Only owner of the nft is able to get the hidden traits of the nft
        function getDragonTraits(uint _id) public view returns(string[] memory){
            require(_ownerOf(_id), "Only owner of this dragon can access this");
            return dragons[_id].traits;
        }

        // It is used to make sure that function called by is the actual owner of the nft
        function _ownerOf(uint256 tokenId) public view returns (bool) {
            return balanceOf(msg.sender, tokenId) != 0;
        }

        // Moderator will be able to mint numbers of nft and send them to mulitple addresses. It is used for transferring nfts to the list of bidders
        function mintOnBidding(address[] calldata _receivers, uint256[] calldata _parentIds,string[] memory _imageUrls) public onlyModerator{
          for (uint8 i = 0; i < _receivers.length; i++) {
            _dragonId.increment();
            _mint(_receivers[i], _dragonId.current(), mint_limit, "");
            string[] memory arr;
            Dragon memory newDragon = Dragon({
                name: "",
                owner: _receivers[i],
                tokenId: _dragonId.current(),
                parentId: _parentIds[i],
                level: 0,
                traits: arr,
                xp: 0,
                imageURI: _imageUrls[i]
            });
            dragons[_dragonId.current()] = newDragon;
          }
        }

        // Users will be able to mint their nft when they send the mint price
        function mint(address _receiver, uint256 _parentId,string memory _imageUrl) public payable {
            require(msg.value >= mint_price, "not enough price");
            require((_dragonId.current() < mint_total) || (_moderator == msg.sender || _owner == msg.sender), "Cannot mint more dragons");
            _dragonId.increment();
            _mint(_receiver, _dragonId.current(), mint_limit, "");
            if(_dragonId.current() >= bid_max_supply){
                claimAChildAvailable = true;
            }
            string[] memory arr;
            Dragon memory newDragon = Dragon({
                name: "",
                owner: _receiver,
                tokenId: _dragonId.current(),
                parentId: _parentId,
                level: 0,
                traits: arr,
                xp: 0,
                imageURI: _imageUrl
            });
            dragons[_dragonId.current()] = newDragon;
        }

        // Returns data in non uri form to be used for backend and frontend
        function getDragonDetails(uint256 _id) public view returns(string memory,address,uint256,uint256,uint256,string memory){
            Dragon memory dragon = dragons[_id];
            return (
                dragon.name,
                dragon.owner,
                dragon.tokenId,
                dragon.level,
                dragon.xp,
                dragon.imageURI
            );
        }

        // Moderator can change the price of minting
        function changeMintPrice(uint256 newMintPrice) public onlyModerator{
            require(newMintPrice!=mint_price,"New mint price cannot be same with the old mint price");
            mint_price = newMintPrice;
        }

        // Allows moderator to change the bid base price for bidding purposes
        function changeBidBasePrice(uint256 newBidBasePrice) public onlyModerator{
            require(newBidBasePrice!=bid_base_price,"New bid price cannot be same with the old bid price");
            bid_base_price = newBidBasePrice;
        }

        // After submitting fee, the user will be able to claim child
        function changeClaimAChildFee(uint256 _claim_child_fee) public onlyModerator{
            require(claim_child_fee!=_claim_child_fee,"New fee price cannot be same with the old fee price");
            claim_child_fee = _claim_child_fee;
        }

        // Helps us to change the mint amount of nfts
        function changeMintTotal(uint256 _newMintTotal) public onlyModerator{
            require(mint_total!=_newMintTotal,"New mint total cannot be same with the old mint total");
            mint_total = _newMintTotal;
        }

        // Changes the maximum bid supply
        function changeBidSupply(uint256 _newBidTotal) public onlyModerator{
            require(bid_max_supply!=_newBidTotal,"New bid total cannot be same with the old bid total");
            bid_max_supply = _newBidTotal;
        }

        // Returns the total supply of nfts
        function totalSupply() public view returns (uint256) {
            uint256 totalMintedNFTs = _dragonId.current();
            return totalMintedNFTs;
        }

        // Owner can withdraw matics the user has deposited as a fee to buy/mint nfts
        function withdraw(uint256 _amount) public nonReentrant onlyOwner {
            uint256 valueToWithdraw = address(this).balance.sub(biddingAmount);
            require(_amount<=valueToWithdraw,"Invalid amount");

            (bool success, ) = payable(_owner).call{value: _amount}("");
            require(success);
        }

        // User can submit bid amount to take part in the bidding process
        function bid() public payable{
            require(isBiddingAvailable, "Bidding is not available at the moment");
            require(msg.value >= bid_base_price, "Bid value is too low");
            uint256 fee = msg.value.mul(BENIFICIARY_PERC).div(100);
            (bool success, ) = payable(benificiary).call{value: fee}("");
            require(success);
            uint256 _amountToBid = msg.value.sub(fee);
            biddingAmount = biddingAmount.add(_amountToBid);
        }

        // Enabling or disabling bid availability
        function setBiddingAvailableValue(bool _value) public onlyModerator{
            require(_value != isBiddingAvailable, "Bid value cannot be same with the current value");
            isBiddingAvailable = _value;
            mint_total = _dragonId.current();
            if(_value) {
                mint_price = 0;
            }
        }

        // Enabling or disabling claimAChildAvailable
        function setClaimAChildAvailableValue(bool _value) public onlyModerator{
            require(_value != claimAChildAvailable, "claimAChildAvailable value cannot be same with the current value");
            claimAChildAvailable = _value;
        }


        // Returns balance of the smart contract
        function getBalance() public view returns(uint256){
            return address(this).balance;
        }

        // User sumbits fee to claim a child nft
        function submitClaimAChildFee() public payable{
            require(claimAChildAvailable, "Child dragon cannot be minted at the moment");
            require(msg.value >= claim_child_fee, "Please send 1 Matic");
        }

        // Function to change the owner
        function changeOwnerAddress(address _newOwner) public onlyOwner{
            require(_newOwner!=address(0) && _newOwner != _owner,"Invalid address for new owner");
            _owner = _newOwner;
        }

        //Function to change the moderator address
        function changeModeratorAddress(address _newModerator) public onlyModerator{
            require(_newModerator!=address(0) && _newModerator != _moderator,"Invalid address for new moderator");
            _moderator = _newModerator;
        }

        // Function to change the benificiary address
        function changeBenificiaryAddress(address _newBenifiary) public onlyOwner{
            require(_newBenifiary!=address(0) && _newBenifiary != benificiary,"Invalid address for new benificary");
            benificiary = _newBenifiary;
        }

        // Resets the bidding amount to 0 and availability to false
        function resetBiddingAmount() public onlyModerator{
            require(_dragonId.current() >= bid_max_supply,"Cannot reset bidding at the moment");
            biddingAmount = 0;
            isBiddingAvailable = false;
        }

        // Returns the bidding amount
        function getBiddingAmount() public onlyModerator view returns(uint256){
            return biddingAmount;
        }


        // Function to return the amount that was deposited by the users
        function transferOutbidAmount(address _receiver,uint256 _amount) public nonReentrant onlyModerator{
          require(!isBiddingAvailable && _dragonId.current() >= bid_max_supply,"Cannot transfer amount at the moment");
          require(_amount > 0,"Amount must be greater than 0");
          require(_amount <= address(this).balance,"Amount cannot be greater than the contract balance");

          (bool success, ) = payable(_receiver).call{value: _amount}("");
          require(success);
          biddingAmount = biddingAmount.sub(_amount);
        }

    }
