pragma solidity 0.8.17;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";

contract Randomness is VRFConsumerBaseV2, ConfirmedOwner {
  // ? ============== STRUCTURES ==================
  event RequestSent(uint requestId, uint32 numWords);
  event RequestFulfilled(uint requestId, uint[] randomWords);

  struct RequestStatus {
    uint status; // 0 - not exists | 1 - exists but not fulfilled | 2 - exists and is fulfilled
    uint[] randomWords;
  }

  // ? ============== CUSTOMS BY NETWORK ==================
  // ! POLYGON MAINNET ---------------------------------------
  address constant COORDINATOR_ADDRESS = 0xAE975071Be8F8eE67addBC1A82488F1C24858067;
  // bytes32 keyHash = 0x6e099d640cde6de9d40ac749b4b594126b0169747122711109c9985d47751f93; // 200
  bytes32 keyHash = 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd; // 500
  // bytes32 keyHash = 0xd729dc84e21ae57ffb6be0053bf2b0668aa2aaf300a2a7b2ddf7dc0bb6e875a8; // 1000

  // ! POLYGON TESTNET ---------------------------------------
  // address constant COORDINATOR_ADDRESS = 0x7a1BaC17Ccc5b313516C5E16fb24f7659aA5ebed;
  // bytes32 keyHash = 0x4b09e658ed251bcafeebbc69400383d49f344ace09b9576fe248bb02c003fe9f; // 500

  // ? ============== VARIABLES N CONSTANTS ==================

  // ! CHANCE BASED ON SUBSCRIPTIONS -------------------------
  uint16 constant requestConfirmations = 3;
  uint64 constant s_subscriptionId = 908;

  VRFCoordinatorV2Interface constant COORDINATOR = VRFCoordinatorV2Interface(COORDINATOR_ADDRESS);

  mapping(address => uint) internal _permissions;
  mapping(uint => RequestStatus) public s_requests;
  mapping(uint => uint) public s_uniqueIds;
  uint[] public requestIds;
  uint public lastRequestId;

  uint8 constant _FILLED_SHIFT_BIT = 43;

  constructor() VRFConsumerBaseV2(COORDINATOR_ADDRESS) ConfirmedOwner(msg.sender) {}

  function requestRandomWords(uint32 uniqueId, uint32 numWords) external isOperator returns (uint requestId) {
    require(numWords <= 500, "numWords exceeds limit");
    require(s_uniqueIds[uniqueId] == 0, "Unique Id registered");

    uint32 callbackGasLimit = numWords * 24000 + 60000;

    requestId = COORDINATOR.requestRandomWords(keyHash, s_subscriptionId, requestConfirmations, callbackGasLimit, numWords);
    s_uniqueIds[uniqueId] = requestId;
    s_requests[requestId] = RequestStatus({randomWords: new uint[](0), status: (block.number << 3) + 1});

    requestIds.push(requestId);
    lastRequestId = requestId;
    emit RequestSent(requestId, numWords);
    return requestId;
  }

  function fulfillRandomWords(uint _requestId, uint[] memory _randomWords) internal override {
    uint status = s_requests[_requestId].status;
    if (status == 0) revert("request not found");
    if ((status & 7) > 1) revert("request filled");
    s_requests[_requestId].status = status + 1 + (block.number << _FILLED_SHIFT_BIT);
    s_requests[_requestId].randomWords = _randomWords;
    emit RequestFulfilled(_requestId, _randomWords);
  }

  function getRequestStatus(uint _requestId) public view returns (bool fulfilled, uint requestId, uint blockRequested, uint blockFilled, uint[] memory randomWords) {
    require(s_requests[_requestId].status >= 1, "request not found");
    RequestStatus memory request = s_requests[_requestId];
    return ((request.status & 7) == 2, _requestId, ((((1 << _FILLED_SHIFT_BIT) - 1) & request.status) >> 3), request.status >> _FILLED_SHIFT_BIT, request.randomWords);
  }

  function getRequestStatusByUniqueId(uint _uniqueId) external view returns (bool fulfilled, uint request, uint blockRequested, uint blockFilled, uint[] memory randomWords) {
    uint _requestId = s_uniqueIds[_uniqueId];
    require(_requestId != 0, "request does not exists");
    return getRequestStatus(_requestId);
  }

  function changeKeyHash(bytes32 _keyHash) external isAdmin {
    keyHash = _keyHash;
  }

  // ? ============== Permission Controller ==================
  function setPermission(address wallet, uint8 typeIndex, bool state) external isAdmin {
    _permissions[wallet] = setMapAttribute(_permissions[wallet], typeIndex, state);
  }

  function checkPermission(uint8 typeIndex) private view {
    require(checkMapAttribute(_permissions[msg.sender], typeIndex) || owner() == msg.sender, "Wallet does not have permission");
  }

  function checkMapAttribute(uint mapValue, uint8 shift) internal pure returns (bool) {
    return (mapValue >> shift) & 1 == 1;
  }

  function setMapAttribute(uint mapValue, uint8 shift, bool include) internal pure returns (uint) {
    return include ? (1 << shift) | mapValue : (1 << shift) ^ (type(uint).max & mapValue);
  }

  // ? ============== Modifiers ==================
  modifier isAdmin() {
    checkPermission(0);
    _;
  }

  modifier isOperator() {
    checkPermission(1);
    _;
  }
}
