    "contracts/launchpad/Launchpad.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

import "../interfaces/ILaunchpadFactory.sol";
import "../interfaces/ILaunchpad.sol";
import "../interfaces/IMulticall.sol";

/// @title Launchpad asbtract contract
/// @notice Core logic for launchpad
abstract contract Launchpad is ILaunchpad, OwnableUpgradeable, IERC721Receiver, EIP712Upgradeable, IMulticall {
    using SafeERC20 for IERC20; 

    LaunchpadData internal _launchpadData;
    ILaunchpadFactory private _factory;

    uint32 private _totalRegistered;
    uint32 private _totalRedeemed;

    uint256 private _sharingFee;
    uint256 private _protocolFee;

    mapping(address => bool) _registerAddresses;
    mapping(address => uint256) _totalNftRedeemed;

    bytes32 constant WHITELIST_TYPEHASH= keccak256("Whitelist(address user)");

    modifier onlyRegisterTime() {
        require(block.timestamp >= _launchpadData.registerStartTimestamp, "Launchpad: Invalid registration time");
        require(block.timestamp < _launchpadData.registerEndTimestamp, "Launchpad: Invalid registration time");
        _;
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    modifier onlyRedeemTime() {
        require(block.timestamp >= _launchpadData.redeemStartTimestamp, "Launchpad: Invalid redeem time");
        require(block.timestamp < _launchpadData.redeemEndTimestamp, "Launchpad: Invalid redeem time");
        _;
    }

    modifier onlyClaimTime() {
        require(block.timestamp >= _launchpadData.claimStartTimestamp, "Launchpad: Invalid claim time");
        _;
    }

    modifier onlyRegister() {
        require(_registerAddresses[_msgSender()], "Launchpad: Only register address");
        _;
    }

    modifier onlyActiveLaunchpad() {
        require(_launchpadData.isActive, "Launchpad: Launchpad is not active");
        _;
    }

    modifier onlyFactoryOwner() {
        require(_msgSender() == Ownable(address(_factory)).owner(), "Launchpad: Caller is not factory owner");
        _;
    }

    /**
     * @dev Init launchpad.
     */
    function _launchpadInit(address factory, uint256 sharingFee, uint256 protocolFee) internal onlyInitializing {
        __EIP712_init("Launchpad", "1");
        require(protocolFee <= 5000, "Launchpad: Can not set protocol fee over 50%");

        _factory = ILaunchpadFactory(factory);
        _sharingFee = sharingFee;
        _protocolFee = protocolFee;
    } 

    /**
     * @dev Virtual function for redeem token
     */
    function _redeemToken(uint256 amount) internal virtual;

    /**
     * @dev Virtual function for redeem token
     */
    function _claimToken(uint256 tokenId) internal virtual;

    /**
     * @dev Virtual function for view pending token
     */
    function pendingToken(uint256 tokenId) external virtual view returns (address);

    /**
     * @dev Claim redeem fee and sent protocol fee
     */
    function _claimFee(uint256 total) internal {
        uint256 totalFeeRedeem = _launchpadData.feeRedeem * total;
        if (totalFeeRedeem > 0) {
            address feeAddress = _factory.getFeeAddress();
            uint256 totalSystemFee = (totalFeeRedeem * _protocolFee / 10000) + _sharingFee;
            require(totalSystemFee < totalFeeRedeem, "Launchpad: protocol fee is over launchpad fee");
            if (_launchpadData.feeRedeemAddress == address(0)) {
                require(msg.value >= totalFeeRedeem, "Launchpad: Exceed fee");
                (bool sent,) = payable(feeAddress).call{value: totalSystemFee}("");
                require(sent, "Launchpad: Fail to send protocol fee");
            } else {
                IERC20 token = IERC20(_launchpadData.feeRedeemAddress);
                token.safeTransferFrom(_msgSender(), address(this), totalFeeRedeem);
                token.safeTransfer(feeAddress, totalSystemFee);
            }
        }
    }

    /** 
     * @dev return types data hash
     */
    function _getWhitelistHash(address user) internal view returns (bytes32) {
        return _hashTypedDataV4(
            keccak256(abi.encode(
                WHITELIST_TYPEHASH,
                user
            ))
        );
    }

    /**
     * @dev Update launchpad data
     */
    function setLaunchpad(LaunchpadData memory launchpadData) external override onlyOwner {
        if (_launchpadData.registerStartTimestamp != 0) {
            require(block.timestamp < _launchpadData.registerStartTimestamp, "Launchpad: Launchpad run");
        }

        require(block.timestamp <= launchpadData.registerStartTimestamp, "Launchpad: Time must be set in the future");
        require(launchpadData.registerStartTimestamp < launchpadData.registerEndTimestamp, "Launchpad: Invalid registration time");
        require(launchpadData.redeemStartTimestamp < launchpadData.redeemEndTimestamp, "Launchpad: Invalid mint time");

        _launchpadData = launchpadData;

        emit SetLaunchpad(launchpadData);
    }

    /**
     * @dev Update sharing fee
     */
    function setSharingFee(uint256 sharingFee) external onlyOwner {
        _sharingFee = sharingFee;

        emit SetSharingFee(sharingFee);
    }

    /**
     * @dev Update protocol fee
     */
    function setProtocolFee(uint256 protocolFee) external onlyFactoryOwner {
        require(protocolFee <= 5000, "Launchpad: Can not set protocol fee over 50%");

        // we don't cover sharing fee
        _protocolFee = protocolFee;

        emit SetProtocolFee(protocolFee);
    }

    /**
     * @dev Update launchpad status for emergency
     */
    function setLaunchpadStatus(bool isActive) external onlyOwner {
        _launchpadData.isActive = isActive;

        emit SetLaunchpadStatus(isActive);
    }

    /**
     * @dev Register function for user, user need to register before redeem
     */
    function register(bytes memory signature) onlyRegisterTime onlyActiveLaunchpad external {
        require(!_registerAddresses[_msgSender()], "Launchpad: User registered");
        if (_launchpadData.whitelistAuthority != address(0)) {
            bytes32 digest = _getWhitelistHash(_msgSender());
            require(_launchpadData.whitelistAuthority == ECDSAUpgradeable.recover(digest, signature), "Launchpad: Invalid Signature");
        }

        require(_launchpadData.maxRegister == 0 || _totalRegistered + 1 <= _launchpadData.maxRegister, "Launchpad: Reach max register");
        ++_totalRegistered;

        _registerAddresses[_msgSender()] = true;

        emit Register(_msgSender());
    }

    /**
     * @dev Redeem function, user need to register before redeem
     */
    function redeem(uint256 amount) onlyRedeemTime onlyRegister onlyActiveLaunchpad external payable {
        require(_launchpadData.maxPerUser == 0 || _totalNftRedeemed[_msgSender()] + amount <= _launchpadData.maxPerUser, "Launchpad: Over max nft per user");
        require(_launchpadData.maxRedeem == 0 || _totalRedeemed + amount <= _launchpadData.maxRedeem, "Launchpad: Reach max redeem");
        _claimFee(amount);

        _totalRedeemed = _totalRedeemed + uint32(amount);
        _totalNftRedeemed[_msgSender()] = _totalNftRedeemed[_msgSender()] + amount;

        _redeemToken(amount);

        emit Redeem(_msgSender(), amount);
    }

    /**
     * @dev Claim function, for claim pending amount
     */
    function claim(uint256 tokenId) external onlyClaimTime {
        _claimToken(tokenId);

        emit Claim(_msgSender());
    }

    /**
     * @dev View function return launchpad data
     */
    function getLaunchpadData() external view returns(LaunchpadData memory) {
        return _launchpadData;
    }

    /**
     * @dev View function return user is register
     */
    function isRegistered(address user) external view returns(bool) {
        return _registerAddresses[user];
    }

    /**
     * @dev View function return total user registered
     */
    function totalRegistered() external view returns(uint32) {
        return _totalRegistered;
    }

    /**
     * @dev View function return total user redeemed
     */
    function totalRedeemed() external view returns(uint32) {
        return _totalRedeemed;
    }

    /**
     * @dev View function return total redeemed per user
     */
    function totalRedeemedPerUser(address user) external view returns(uint256) {
        return _totalNftRedeemed[user];
    }

    /**
     * @dev View function return sharing fee amount
     */
    function getSharingFee() external view returns(uint256) {
        return _sharingFee;
    }

    /**
     * @dev View function return protocol fee amount
     */
    function getProtocolFee() external view returns(uint256) {
        return _protocolFee;
    }

    /**
     * @dev Withdraw fee token for owner
     */
    function withdrawFungibleToken(address tokenAddress, uint256 amount) external onlyOwner {
        if (tokenAddress == address(0)) {
            (bool sent,) = _msgSender().call{value: amount}("");
            require(sent, "Launchpad: Fail to send ETH");
        } else {
            IERC20 token = IERC20(tokenAddress);
            token.safeTransfer(_msgSender(), amount);
        }
    }

    /** 
     * @dev External function enable multicall
     */
    function multicall(bytes[] calldata data) external override payable {
        for (uint256 i = 0; i < data.length; i++) {
            (bool success, bytes memory result) = address(this).delegatecall(data[i]);

            if (!success) {
                // Next 5 lines from https://ethereum.stackexchange.com/a/83577
                if (result.length < 68) revert();
                assembly {
                    result := add(result, 0x04)
                }
                revert(abi.decode(result, (string)));
            }
        }
    }
}
"
    },
    "contracts/LaunchpadFactory.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/ILaunchpadFactory.sol";
import "./interfaces/ILaunchpad.sol";
import "./interfaces/IMintableLaunchpad.sol";
import "./interfaces/ITransferableLaunchpad.sol";
import "./libraries/TimeLock.sol";

/// @title Launchpad Factory
/// @notice Deploys launchpads and manage ownership and control fee
contract LaunchpadFactory is ILaunchpadFactory, TimeLock {
    using SafeERC20 for IERC20; 

    address private _feeAddress;
    uint256 private _totalLaunchpad;

    /// store implement for each launchpad type, use Minimal Proxy to create launchpad
    mapping(bytes32 => address) private _launchpadImplements;

    constructor(address feeAddress, uint256 lockTime, address mintableLaunchpadImplement, address transferableLaunchpadImplement) TimeLock(lockTime) {
        _feeAddress = feeAddress;

        /// keccak256(abi.encodePacked("Mintable"))
        _setLaunchpadImplement(0x444f313053c893c305c4a5f333f3b033d548405c830016c4b623e787aa045145, mintableLaunchpadImplement);

        /// keccak256(abi.encodePacked("Transferable"))
        _setLaunchpadImplement(0x1bc7992855b26a5ae511e9b448c90941ef8f1b835f4936d94c8cfd9202da9384, transferableLaunchpadImplement);
    }

    /**
     * @dev Internal function set launchpad implement for key.
     */
    function _setLaunchpadImplement(bytes32 key, address implement) internal {
        _launchpadImplements[key] = implement;
    }

    /**
     * @dev Internal function clone launchpad.
     */
    function _cloneLaunchpad(bytes32 implementKey) internal returns(address launchpad) {
        address implement = _launchpadImplements[implementKey];

        launchpad = Clones.cloneDeterministic(implement, bytes32(_totalLaunchpad));

        _totalLaunchpad++;
    }

    /**
     * @dev Get protocol fee receiver.
     */
    function getFeeAddress() view external returns(address) {
        return _feeAddress;
    }

    /**
     * @dev Set protocol fee receiver.
     */
    function setFeeAddress(address newFeeAddress) external onlyOwner {
        _feeAddress = newFeeAddress;

        emit SetFeeAddress(newFeeAddress);
    }

    /**
     * @dev Create mintable launchpad.
     */
    function createMintableLaunchpad(address owner, string memory name, string memory symbol, string memory baseUri, bool enableMinter, ILaunchpad.LaunchpadData memory data, uint256 sharingFee, uint256 protocolFee) external onlyCreator returns(address) {
        address newLaunchpadAddress = _cloneLaunchpad(0x444f313053c893c305c4a5f333f3b033d548405c830016c4b623e787aa045145);
        IMintableLaunchpad launchpad = IMintableLaunchpad(newLaunchpadAddress);

        launchpad.init(name, symbol, baseUri, enableMinter, sharingFee, protocolFee);
        launchpad.setLaunchpad(data);
        Ownable(newLaunchpadAddress).transferOwnership(owner);

        emit CreateMintableLaunchpad(newLaunchpadAddress, owner, name, symbol, enableMinter, data);
        return newLaunchpadAddress;
    }

    /**
     * @dev Create transferable launchpad.
     */
    function createTransferableLaunchpad(address owner, address nftAddress, ILaunchpad.LaunchpadData memory data, uint256 sharingFee, uint256 protocolFee) external onlyCreator returns(address) {
        address newLaunchpadAddress = _cloneLaunchpad(0x1bc7992855b26a5ae511e9b448c90941ef8f1b835f4936d94c8cfd9202da9384);
        ITransferableLaunchpad launchpad = ITransferableLaunchpad(newLaunchpadAddress);

        launchpad.init(nftAddress, sharingFee, protocolFee);
        launchpad.setLaunchpad(data);
        Ownable(newLaunchpadAddress).transferOwnership(owner);

        emit CreateTransferableLaunchpad(newLaunchpadAddress, owner, nftAddress, data);
        return newLaunchpadAddress;
    }

    /**
     * @dev Set implement for launchpad type.
     */
    function setImplement(bytes32 key, address implement) external whenUnlock onlyOwner {
        _setLaunchpadImplement(key, implement);

        emit SetImplement(key, implement);
    }

    /**
     * @dev Create launchpad.
     */
    function createLaunchpad(address owner, bytes32 key) external onlyCreator returns(address) {
        address newLaunchpadAddress = _cloneLaunchpad(key);
        Ownable(newLaunchpadAddress).transferOwnership(owner);

        emit CreateLaunchpad(key, newLaunchpadAddress, owner);
        return newLaunchpadAddress;
    }

    /**
     * @dev View function return implement address of launchpad type.
     */
    function getLaunchpadImplement(bytes32 key) public view returns(address) {
        return _launchpadImplements[key];
    }

    /**
     * @dev View function return launchpad address of index.
     */
    function getLaunchpadAddress(uint256 index, bytes32 key) external view returns (address) {
        address implement = getLaunchpadImplement(key);
        return Clones.predictDeterministicAddress(implement, bytes32(index));
    }

    /**
     * @dev View function return total launchpad.
     */
    function getTotalLaunchpad() external view returns (uint256) {
        return _totalLaunchpad;
    }

    /**
     * @dev function for owner withdraw fee token.
     */
    function withdrawFungibleToken(address tokenAddress, uint256 amount) external onlyOwner {
        if (tokenAddress == address(0)) {
            (bool sent,) = _msgSender().call{value: amount}("");
            require(sent, "Launchpad: Fail to send ETH");
        } else {
            IERC20 token = IERC20(tokenAddress);
            token.safeTransfer(_msgSender(), amount);
        }
    }

    fallback() external {}
    receive() external payable {}
}
"
    },
    "contracts/libraries/AccessControl.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";

contract AccessControl is Ownable {
    mapping(address => bool) _creators;

    event SetCreator(address creator, bool isActive);

    function isActiveCreator(address creator) public view returns(bool) {
        return _creators[creator];
    }

    modifier onlyCreator() {
        require(isActiveCreator(_msgSender()), "Launchpad Access Control: caller is not the creator");
        _;
    }

    function setCreator(address creator, bool isActive) external onlyOwner {
        _setCreator(creator, isActive);
    }

    function _setCreator(address creator, bool isActive) internal {
        _creators[creator] = isActive;

        emit SetCreator(creator, isActive);
    }
}
