    "contracts/libraries/Randomness.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

library Randomness {
    function randomIndex(uint256[] storage array, uint256 base, uint256 lastRandomBlock) internal view returns(uint256 result) {
        uint256 length = array.length;
        // saving gas
        assembly {  
            // need allocate memory
            let emptyPtr := mload(0x40)
            mstore(0x40, add(emptyPtr, 0xa0))

            mstore(emptyPtr, base)
            mstore(add(emptyPtr, 0x20), blockhash(lastRandomBlock))
            mstore(add(emptyPtr, 0x40), length)

            result := mod(keccak256(emptyPtr, 0x60), length)
        }
    }
}
"
