    "contracts/MintableLaunchpad.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";

import "./libraries/Randomness.sol";
import "./interfaces/IMintableLaunchpad.sol";
import "./launchpad/Launchpad.sol";

contract MintableLaunchpad is IMintableLaunchpad, Launchpad, ERC721EnumerableUpgradeable {
    using Randomness for uint256[];

    string private _baseUri;
    bool private _enableMinter;
    IERC721 private _convertToken;
    uint256[] private _tokenIds;

    uint256 private _lastConvertBlock;

    uint256 private _convertTime;
    uint256 private _convertEndTime;
    mapping(uint256 => address) private _pendingTokens;

    modifier onlyConvertTime() {
        require(_convertTime == 0 || block.timestamp >= _convertTime, "Mintable Launchpad: Convert Time has not been started yet");
        require(_convertEndTime == 0 || block.timestamp < _convertEndTime, "Mintable Launchpad: Convert Time has been ended");
        _;
    }

    /**
     * @dev Override function, for mintable launchpad: when user redeem, launchpad mint new token for user
     */
    function _redeemToken(uint256 amount) internal override {
        uint256 currentIndex = totalSupply() + 1;
        uint256 fromId = currentIndex;
        bool isClaimed = block.timestamp > _launchpadData.claimStartTimestamp;
        for (uint i = 0; i < amount; i++) {
            if (isClaimed) {
                _safeMint(_msgSender(), currentIndex);
            } else {
                _safeMint(address(this), currentIndex);
                _pendingTokens[currentIndex] = _msgSender();
            }
            currentIndex++;
        }

        if (isClaimed) {
            emit RedeemToken(_msgSender(), fromId, totalSupply() + 1);
        } else {
            emit PendingToken(_msgSender(), fromId, totalSupply() + 1);
        }

    }

    function _claimToken(uint256 tokenId) internal override {
        require(_pendingTokens[tokenId] == _msgSender(), "Mintable Launchpad: Invalid token id");
        IERC721 token = IERC721(address(this));
        token.safeTransferFrom(address(this), _msgSender(), tokenId);
        delete _pendingTokens[tokenId];
    }

    /**
     * @dev Init launchpad data
     */
    function init(
        string memory name,
        string memory symbol,
        string memory baseUri,
        bool enableMinter,
        uint256 sharingFee,
        uint256 protocolFee
    ) external override initializer {
        __ERC721_init(name, symbol);
        __Ownable_init();
        _launchpadInit(_msgSender(), sharingFee, protocolFee);

        _enableMinter = enableMinter;
        _baseUri = baseUri;
    }

    /**
     * @dev Override function, return override base uri
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseUri;
    }

    /**
     * @dev pending token
     */
    function pendingToken(uint256 tokenId) external override view returns (address) {
        return _pendingTokens[tokenId];
    }

    /**
     * @dev Override function, return override base uri
     */
    function mint(address owner) external onlyOwner {
        require(_enableMinter, "Mintable Launchpad: Not enable minter");
        uint256 currentIndex = totalSupply();

        _safeMint(owner, currentIndex + 1);
    }

    /**
     * @dev Mintable launchpad can be mystery box, mistery box can be convert to mystery nft
     */
    function setConvertTokenInfo(
        address convertTokenAddress,
        uint256 convertTime,
        uint256 convertEndTime
    ) external onlyOwner {
        if (convertTokenAddress != address(_convertToken)) {
            require(_tokenIds.length == 0, "Mintable Launchpad: convert token's address should not be changed after pushing the token");
        }

        _convertToken = IERC721(convertTokenAddress);
        _convertTime = convertTime;
        _convertEndTime = convertEndTime;

        emit SetConvertInfo(convertTokenAddress, convertTime, convertEndTime);
    }

    /**
     * @dev Push mystery nft to launchpad
     */
    function pushToken(uint256[] memory tokenIds) external onlyOwner {
        require(address(_convertToken) != address(0), "Mintable Launchpad: Invalid convert token");

        for (uint256 i; i < tokenIds.length; i++) {
            _convertToken.safeTransferFrom(_msgSender(), address(this), tokenIds[i]);
            _tokenIds.push(tokenIds[i]);
        }

        emit PushToken(address(_convertToken), tokenIds);
    }

    /**
     * @dev Mintable launchpad can be mystery box, mistery box can be convert to mystery nft
     */
    function convertToken(uint256 tokenId) external onlyConvertTime {
        require(address(_convertToken) != address(0), "Mintable Launchpad: Not mistery box");
        require(_tokenIds.length > 0, "Mintable Launchpad: Not enough token");
        require(ownerOf(tokenId) == _msgSender(), "Mintable Launchpad: Not an owner");

        _burn(tokenId);

        uint256 lastConvertBlock = _lastConvertBlock;

        if (lastConvertBlock == block.number || lastConvertBlock == 0 || block.number - lastConvertBlock >= 256) {
            lastConvertBlock = block.number - 1;
        }

        uint256 tokenIndex = _tokenIds.randomIndex(tokenId, lastConvertBlock);
        uint256 receiveTokenId = _tokenIds[tokenIndex];

        _convertToken.safeTransferFrom(address(this), _msgSender(), receiveTokenId);

        _tokenIds[tokenIndex] = _tokenIds[_tokenIds.length - 1];
        _tokenIds.pop();

        _lastConvertBlock = block.number;

        emit ConvertToken(_msgSender(), tokenId, address(_convertToken), receiveTokenId);
    }

    function withdrawNft(address tokenAddress, uint256[] memory indexes) external onlyOwner {
        if (tokenAddress == address(_convertToken)) {
            require(block.timestamp < _launchpadData.redeemStartTimestamp || block.timestamp > _convertEndTime, "Mintable Launchpad: Convert time phase has not been ended yet");
            uint256[] memory tokenWithdrawList = new uint256[](indexes.length);
            for (uint256 i = 0; i < indexes.length; i++) {
                uint256 tokenId = _tokenIds[indexes[i]];
                tokenWithdrawList[i] = tokenId;
                _convertToken.safeTransferFrom(address(this), _msgSender(), tokenId);
                _tokenIds[indexes[i]] = _tokenIds[_tokenIds.length - 1];
                _tokenIds.pop();
            }
            emit WithdrawNFT(tokenAddress, tokenWithdrawList);
        } else if (tokenAddress == address(this)) {
            IERC721 token = IERC721(tokenAddress);
            for (uint256 i = 0; i < indexes.length; i++) {
                require(_pendingTokens[indexes[i]] == address(0), "Mintable Launchpad: Can not withdraw pending token");
                token.safeTransferFrom(address(this), _msgSender(), indexes[i]);
            }
        } else {
            IERC721 token = IERC721(tokenAddress);
            for (uint256 i = 0; i < indexes.length; i++) {
                token.safeTransferFrom(address(this), _msgSender(), indexes[i]);
            }
            emit WithdrawNFT(tokenAddress, indexes);
        }
    }
}
"
    },
