    "contracts/TransferableLaunchpad.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.9;

import "./libraries/Randomness.sol";
import "./launchpad/Launchpad.sol";
import "./interfaces/ITransferableLaunchpad.sol";

contract TransferableLaunchpad is ITransferableLaunchpad, Launchpad {
    using Randomness for uint256[];

    IERC721 private _token;
    uint256[] private _tokenIds;

    uint256 private _lastRedeemBlock;
    mapping(uint256 => address) private _pendingTokens;

    function _redeemToken(uint256 amount) internal override {
        uint256 lastRedeemBlock = _lastRedeemBlock;

        if (lastRedeemBlock == block.number || lastRedeemBlock == 0 || block.number - lastRedeemBlock >= 256) {
          lastRedeemBlock = block.number - 1;
        }

        uint256[] memory redeemTokens = new uint256[](amount);
        bool isClaimed = block.timestamp > _launchpadData.claimStartTimestamp;
        for (uint i = 0; i < amount; i++) {
            uint256 tokenIndex = _tokenIds.randomIndex(uint256(uint160(_msgSender())), lastRedeemBlock);
            uint256 tokenId = _tokenIds[tokenIndex];

            if (isClaimed) {
                _token.safeTransferFrom(address(this), _msgSender(), tokenId);
            } else {
                _pendingTokens[tokenId] = _msgSender();
            }
            redeemTokens[i] = tokenId;

            _tokenIds[tokenIndex] = _tokenIds[_tokenIds.length - 1];
            _tokenIds.pop();
        }

        _lastRedeemBlock = block.number;

        if (isClaimed) {
            emit RedeemToken(_msgSender(), redeemTokens);
        } else {
            emit PendingToken(_msgSender(), redeemTokens);
        }

        delete redeemTokens;
    }

    function _claimToken(uint256 tokenId) internal override {
        require(_pendingTokens[tokenId] == _msgSender(), "Transferable Launchpad: Invalid token id");
        _token.safeTransferFrom(address(this), _msgSender(), tokenId);
        delete _pendingTokens[tokenId];
    }

    /**
     * @dev pending token
     */
    function pendingToken(uint256 tokenId) external override view returns (address) {
        return _pendingTokens[tokenId];
    }

    function init(address token, uint256 sharingFee, uint256 protocolFee) initializer override external {
        _token = IERC721(token);
        __Ownable_init();
        _launchpadInit(_msgSender(), sharingFee, protocolFee);
    }

    function pushToken(uint256[] memory tokenIds) external onlyOwner {
        for(uint256 i; i< tokenIds.length; i++) {
            _token.safeTransferFrom(_msgSender(), address(this), tokenIds[i]);
            _tokenIds.push(tokenIds[i]);
        }

        emit PushToken(address(_token), tokenIds);
    }

    function withdrawNft(address tokenAddress, uint256[] memory indexes) external onlyOwner {
        if (tokenAddress == address(_token)) {
            require(block.timestamp < _launchpadData.redeemStartTimestamp || block.timestamp > _launchpadData.redeemEndTimestamp, "Transferable Launchpad: During redeem time");
            uint256[] memory tokenWithdrawList = new uint256[](indexes.length);
            for(uint i = 0; i < indexes.length; i++) {
                require(_pendingTokens[indexes[i]] == address(0), "Transferable Launchpad: Can not withdraw pending token");

                uint256 tokenId = _tokenIds[indexes[i]];
                tokenWithdrawList[i] = tokenId;
                _token.safeTransferFrom(address(this), _msgSender(), tokenId);
                _tokenIds[indexes[i]] = _tokenIds[_tokenIds.length - 1];
                _tokenIds.pop();
            }
            emit WithdrawNFT(tokenAddress, tokenWithdrawList);
        } else {
            IERC721 token = IERC721(tokenAddress);
            for(uint i = 0; i < indexes.length; i++) {
                token.safeTransferFrom(address(this), _msgSender(), indexes[i]);
            }
            emit WithdrawNFT(tokenAddress, indexes);
        }

    }
}
