{{
  "language": "Solidity",
  "sources": {
    "contracts/Luckydudes.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.7.5;
pragma abicoder v2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-periphery/contracts/libraries/OracleLibrary.sol";
import "./interfaces/IWrappedToken.sol";
import "./Traits.sol";
import "./Luckydude.sol";

struct RoundResult {
    uint winner;
    uint poolSize; // in usdc
    uint mainPrize; // in usdc
    uint locationPrize;
    uint namePrize;
}

contract Luckydudes is ERC721, Ownable {
    Traits traits;
    ISwapRouter internal swapRouter;
    IUniswapV3Pool internal uniswapv3Pool;

    event DudeMinted(uint tokenId, address to, uint referer);
    event RoundsLoaded(uint tokenId, uint startRound, uint rounds);
    event RaffleLauched(
        uint timestamp,
        uint round,
        uint participants,
        uint poolSize,
        uint winner,
        bool bonusRaffle
    );
    event PrizeClaimed(
        uint tokenId,
        uint round,
        uint rewardBalance,
        uint rewardRounds,
        bool bonusRaffle
    );

    uint8 public constant batchLimit = 10; // mint amount limit

    uint public maxSupply = 10_000;
    uint public mintPrice = 10; // in USD cents
    uint public directReferrerBonus = 2; // in USD cents
    uint public secondaryReferrerBonus = 1; // in USD cents
    mapping(address => uint) public referrerBonusBalance; // in USD cents
    uint public loadPrice = 1; // in USD cents
    uint public mintLoadRounds = 20;
    uint public referalRaffleExtraTokensAmount = 2;
    uint[] public mainPrizes = [60_00, 65_00, 70_00]; // percents
    uint public locationPrize = 10_00; // percents;
    uint public namePrize = 4_00; // percents
    uint public colorPrize = 1; // raffles
    uint public directReferrerPrize = 3_00; // perecents
    uint public secondaryReferrerPrize = 1_00; // percents
    uint public bonusPoolContribution = 2_00; // percents

    address WMATIC;
    address USDC;
    uint24 poolFee;

    bool public raffleState = false;
    bool public mintStarted = false; // is mint started flag

    uint public currentRound = 0;
    uint public mintedCount = 0;
    uint[2][3] traitsAmount;

    mapping(uint => Luckydude) public dudes;
    mapping(uint => address[]) public pastDudeOwners;
    mapping(uint => uint[]) public roundParticipants;
    mapping(uint => RoundResult) public roundResults;
    mapping(uint => mapping(uint => uint)) public roundLocations;
    mapping(uint => mapping(uint => uint)) public roundNames;
    mapping(uint => mapping(uint => uint)) public roundColors;

    mapping(uint => mapping(uint => uint)) usedTraitIds;
    mapping(uint => mapping(uint => bool)) activeRounds;
    mapping(uint => mapping(uint => bool)) claimedRounds;

    // bonus rounds
    uint public currentBonusRound = 0;
    uint[] public bonusRoundParticipants;
    mapping(uint => RoundResult) public bonusRoundResults;
    mapping(uint => bool) claimedBonusRounds;

    constructor(
        address _traits,
        address _uniswapV3Pool,
        address _unirouter,
        address _wmatic,
        address _usdc,
        uint24 _poolFee
    ) ERC721("LuckyDudes", "LD") {
        traits = Traits(_traits);

        uniswapv3Pool = IUniswapV3Pool(_uniswapV3Pool);
        swapRouter = ISwapRouter(_unirouter);

        USDC = _usdc;
        WMATIC = _wmatic;
        poolFee = _poolFee;
        roundParticipants[0] = new uint[](0);
        bonusRoundParticipants = new uint[](0);

        // fill amounts of each trate to generate unique ids
        traitsAmount[0] = [2_000, 2_000]; // totalAmount (constant), unusedAmount (will be decreased when id generated)
        traitsAmount[1] = [200, 200];
        traitsAmount[2] = [20, 20];
    }

    function mint(uint _mintAmount, uint _referrer) public payable {
        require(mintStarted, "Mint is not started");
        require(_mintAmount <= batchLimit, "Not in batch limit");
        require(msg.value >= getMintPrice(_mintAmount), "Wrong Payment Amount");

        uint roundsToLoad = mintLoadRounds;
        if (_referrer != 0) {
            require(
                _exists(_referrer) && ownerOf(_referrer) != msg.sender,
                "Referrer not exists"
            );

            referrerBonusBalance[ownerOf(_referrer)] += _mintAmount * directReferrerBonus;
            dudes[_referrer].directRefferalls += _mintAmount;

            // if referer become elite referer - add to bonus participants
            if (
                dudes[_referrer].directRefferalls >= 2 &&
                dudes[_referrer].directRefferalls - _mintAmount < 2
            ) {
                bonusRoundParticipants.push(_referrer);
            }

            uint secondaryReferrer = dudes[_referrer].referrerId;
            if (secondaryReferrer != 0) {
                referrerBonusBalance[ownerOf(secondaryReferrer)] += _mintAmount * secondaryReferrerBonus;
                dudes[secondaryReferrer].secondaryRefferalls += _mintAmount;
            }
            roundsToLoad += referalRaffleExtraTokensAmount;
        }

        IWrappedToken(WMATIC).deposit{value: address(this).balance}();
        swap(
            WMATIC,
            USDC,
            IWrappedToken(WMATIC).balanceOf(address(this)),
            address(this)
        );
        mintInternal(_msgSender(), _mintAmount, roundsToLoad, _referrer);
    }

    function swap(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn,
        address _to
    ) private returns (uint amountOut) {
        TransferHelper.safeApprove(_tokenIn, address(swapRouter), _amountIn);

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: _tokenIn,
                tokenOut: _tokenOut,
                fee: poolFee,
                recipient: _to,
                deadline: block.timestamp,
                amountIn: _amountIn,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            });

        amountOut = swapRouter.exactInputSingle(params);
    }
    
    function loadRounds(uint[] calldata _tokens, uint _rounds) public payable {
        require(
            msg.value >= getLoadPrice(_rounds, _tokens.length),
            "Wrong Payment Amount"
        );
        for (uint i = 0; i < _tokens.length; i++) {
            require(_msgSender() == ownerOf(_tokens[i]), "Not your tokens!");
            loadRoundsInternal(_tokens[i], _rounds);
        }
        IWrappedToken(WMATIC).deposit{value: msg.value}();
        swap(
            WMATIC,
            USDC,
            IWrappedToken(WMATIC).balanceOf(address(this)),
            address(this)
        );
    }

    function ownerMint(
        uint _mintAmount,
        uint _loadRounds
    ) public payable onlyOwner {
        require(
            msg.value >= getLoadPrice(_loadRounds, _mintAmount),
            "Wrong Payment Amount"
        );
        mintInternal(_msgSender(), _mintAmount, _loadRounds, 0);
    }

    function mintInternal(
        address _to,
        uint _mintAmount,
        uint _loadRounds,
        uint _referrer
    ) private {
        for (uint i = 1; i <= _mintAmount; i++) {
            uint tokenId = mintedCount + i;
            _safeMint(_to, tokenId);

            // generate dude info
            Luckydude memory dude = generate(random(tokenId));
            dude.referrerId = _referrer;
            dudes[tokenId] = dude;

            emit DudeMinted(tokenId, _to, _referrer);
            loadRoundsInternal(tokenId, _loadRounds);
        }
        mintedCount += _mintAmount;
    }

    function generate(uint _seed) private returns (Luckydude memory dude) {
        uint[3] memory traitsIds = generateTraits(_seed);
        // set generated traits to LuckyDude struct
        dude.name = traitsIds[0];
        dude.location = traitsIds[1];
        dude.color = traitsIds[2];
        dude.mintedTimestamp = block.timestamp;
    }

    function generateTraits(
        uint256 _seed
    ) private returns (uint[3] memory _traitsIds) {
        // loop by each trait type
        for (uint i = 0; i < traitsAmount.length; i++) {
            // reset unused trait amount if its equal to zero
            if (traitsAmount[i][1] == 0) {
                traitsAmount[i][1] = traitsAmount[i][0]; // unused amount = total amount
            }

            _seed >>= 16;
            uint traitId = ((_seed & 0xFFFF) % traitsAmount[i][1]);
            uint temp = traitId;

            // select unique trait id
            if (usedTraitIds[i][traitId] != 0)
                traitId = usedTraitIds[i][traitId];

            // decrease unused trait amount
            traitsAmount[i][1] -= 1;

            // move last trait to the place of taken
            if (usedTraitIds[i][traitsAmount[i][1]] != 0)
                usedTraitIds[i][temp] = usedTraitIds[i][traitsAmount[i][1]];
            else usedTraitIds[i][temp] = traitsAmount[i][1];
            usedTraitIds[i][traitsAmount[i][1]] = 0;

            _traitsIds[i] = traitId;
        }
    }

    function loadRoundsInternal(uint tokenId, uint rounds) private {
        uint startRound = dudes[tokenId].lastActiveRound > 0 ? dudes[tokenId].lastActiveRound + 1 : 0;
        if (startRound < currentRound) {
            startRound = currentRound;
        }
        for (uint i = startRound; i < startRound + rounds; i++) {
            activeRounds[tokenId][i] = true;
            roundParticipants[i].push(tokenId);
            roundLocations[i][dudes[tokenId].location] += 1;
            roundNames[i][dudes[tokenId].name] += 1;
            roundColors[i][dudes[tokenId].color] += 1;
        }
        if (dudes[tokenId].lastActiveRound < currentRound) {
            dudes[tokenId].activeFromRound = currentRound;
        }
        dudes[tokenId].lastActiveRound = startRound + rounds - 1;
        emit RoundsLoaded(tokenId, startRound, rounds);
    }

    function convertToNative(uint usdAmountinCents) public view returns (uint) {
        (int24 arithmeticMeanTick, ) = OracleLibrary.consult(
            address(uniswapv3Pool),
            100
        );
        return
            OracleLibrary.getQuoteAtTick(
                arithmeticMeanTick,
                uint128(usdAmountinCents * 10 ** (ERC20(USDC).decimals() - 2)),
                USDC,
                WMATIC
            );
    }

    function getMintPrice(uint _mintAmount) public view returns (uint) {
        return
            convertToNative(mintPrice + loadPrice * mintLoadRounds) *
            _mintAmount;
    }

    function getLoadPrice(
        uint _rounds,
        uint _tokens
    ) public view returns (uint totalPrice) {
        totalPrice = convertToNative(_tokens * _rounds * loadPrice);
    }

    function getRoundParticipantsCount(uint round) public view returns (uint) {
        return roundParticipants[round].length;
    }

    function getBonusRoundParticipantsCount() public view returns (uint) {
        return bonusRoundParticipants.length;
    }

    function claimPrizes(
        uint[] calldata _tokenIds,
        uint[] calldata _rounds
    ) public {
        uint rewardBalance = 0;
        uint rewardRounds = 0;
        for (uint i = 0; i < _tokenIds.length; i++) {
            require(
                ownerOf(_tokenIds[i]) == _msgSender(),
                "Only owner can call"
            ); // add controller to be able to call on behalf of user
            require(
                !claimedRounds[_tokenIds[i]][_rounds[i]],
                "Already claimed this round"
            );
            require(
                activeRounds[_tokenIds[i]][_rounds[i]],
                "Was not a ctive in this round"
            );
            // check if was winner of all prizes types
            uint winner = roundResults[_rounds[i]].winner;
            // main prize
            if (winner == _tokenIds[i]) {
                rewardBalance += roundResults[_rounds[i]].mainPrize;
            } else {
                // primary referal
                if (dudes[winner].referrerId == _tokenIds[i]) {
                    rewardBalance +=
                        (roundResults[_rounds[i]].poolSize *
                            directReferrerPrize) /
                        100_00;
                } else if (
                    dudes[winner].referrerId != 0 &&
                    dudes[dudes[winner].referrerId].referrerId == _tokenIds[i]
                ) {
                    rewardBalance +=
                        (roundResults[_rounds[i]].poolSize *
                            secondaryReferrerPrize) /
                        100_00;
                }
                // location
                if (dudes[_tokenIds[i]].location == dudes[winner].location) {
                    rewardBalance += roundResults[_rounds[i]].locationPrize;
                }
                // name
                if (dudes[_tokenIds[i]].name == dudes[winner].name) {
                    rewardBalance += roundResults[_rounds[i]].locationPrize;
                }
                // color loadRoundsInternal(1 round) if won
                if (dudes[_tokenIds[i]].color == dudes[winner].color) {
                    rewardRounds = 1;
                    loadRoundsInternal(_tokenIds[i], rewardRounds);
                }
            }
            claimedRounds[_tokenIds[i]][_rounds[i]] = true;
            // swap balance to wmatic
            uint wmatic = swap(USDC, WMATIC, rewardBalance, address(this));

            // wmatic to matic
            IWrappedToken(WMATIC).withdraw(wmatic);

            // transfer to msg sender
            payable(_msgSender()).transfer(wmatic);
            emit PrizeClaimed(
                _tokenIds[i],
                _rounds[i],
                rewardBalance,
                rewardRounds,
                false
            );
        }
    }

    function claimBonusPrize(uint _tokenId, uint _round) public {
        require(ownerOf(_tokenId) == _msgSender(), "Only owner can call");
        require(!claimedBonusRounds[_round], "Already claimed this round");
        require(
            bonusRoundResults[_round].winner == _tokenId,
            "Was not a winner of a round"
        );

        claimedBonusRounds[_round] = true;
        // swap balance to wmatic
        uint wmatic = swap(
            USDC,
            WMATIC,
            bonusRoundResults[_round].mainPrize,
            address(this)
        );

        // wmatic to matic
        IWrappedToken(WMATIC).withdraw(wmatic);

        // transfer to msg sender
        payable(_msgSender()).transfer(wmatic);
        emit PrizeClaimed(
            _tokenId,
            _round,
            bonusRoundResults[_round].mainPrize,
            0,
            true
        );
    }

    // TODO replace random with real random
    function launchRaffleRound() external onlyOwner {
        RoundResult memory result;
        result.poolSize =
            roundParticipants[currentRound].length *
            loadPrice *
            10 ** (ERC20(USDC).decimals() - 2);
        result.winner = roundParticipants[currentRound][
            random(currentRound) % roundParticipants[currentRound].length
        ];

        if (dudes[result.winner].directRefferalls >= 2) {
            result.mainPrize = (result.poolSize * mainPrizes[2]) / 100_00;
        } else if (dudes[result.winner].directRefferalls == 1) {
            result.mainPrize = (result.poolSize * mainPrizes[1]) / 100_00;
        } else {
            result.mainPrize = (result.poolSize * mainPrizes[0]) / 100_00;
        }
        result.locationPrize =
            (result.poolSize * locationPrize) /
            100_00 /
            roundLocations[currentRound][dudes[result.winner].location];
        result.namePrize =
            (result.poolSize * namePrize) /
            100_00 /
            roundNames[currentRound][dudes[result.winner].name];

        emit RaffleLauched(
            block.timestamp,
            currentRound,
            roundParticipants[currentRound].length,
            result.poolSize,
            result.winner,
            false
        );
        roundResults[currentRound] = result;
        currentRound += 1;
    }

    // TODO replace random with real random
    // pool size in usd cents
    function launchBonusRound(uint _poolSize) external onlyOwner {
        RoundResult memory result;
        result.poolSize = _poolSize * 10 ** (ERC20(USDC).decimals() - 2);
        result.winner = bonusRoundParticipants[random(currentBonusRound) % bonusRoundParticipants.length];
        result.mainPrize = result.poolSize;

        emit RaffleLauched(
            block.timestamp,
            currentBonusRound,
            bonusRoundParticipants.length,
            result.poolSize,
            result.winner,
            true
        );
        bonusRoundResults[currentBonusRound] = result;
        currentBonusRound += 1;
    }

    function setMintState(bool _mintState) external onlyOwner {
        mintStarted = _mintState;
    }

    function withdrawReferrerBonus() public {
        require(referrerBonusBalance[_msgSender()] > 0, "Referrer Balance must be greater than 0");
        uint amount = referrerBonusBalance[_msgSender()] * 10 ** (ERC20(USDC).decimals() - 2);
        referrerBonusBalance[_msgSender()] = 0;
        uint wmatic = swap(
            USDC,
            WMATIC,
            amount,
            address(this)
        );
        IWrappedToken(WMATIC).withdraw(wmatic);
        payable(_msgSender()).transfer(wmatic);
    }


    function withdraw() public onlyOwner {
        uint wmatic = swap(
            USDC,
            WMATIC,
            IWrappedToken(USDC).balanceOf(address(this)),
            address(this)
        );
        IWrappedToken(WMATIC).withdraw(wmatic);
        payable(owner()).transfer(wmatic);
    }

    function updateMintConfig(
        uint _maxSupply,
        uint _mintPrice,
        uint _directReferrerBonus,
        uint _secondaryReferrerBonus,
        uint _mintLoadRounds,
        uint _loadPrice,
        uint _referalRaffleExtraTokensAmount
    ) public onlyOwner {
        maxSupply = _maxSupply;
        mintPrice = _mintPrice;
        directReferrerBonus = _directReferrerBonus;
        secondaryReferrerBonus = _secondaryReferrerBonus;
        mintLoadRounds = _mintLoadRounds;
        loadPrice = _loadPrice;
        referalRaffleExtraTokensAmount = _referalRaffleExtraTokensAmount;
    }

    function updatePrizeConfig(
        uint[] memory _mainPrizes,
        uint _locationPrize,
        uint _namePrize,
        uint _colorPrize,
        uint _directReferrerPrize,
        uint _seecondaryReferrerPrize,
        uint _bonusPoolContribution
    ) public onlyOwner {
        mainPrizes = _mainPrizes;
        locationPrize = _locationPrize;
        namePrize = _namePrize;
        colorPrize = _colorPrize;
        directReferrerPrize = _directReferrerPrize;
        secondaryReferrerPrize = _seecondaryReferrerPrize;
        bonusPoolContribution = _bonusPoolContribution;
    }

    function random(uint256 seed) internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        tx.origin,
                        blockhash(block.number - 1),
                        block.timestamp,
                        seed
                    )
                )
            );
    }

    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory _tokenURI) {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );
        _tokenURI = traits.tokenURI(dudes[tokenId], tokenId);
    }

    receive() external payable {}
    fallback() external payable {}
}
"
    },
