    "contracts/Factory.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8;
import "./MKWallet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Factory is Ownable {

  event Deployed(address addr, uint256 salt);

  function getByteCode(address[] memory _owner) public pure returns (bytes[] memory) {
    bytes[] memory result = new bytes[](_owner.length);
    for (uint256 i = 0; i < _owner.length; i++) {
      bytes memory bytecode = type(MKWallet).creationCode;
      result[i] = abi.encodePacked(bytecode, abi.encode(_owner[i]));
    }    
    return result;
  }

  function getAddress(bytes[] memory _bytecode, uint _salt) public view returns (address[] memory) {
    address[] memory result = new address[](_bytecode.length);
    for (uint256 i = 0; i < _bytecode.length; i++) {
      bytes32 hash = keccak256(
        abi.encodePacked(
          bytes1(0xff),
          address(this),
          _salt,
          keccak256(_bytecode[i])
        )
      );
      result[i] = address(uint160(uint256(hash)));
    }
    return result;
  }

  function deploy(bytes[] memory _bytecode, uint _salt) public payable {
    for (uint256 i = 0; i < _bytecode.length; i++) {
      bytes memory bytecode = _bytecode[i];
      address addr;
      assembly {
        addr := create2(
          callvalue(),
          add(bytecode, 0x20),
          mload(bytecode),
          _salt
        )

        if iszero(extcodesize(addr)) {
          revert(0, 0)
        }
      }
      emit Deployed(addr, _salt);
    }
  }

}
"
