    "contracts/MKWallet.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Address.sol";
// import "hardhat/console.sol";

contract MKWallet is EIP712, IERC721Receiver, Ownable{
  using Counters for Counters.Counter;
  mapping (address => Counters.Counter) _nonces;
  event Multicall(bytes [] result);


  constructor(address _owner) EIP712("MKWallet", "1") {
    transferOwnership(_owner);
  }

  /**
   * Multicall
   */
  function multicall(
    address callTo,
    bytes[] memory signatures,
    bytes[] memory contents)
  external {
    bytes[] memory results = new bytes[](signatures.length);
    for (uint256 i = 0; i < signatures.length; i++) {
	  verify(signatures[i], contents[i]);
      results[i] = Address.functionCall(callTo, contents[i]);
    }
    _nonces[msg.sender].increment();
    emit Multicall(results);
  }

  function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
    return this.onERC721Received.selector;
  }

  function domainSeparator() external view returns (bytes32) {
    return _domainSeparatorV4();
  }

  function getNonce() public view returns(uint256) {
    return _nonces[msg.sender].current();
  }

  function verify(
    bytes memory signature,
    bytes memory contents 
  ) public view {
    bytes32 digest = _hashTypedDataV4(
      keccak256(abi.encode(keccak256("Call(uint256 nonce,bytes contents)"), _nonces[msg.sender].current(), keccak256(bytes(contents))))
    );
    address recoveredSigner = ECDSA.recover(digest, signature);
	  require(owner() == recoveredSigner, 'Only owner can sign transactions');
  }

  function getChainId() external view returns (uint256) {
    return block.chainid;
  }
}
"
