/**
 * @title ERC721Tradable
 * ERC721Tradable - ERC721 contract that whitelists a trading address, and has minting functionality.
 */
abstract contract ERC721Tradable is ERC2981, ContextMixin, ERC721Enumerable, NativeMetaTransaction, Ownable, Pausable {
  using SafeMath for uint256;
  using Strings for uint256;
  mapping(uint256 => string) private _tokenURIs;
  string public uriPrefix = "";
  address private _minter;
  address private _verifier;

  constructor(
    string memory name_,
    string memory symbol_,
    address minter_
  ) ERC721(name_, symbol_) {
    _initializeEIP712(name_);
    _minter = minter_;
    _verifier = _msgSender();
  }

  modifier onlyMinter() {
    require(_minter == _msgSender(), "Ownable: caller is not the owner");
    _;
  }

  /**
   * @dev Pause/Unpause the contract
   */
  function pause(bool stop) public virtual onlyOwner {
    if (stop) {
      _pause();
    } else {
      _unpause();
    }
  }

  /**
    * @dev Returns the address of the current minter.
    */
  function minter() public view virtual returns (address) {
      return _minter;
  }

  /**
   * @dev Changes the minter
   */
  function setMinter(address newMinter) public virtual onlyOwner {
    _minter = newMinter;
  }

  /**
    * @dev Returns the address of the current verifier.
    */
  function verifier() public view virtual returns (address) {
      return _verifier;
  }

  /**
   * @dev Changes the verifier
   */
  function setVerifier(address newVerifier) public virtual onlyOwner {
    _verifier = newVerifier;
  }

  /**
   * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-less listings.
   */
  function isApprovedForAll(
    address _owner,
    address _operator
  ) public override view returns (bool isOperator) {
    if (_operator == address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE)) {
      return true;
    }
    return ERC721.isApprovedForAll(_owner, _operator);
  }

  /**
   * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
   */
  function _msgSender()
    internal
    override
    view
    returns (address sender)
  {
    return ContextMixin.msgSender();
  }

  /**
    * @dev Hook that is called before any token transfer. This includes minting
    * and burning.
    */
  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 tokenId
  ) internal virtual override {
    super._beforeTokenTransfer(from, to, tokenId);
    require(!paused(), "Token transfer while paused");
  }

  /**
    * @dev Withdraw contract balance
    */
  function claimTreasury(address payable _to) external onlyOwner {
    ( bool sent, ) = _to.call{value: address(this).balance}('');
    require(sent, 'Failed to send amount');
  }

  /**
    * @dev Check if token is already minted
    */
  function exists(uint256 tokenId) public view virtual returns (bool) {
    return super._exists(tokenId);
  }

  /**
    * @dev See {IERC165-supportsInterface}.
    */
  function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Enumerable, ERC2981) returns (bool) {
      return super.supportsInterface(interfaceId);
  }

  /**
    * @dev Set default royalty
    */
  function setDefaultRoyalty(address receiver, uint96 feeNumerator) public virtual onlyOwner {
    super._setDefaultRoyalty(receiver, feeNumerator);
  }
}
