      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;
import "./ERC721Tradable.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// import "hardhat/console.sol";

/**
 * @title ERC721MinterPauserMutable
 * ERC721Tradeable contract with mutable or inmutable metadata
 */
contract ERC721MinterPauserMutable is ERC721Tradable, EIP712 {
  struct Collection {
    string baseURI;
    uint256 tokenId;
  }
  Collection[] public collections;
  address public treasury;

  /**
   * @dev Contructor
   * @param _name | The Contract name
   * @param _symbol | The Symbol
   * @param _owner | The Owner and Verifier
   * @param _minter | The Minter
   * @param _baseURI | The baseUri for the first initial collection: TokenId=0 to infinite
   */
  constructor(string memory _name, string memory _symbol, address _owner, address _minter, string memory _baseURI )
    EIP712(_name, "1")
    ERC721Tradable(_name, _symbol, _minter)
  {
    super.setMinter(_minter);
    super.setVerifier(_owner);
    transferOwnership(_owner);
    // Add the first collection
    collections.push(Collection(_baseURI, 0));
  }

  /**
   * @dev Set collection
   * @param _baseURI | It´s the base uri of the new collection
   * @param _tokenId | It´s the first tokenId of the new collection
   */
  function setCollection(string memory _baseURI, uint256 _tokenId) public onlyOwner {
    require(bytes(_baseURI).length > 0, "Base Uri cannot be empty");
    require(_tokenId >= 0, "TokenId must be greater or equal than 0");
    bool exist = false;
    for (uint256 i = 0; i < collections.length; i++) {
      if (collections[i].tokenId >= _tokenId) {
        exist = true;
      }
    }
    require(!exist, "Collection has been already set");  
    // Add the new collection
    collections.push(Collection(_baseURI, _tokenId));
  }

  /**
   * @dev Get collection
   * @param _index | It´s the index of the array (collections)
   * @return baseURI | It´s the base uri of the collection
   * @return tokenId | It´s the first tokenId of the collection
   */
  function getCollection(uint256 _index) public view returns (string memory, uint256) {
    require(_index < collections.length, "Collection does not exist");
    return (collections[_index].baseURI, collections[_index].tokenId);
  }

  /**
   * @dev Get token URI
   * @param _tokenId | It´s the token Id
   * @return url | <baseURI of collection>/<tokenId>.json
   */
  function tokenURI(uint256 _tokenId) public view override returns (string memory) {
    require(_exists(_tokenId), "Token does not exist.");
    string memory baseURI;
    for (uint256 i = 0; i < collections.length; i++) {
      if (_tokenId < collections[i].tokenId) break;
      baseURI = collections[i].baseURI; 
    }
    return string(abi.encodePacked(baseURI, Strings.toString(_tokenId), ".json"));
  }

  /**
   * @dev Mints a token to an address with a tokenURI.
   * @param _to | Address of the future owner of the token
   * @param _tokenId | It´s the token Id
   * @return tokenId
   */
  function mintTo(address _to, uint256 _tokenId) external onlyMinter returns (uint256 tokenId){
    require(!_exists(_tokenId), "Token already exist.");
    _mint(_to, _tokenId);
    return _tokenId;
  }

  /**
   * @dev Buy a token with signature. Payable method
   * @param _tokenId | Token ID
   * @param _price | Token price
   * @param _signature | The signature
   * @return tokenId
   */
  function buyToken(uint256 _tokenId, uint256 _price, bytes calldata _signature) public payable returns (uint256 tokenId) {
    require(!_exists(_tokenId), "Token already exist.");
    require(msg.value >= _price, 'Insufficient funds');
    verify(_signature, _msgSender(), _tokenId, _price);
    _mint(_msgSender(), _tokenId);
    return _tokenId;
  }

  /**
   * @dev Verify the signature
   * @param _signature | The signature
   * @param _buyer | The address of the buyer
   * @param _tokenId | The tokenId of the NFT
   * @param _price | The price of the NFT
   * @return address | The verifier address
   * @return address | The verifier address calculated
   */
  function verify(
    bytes memory _signature,
    address _buyer,
    uint256 _tokenId,
    uint256 _price
  ) public view returns (address, address) {
    bytes32 digest = _hashTypedDataV4(
      keccak256(abi.encode(keccak256("Call(address buyer,uint256 tokenId,uint256 price)"), _buyer, _tokenId, _price ))
    );
    address recoveredSigner = ECDSA.recover(digest, _signature);
    require(verifier() == recoveredSigner, 'Invalid signature');
    return (verifier(), recoveredSigner);
  }

  /**
   * @dev Buy a token with ERC20 & signature. Not Payable method
   * @param _tokenId | Token ID
   * @param _price | Token price
   * @param _payTokenAddress | Token address (ERC20) to pay with
   * @param _signature | The signature
