    "contracts/polygon/ERC20/ERC20MinterPauser.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";

contract ERC20MinterPauser is ERC20, ERC20Pausable, Ownable {

  constructor(string memory name, string memory symbol, address minter)
    ERC20(name, symbol) {
    transferOwnership(minter);
  }

  function pause() onlyOwner public virtual {
    _pause();
  }

  function unpause() onlyOwner public virtual {
    _unpause();
  }

  function mint(address account, uint256 amount) onlyOwner external
  {
    _mint(account, amount);
  }

  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 amount
  ) internal virtual override(ERC20, ERC20Pausable) {
    super._beforeTokenTransfer(from, to, amount);
  }
}
"
    },
    "contracts/polygon/ERC721/ERC721MinterPauserInmutable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;
import "./ERC721Tradable.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// import "hardhat/console.sol";

/**
 * @title ERC721MinterPauserInmutable
 * ERC721Tradeable contract with on-chain inmutable metadata
 */
contract ERC721MinterPauserInmutable is ERC721Tradable, EIP712 {
  mapping(uint256 => string) private _tokenURIs;
  address public treasury;

  /**
   * @dev Contructor
   * @param _name | The Contract name
   * @param _symbol | The Symbol
   * @param _owner | The Owner and Verifier
   * @param _minter | The Minter
   */
  constructor(string memory _name, string memory _symbol, address _owner, address _minter )
    EIP712(_name, "1")
    ERC721Tradable(_name, _symbol, _minter)
  {
    super.setMinter(_minter);
    super.setVerifier(_owner);
    transferOwnership(_owner);
  }

  /**
   * @dev Get the token URI
   * @param _tokenId | It´s the token Id
   * @return url | The token URI belongs the specific tokenId
   */
  function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
    require(_exists(_tokenId), "URI query for nonexistent token");
    string memory _tokenURI = _tokenURIs[_tokenId];
    return _tokenURI;
  }

  /**
   * @dev Set the token URI
   * @param _tokenId | It´s the token Id
   * @param _tokenURI | It´s the token URI
   */
  function _setTokenURI(uint256 _tokenId, string memory _tokenURI) internal virtual {
    require(_exists(_tokenId), "URI set of nonexistent token");
    _tokenURIs[_tokenId] = _tokenURI;
  }

  /**
   * @dev Mints a token to an address with a tokenURI.
   * @param _to | Address of the future owner of the token
   * @param _tokenURI | It´s the URL where we can see or get the metadata
   * @param _tokenId | It´s the token Id
   * @return tokenId
   */
  function mintTo(address _to, string memory _tokenURI, uint256 _tokenId) public onlyMinter returns (uint256 tokenId){
    require(!_exists(_tokenId), "Token already exist.");
    _mint(_to, _tokenId);
    _setTokenURI(_tokenId, _tokenURI);
    return _tokenId;
  }

  /**
   * @dev Buy a token with signature. Payable method
   * @param _tokenId | Token ID
   * @param _price | Token price
   * @param _signature | The signature
   * @return tokenId
   */
  function buyToken(uint256 _tokenId, uint256 _price, bytes calldata _signature) public payable returns (uint256 tokenId) {
    require(!_exists(_tokenId), "Token already exist.");
    require(msg.value >= _price, 'Insufficient funds');
    verify(_signature, _msgSender(), _tokenId, _price);
    _mint(_msgSender(), _tokenId);
    return _tokenId;
  }

  /**
   * @dev Verify the signature
   * @param _signature | The signature
   * @param _buyer | The address of the buyer
   * @param _tokenId | The tokenId of the NFT
   * @param _price | The price of the NFT
   * @return address | The verifier address
   * @return address | The verifier address calculated
   */
  function verify(
    bytes memory _signature,
    address _buyer,
    uint256 _tokenId,
    uint256 _price
  ) public view returns (address, address) {
    bytes32 digest = _hashTypedDataV4(
      keccak256(abi.encode(keccak256("Call(address buyer,uint256 tokenId,uint256 price)"), _buyer, _tokenId, _price ))
    );
    address recoveredSigner = ECDSA.recover(digest, _signature);
    require(verifier() == recoveredSigner, 'Invalid signature');
    return (verifier(), recoveredSigner);
  }

  /**
   * @dev Buy a token with ERC20 & signature. Not Payable method
   * @param _tokenId | Token ID
   * @param _price | Token price
   * @param _payTokenAddress | Token address (ERC20) to pay with
   * @param _signature | The signature
   * @return tokenId
   */
  function buyTokenWithERC20(uint256 _tokenId, uint256 _price, address _payTokenAddress, bytes calldata _signature) public returns (uint256 tokenId) {
    require(!_exists(_tokenId), "Token already exist.");
    require(treasury != address(0), "Treasury address is not defined yet");
    // Verify signature
    verifyWithERC20(_signature, _msgSender(), _tokenId, _price, _payTokenAddress);
    // Transfer treasure
    IERC20(_payTokenAddress).transferFrom(
      _msgSender(),
      treasury,
      _price
    );
    // Mint NFT
    _mint(_msgSender(), _tokenId);
    return _tokenId;
  }

  /**
   * @dev Verify the signature using ERC20
   * @param _signature | The signature
   * @param _buyer | The address of the buyer
   * @param _tokenId | The tokenId of the NFT
   * @param _price | The price of the NFT
   * @param _payTokenAddress | The token of payment (address)
   * @return address | The verifier address
   * @return address | The verifier address calculated
   */
  function verifyWithERC20(
    bytes memory _signature,
    address _buyer,
    uint256 _tokenId,
    uint256 _price, 
    address _payTokenAddress
  ) public view returns (address, address) {
    bytes32 digest = _hashTypedDataV4(
      keccak256(abi.encode(keccak256("Call(address buyer,uint256 tokenId,uint256 price,address payTokenAddress)"), _buyer, _tokenId, _price, _payTokenAddress ))
    );
    address recoveredSigner = ECDSA.recover(digest, _signature);
    require(verifier() == recoveredSigner, 'Invalid signature');
    return (verifier(), recoveredSigner);
  }

  /**
   * @dev Set treasure address
   * @param _treasury address
   */
  function setTreasureAddress(address _treasury) public onlyOwner {
    treasury = _treasury;
  }
}
"
    },
