    "contracts/PaymentForwarder.sol": {
      "content": "// SPDX-License-Identifier: MIT\npragma solidity ^0.8.11;\n\ninterface TokenContract {\n  function buy(uint256 _tokenId, address seller, address receiver) external payable;\n}\n\ncontract PaymentForwarder {\n  function buy(address tokenAddr, uint256 _tokenId, address seller, address receiver) external payable {\n    TokenContract token = TokenContract(tokenAddr);\n    token.buy{value: msg.value}(_tokenId, seller, receiver);\n  }\n\n  function buyMore(address tokenAddr, uint256 _tokenId, address seller, address receiver, uint256 quantity) external payable {\n    TokenContract token = TokenContract(tokenAddr);\n    uint pricePerToken = msg.value / quantity;\n\n    for(uint i = 0; i < quantity; i++) {\n      token.buy{value: pricePerToken}(_tokenId, seller, receiver);\n    }\n  }  \n}"
    }
