    "contracts/AvatarGenerator/FOMOAvatars.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@chainlink/contracts/src/v0.6/VRFConsumerBase.sol";

/**
 * @title JunkieCatzAvatars contract
 * @dev Extends ERC721 Non-Fungible Token Standard basic implementation
 */
contract JunkieCatzAvatars is ERC721, Ownable, VRFConsumerBase {
    using SafeERC20 for IERC20;

    string public JUNKIECATZ_PROVENANCE = "";

    uint256 public constant maxJUNKIECATZsPurchase = 20;

    uint256 public constant presaleMaxPurchase = 3;

    uint256 public MAX_JUNKIECATZ;

    bool public saleIsActive = false;

    uint256 public REVEAL_TIMESTAMP;

    uint256 public JUNKIECATZPrice;

    address public Custodywallet;

    uint256 public totalWhitelisted;

    uint16[] private _tokenIds;

    // Address => isWhitelisted
    mapping(address => bool) public whitelisted;
    mapping(address => uint256) public purchased;
    mapping(bytes32 => address) public requests;

    bytes32 internal keyHash;
    uint256 internal fee;

    /**
     * Constructor inherits VRFConsumerBase
     *
     * Network: Mumbai
     * Chainlink VRF Coordinator address: 0x8C7382F9D8f56b33781fE506E897a4F1e2d17255
     * LINK token address:                0x326C977E6efc84E512bB9C30f76E30c160eD06FB
     * Key Hash: 0x6e75b569a01ef56d18cab6a8e71e6600d6ce853834d4a5748b720d06f878b3a4
     *
     * Constructor inherits VRFConsumerBase
     *
     * Network: Matic
     * Chainlink VRF Coordinator address: 0x3d2341ADb2D31f1c5530cDC622016af293177AE0
     * LINK token address:                0xb0897686c545045aFc77CF20eC7A532E3120E0F1
     * Key Hash: 0xf86195cf7690c55907b2b611ebb7343a6f649bff128701cc542f0569e2c549da
     */

    constructor(
        string memory name,
        string memory symbol,
        uint256 maxNftSupply,
        uint256 revealTimestamp,
        address recipient
    )
        public
        ERC721(name, symbol)
        VRFConsumerBase(
            0x3d2341ADb2D31f1c5530cDC622016af293177AE0,
            0xb0897686c545045aFc77CF20eC7A532E3120E0F1
        )
    {
        MAX_JUNKIECATZ = maxNftSupply;
        REVEAL_TIMESTAMP = revealTimestamp;
        Custodywallet = recipient;
        JUNKIECATZPrice = 1e20;
        keyHash = 0xf86195cf7690c55907b2b611ebb7343a6f649bff128701cc542f0569e2c549da;
        fee = 0.0001 * 10**18; // 0.1 LINK (Varies by network)
    }

    function initialize() public onlyOwner {
        _tokenIds = new uint16[](MAX_JUNKIECATZ);
        for (uint16 i = 0; i < MAX_JUNKIECATZ; i++) {
            _tokenIds[i] = i + 1;
        }
    }

    /**
     * Requests randomness
     */
    function getRandomNumber() public returns (bytes32 requestId) {
        require(
            LINK.balanceOf(address(this)) >= fee,
            "Not enough LINK - fill contract with faucet"
        );
        return requestRandomness(keyHash, fee);
    }

    /**
     * Callback function used by VRF Coordinator
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness)
        internal
        override
    {
        address requestor = requests[requestId];
        uint256 mintIndex = SafeMath.mod(randomness, _tokenIds.length);

        _safeMint(requestor, _tokenIds[mintIndex]);

        _tokenIds[mintIndex] = _tokenIds[_tokenIds.length - 1];
        _tokenIds.pop();
    }

    function addWhitelist(address[] memory _accounts) external onlyOwner {
        require(_accounts.length != 0, "invalid accounts array");

        for (uint256 i = 0; i < _accounts.length; i++) {
            if (!whitelisted[_accounts[i]]) {
                whitelisted[_accounts[i]] = true;
                totalWhitelisted = SafeMath.add(totalWhitelisted, 1);
            }
        }
    }

    function removeWhitelist(address[] memory _accounts) external onlyOwner {
        require(_accounts.length != 0, "invalid accounts array");

        for (uint256 i = 0; i < _accounts.length; i++) {
            if (whitelisted[_accounts[i]]) {
                whitelisted[_accounts[i]] = false;
                totalWhitelisted = SafeMath.sub(totalWhitelisted, 1);
            }
        }
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        msg.sender.transfer(balance);
    }

    /**
     *
     */
    function setRevealTimestamp(uint256 revealTimeStamp) public onlyOwner {
        REVEAL_TIMESTAMP = revealTimeStamp;
    }

    /**
     * Set some FOMOs aside
     */
    function reserveJunkieCatz() public onlyOwner {
        uint256 supply = totalSupply();
        uint256 i;

        if (SafeMath.add(supply, 100) < MAX_JUNKIECATZ) {
            for (i = 0; i < 100; i++) {
                bytes32 requestId = getRandomNumber();
                requests[requestId] = msg.sender;
            }
        }
    }

    /*
     * Set provenance once it's calculated
     */
    function setProvenanceHash(string memory provenanceHash) public onlyOwner {
        JUNKIECATZ_PROVENANCE = provenanceHash;
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        _setBaseURI(baseURI);
    }

    function withdrawLink() public onlyOwner {
        LINK.transfer(msg.sender, LINK.balanceOf(address(this)));
    }

    /*
     * Pause sale if active, make active if paused
     */
    function flipSaleState() public onlyOwner {
        saleIsActive = !saleIsActive;
    }

    /**
     * Mints JunkieCatz
     */
    function mintJunkieCat(uint256 numberOfTokens) public payable {
        if (
            SafeMath.sub(REVEAL_TIMESTAMP, 3600) <= block.timestamp &&
            block.timestamp < REVEAL_TIMESTAMP
        ) {
            require(
                whitelisted[msg.sender],
                "Only whitelisted users can purchase before 1 hour"
            );
            require(
                SafeMath.add(purchased[msg.sender], numberOfTokens) <=
                    presaleMaxPurchase,
                "Invalid purchase amount"
            );
            purchased[msg.sender] = SafeMath.add(
                purchased[msg.sender],
                numberOfTokens
            );
        } else {
            require(saleIsActive, "Sale must be active to mint Creamies");
        }

        require(
            numberOfTokens <= maxJUNKIECATZsPurchase,
            "Can only mint 20 tokens at a time"
        );
        require(
            SafeMath.add(totalSupply(), numberOfTokens) <= MAX_JUNKIECATZ,
            "Purchase would exceed max supply of Junkie Catz"
        );
        require(
            SafeMath.mul(JUNKIECATZPrice, numberOfTokens) == msg.value,
            "Ether value sent is not correct"
        );

        // IERC20(saleToken).safeTransferFrom(msg.sender, custodywallet, value);
        (bool saleTransferSuccess, ) = Custodywallet.call{value: msg.value}("");
        require(saleTransferSuccess, "Ether not sent successfully");

        for (uint256 i = 0; i < numberOfTokens; i++) {
            if (totalSupply() < MAX_JUNKIECATZ) {
                bytes32 requestId = getRandomNumber();
                requests[requestId] = msg.sender;
                // _safeMint(msg.sender, mintIndex);
            }
        }
    }

    function updateCustodyAddress(address _account) public onlyOwner {
        require(_account != address(0), "invalid custody address");

        Custodywallet = _account;
    }

    function updateJunkieCatzPrice(uint256 _price) public onlyOwner {
        require(_price != 0, "invalid price");

        JUNKIECATZPrice = _price;
    }
}
