pragma solidity ^0.8.17;

library Helper {


    struct INV{
        address account;
        uint256 amount;
        uint256 trigger;
    }

    struct CFG_INV{
        uint32 profitRate; //0...10000
        uint32 max_inv;    
        uint32 max_turn;    
        uint256 current_turn;
        uint256 turn;
        uint256 trigger;
        uint256 current_supply;
        uint256 currentPiAmount;        
    }


}



//import "./Helper.sol"  ;


pragma solidity ^0.8.17;

//  EB v 5
contract LoansEnd is Pausable, AccessControl {


    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    IAUTOPOOLEND public autoPOOL;
    Helper.CFG_INV public cfg_inv;
    IERC_20 public endtoken;


    mapping(uint256 => Helper.INV) public inv_queue  ;

    mapping(address => uint256) public user_amount  ;



    event NewInvestment(address indexed account,uint256 amount,uint256 indexed turn);
    event PaidInvestment(address indexed account,uint256 amount,uint256 indexed turn);


    constructor(
        address _autoPOOL,
        IERC_20 end_token

    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        autoPOOL = (IAUTOPOOLEND)(_autoPOOL);
        endtoken = (IERC_20)(end_token);

        cfg_inv.trigger =100 ether;
        cfg_inv.profitRate = 1000; // 10%
        cfg_inv.max_inv = 100;//cfg_inv.trigger * 100;
        cfg_inv.max_turn = 100;
    }



    // This function is called for all messages sent to
    // this contract, except plain Ether transfers
    // (there is no other function except the receive function).
    // Any call with non-empty calldata to this contract will execute
    // the fallback function (even if Ether is sent along with the call).
    fallback() external payable {}

    // This function is called for plain Ether transfers, i.e.
    // for every call with empty calldata.
    receive() external payable {

        require(false,"LOANS: transfer eth not allowed!!");
    }


    function updateFromContract(uint256 amount) external onlyRole(ADMIN_ROLE) {

        cfg_inv.currentPiAmount += amount;
        update_invest();

    }




    function receiveFromContract(uint256 amount) external  {

        require(
            endtoken.balanceOf(_msgSender()) >= amount,
            "LOANS: insufficient balance"
        );

        cfg_inv.currentPiAmount += amount;
        update_invest();
        endtoken.burnFrom(_msgSender(), amount);
    }



    function invest(uint256 amount) external  whenNotPaused{

        uint256 cfg_inv_turn = cfg_inv.turn;
        uint256 cfg_inv_trigger = cfg_inv.trigger;

        require(autoPOOL.isLocked(), "LOANS: AutoPOOL must be locked ");

        require(autoPOOL.isLocked(), "LOANS: AutoPOOL must be locked ");

        require(user_amount[_msgSender()] == 0 , "LOANS:The user still has an active deposit ");

        require( (cfg_inv_turn - cfg_inv.current_turn) <= cfg_inv.max_turn, "LOANS: queue exceeds maximum allowed");    

        require(amount <= (cfg_inv_trigger * cfg_inv.max_inv) , "LOANS: exceeded the maximum allowed");

        uint256 exact_amount=(amount / cfg_inv_trigger) * cfg_inv_trigger;

        require(exact_amount > 0, "LOANS: amount rounded must be greater than 0");

        require(
            endtoken.balanceOf(_msgSender()) >= exact_amount,
            "LOANS: insufficient balance"
        );

        update_invest();

        cfg_inv.turn++;
        cfg_inv_turn = cfg_inv.turn;
        cfg_inv.current_supply+=exact_amount;
        inv_queue[cfg_inv_turn].amount=exact_amount;
        inv_queue[cfg_inv_turn].trigger=cfg_inv_trigger;
        inv_queue[cfg_inv_turn].account=_msgSender();

        (IAUTOPOOLEND)(autoPOOL).updateFromContract(exact_amount);

        user_amount[_msgSender()]+=exact_amount;
        
        emit NewInvestment(_msgSender(),exact_amount,cfg_inv_turn);
        
        update_invest();

        endtoken.burnFrom(_msgSender(), exact_amount);

    }


    function  update_invest() public {

        uint256 cfg_inv_turn = cfg_inv.turn;

        uint256 cfg_inv_current_turn=cfg_inv.current_turn;
        uint256 cfg_inv_currentPiAmount=cfg_inv.currentPiAmount;

         

        if(cfg_inv_turn==0) return;

        // reached the end of the queue of investors there is no more
        if( cfg_inv_current_turn > cfg_inv_turn ) return;

        //begin
        if(cfg_inv_current_turn==0)
        {
            (cfg_inv_current_turn,cfg_inv.current_turn)=(1,1);
        }
         

        Helper.INV memory queue = inv_queue[cfg_inv_current_turn];
        
        //move to the next turn
        if( queue.amount == 0 && cfg_inv_current_turn < cfg_inv_turn ){
            cfg_inv_current_turn=++cfg_inv.current_turn;
            return;
        } 

        if( queue.amount == 0 || cfg_inv.current_supply == 0 ){
            return;
        }


        uint256 trigger_amount = queue.trigger + ( queue.trigger * cfg_inv.profitRate /10000);

        if(trigger_amount > cfg_inv_currentPiAmount || cfg_inv_currentPiAmount == 0  ) return;

        cfg_inv.currentPiAmount-= trigger_amount;
        cfg_inv.current_supply-= queue.trigger;
        inv_queue[cfg_inv_current_turn].amount-= queue.trigger;
        user_amount[queue.account]=inv_queue[cfg_inv_current_turn].amount;

        endtoken.mint(queue.account , trigger_amount);

        emit PaidInvestment(queue.account,trigger_amount,cfg_inv_current_turn);

        //move to the next turn
        if(inv_queue[cfg_inv_current_turn].amount==0 && cfg_inv_current_turn < cfg_inv_turn){
            cfg_inv.current_turn++;
        }


    }







    function changeAutoPOOL(address payable newAP)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newAP != address(0), "LOANS: Invalid address");
        autoPOOL = (IAUTOPOOLEND)(newAP);
    }




    function changeCNF_INVop(uint256 newTrigger,uint32 newProfitRate,uint32 newMaxInv,uint32 newMaxturn)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newProfitRate >0 && newProfitRate <= 10000, "LOANS: Invalid profitRate");
        require(newTrigger >0 , "LOANS: Invalid newTrigger");
        require(newMaxInv >0 , "LOANS: Invalid newMaxInv");
        require(newMaxturn >0 , "LOANS: Invalid newMaxturn");


        cfg_inv.trigger =newTrigger;
        cfg_inv.profitRate = newProfitRate;
        cfg_inv.max_inv = newMaxInv;
        cfg_inv.max_turn = newMaxturn;
    }




    function update() external {
        update_invest();
        
    }



    function pause() public onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(ADMIN_ROLE) {
        _unpause();
    }


}
