pragma solidity >=0.8.4;

import { Order, ExecuteOrderAction } from '../libraries/OrderStructs.sol';

interface INFTMarket {
  event FillOrder(
    uint256 indexed hashId,
    address indexed nft,
    uint256 indexed tokenId,
    address trader,
    address seller,
    address buyer,
    uint256 amount,
    address currency,
    uint256 price
  );

  event CancelOrder(
    uint256 indexed hashId,
    address indexed nft,
    uint256 indexed tokenId,
    address userId,
    uint256 amount,
    address currency,
    uint256 price1,
    uint256 price2
  );

  event IncrementUserNonce(address indexed userId, uint256 newNonce);

  event UpdateSigner(address newSigner);

  function cancelOrder(Order calldata order) external;

  function fillOrder(Order calldata order) external payable;

  function matchOrders(Order calldata sellOrder, Order calldata buyOrder) external;

  function executeOrders(ExecuteOrderAction[] calldata orders) external payable;

  function incrementUserNonce() external;
}
