    "contracts/libraries/EIP712.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.4;

import { SignatureChecker } from '@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol';

import { FeeConfig, OrderParams, Order } from './OrderStructs.sol';

contract EIP712 {
  bytes32 constant EIP712DOMAIN_TYPEHASH =
    keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)');

  bytes32 public constant FEE_CONFIG_TYPEHASH = keccak256('FeeConfig(address to,uint16 rate)');

  bytes32 public constant ORDER_PARAMS_TYPEHASH =
    keccak256(
      'OrderParams(address currency,uint256 price1,uint256 price2,address toUserId,uint256 createdAt,uint256 expiredAt)'
    );

  bytes32 public constant ORDER_TYPEHASH =
    keccak256(
      'Order(uint256 id,address userId,uint256 nonce,uint256 chainId,address nft,uint256 tokenId,uint256 amount,uint8 side,uint8 typ,OrderParams params,FeeConfig[] fees)FeeConfig(address to,uint16 rate)OrderParams(address currency,uint256 price1,uint256 price2,address toUserId,uint256 createdAt,uint256 expiredAt)'
    );

  bytes32 public DOMAIN_SEPARATOR;

  function _hashDomain(
    string memory name,
    string memory version,
    uint256 chainid,
    address verifyingContract
  ) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encode(EIP712DOMAIN_TYPEHASH, keccak256(bytes(name)), keccak256(bytes(version)), chainid, verifyingContract)
      );
  }

  function _hashFee(FeeConfig calldata fee) internal pure returns (bytes32) {
    return keccak256(abi.encode(FEE_CONFIG_TYPEHASH, fee.to, fee.rate));
  }

  function _hashOrderParams(OrderParams calldata params) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encode(
          ORDER_PARAMS_TYPEHASH,
          params.currency,
          params.price1,
          params.price2,
          params.toUserId,
          params.createdAt,
          params.expiredAt
        )
      );
  }

  function _packFees(FeeConfig[] calldata fees) internal pure returns (bytes32) {
    bytes32[] memory feeHashes = new bytes32[](fees.length);
    for (uint256 i = 0; i < fees.length; i++) {
      feeHashes[i] = _hashFee(fees[i]);
    }
    return keccak256(abi.encodePacked(feeHashes));
  }

  function _hashOrder(Order calldata order) internal pure returns (bytes32) {
    return
      keccak256(
        abi.encode(
          ORDER_TYPEHASH,
          order.id,
          order.userId,
          order.nonce,
          order.chainId,
          order.nft,
          order.tokenId,
          order.amount,
          order.side,
          order.typ,
          _hashOrderParams(order.params),
          _packFees(order.fees)
        )
      );
  }

  function _hashOrderToSign(Order calldata order) internal view returns (bytes32) {
    bytes32 orderHash = _hashOrder(order);
    return keccak256(abi.encodePacked('\x19\x01', DOMAIN_SEPARATOR, orderHash));
  }

  function _isValidOrderSignature(
    address signer,
    bytes32 orderHashToSign,
    bytes memory signature
  ) internal view returns (bool) {
    return signer != address(0) && SignatureChecker.isValidSignatureNow(signer, orderHashToSign, signature);
  }
}
"
