    "contracts/libraries/OrderStructs.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.4;

enum OrderSide {
  SELL,
  BUY
}

enum OrderType {
  FIXED,
  DUTCH_AUCTION,
  ENGLISH_AUCTION
}

enum OrderResult {
  CANCELLED,
  FILLED
}

enum OrderAction {
  CANCEL,
  FILL
}

struct FeeConfig {
  address to; // recipient address
  uint16 rate; // 10 = 0.1%, 250 = 2.5%
}

struct OrderParams {
  address currency; // payment token address (0x0 = Chain Native Token)
  uint256 price1;
  uint256 price2;
  address toUserId;
  uint256 createdAt;
  uint256 expiredAt;
}

struct Order {
  uint256 id;
  address userId; // address of this order owner
  uint256 nonce;
  uint256 chainId;
  address nft; // nft contract address
  uint256 tokenId;
  uint256 amount;
  OrderSide side;
  OrderType typ;
  OrderParams params;
  FeeConfig[] fees;
  bytes userSignature; // the signature signed when user created this order
  bytes serverSignature; // the signature signed by server
}

struct ExecutedOrder {
  uint256 hashId;
  OrderResult result;
  uint256 blockNo;
  uint256 timestamp;
}

struct ExecuteOrderAction {
  Order order;
  OrderAction action;
}
"
