    "contracts/NFTMarket.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.4;

import { IERC20 } from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import { SafeERC20 } from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import { IERC721 } from '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import { IERC1155 } from '@openzeppelin/contracts/token/ERC1155/IERC1155.sol';
import { Math } from '@openzeppelin/contracts/utils/math/Math.sol';
import { Address } from '@openzeppelin/contracts/utils/Address.sol';
import { ERC165Checker } from '@openzeppelin/contracts/utils/introspection/ERC165Checker.sol';
import { Ownable } from '@openzeppelin/contracts/access/Ownable.sol';
import { Pausable } from '@openzeppelin/contracts/security/Pausable.sol';
import { ReentrancyGuard } from '@openzeppelin/contracts/security/ReentrancyGuard.sol';

import { EIP712 } from './libraries/EIP712.sol';
import {
  Order,
  ExecutedOrder,
  OrderType,
  OrderSide,
  OrderResult,
  FeeConfig,
  OrderAction,
  ExecuteOrderAction
} from './libraries/OrderStructs.sol';

import { INFTMarket } from './interfaces/INFTMarket.sol';

contract NFTMarket is INFTMarket, EIP712, Ownable, Pausable, ReentrancyGuard {
  using Address for address payable;
  using SafeERC20 for IERC20;
  using ERC165Checker for address;

  bytes4 private constant ERC1155_IID = type(IERC1155).interfaceId;
  bytes4 private constant IERC721_IID = type(IERC721).interfaceId;

  address public signer; // the wallet address to verify serverSignature
  mapping(address => uint256) public userNonce;
  mapping(uint256 => bool) public isOrderExecuted;
  ExecutedOrder[] public executedOrders;

  constructor(string memory name, string memory version, address serverSigner) {
    DOMAIN_SEPARATOR = _hashDomain(name, version, block.chainid, address(this));
    setSigner(serverSigner);
  }

  receive() external payable {}

  //////////////////////////
  /////////  user  /////////
  //////////////////////////

  function cancelOrder(Order calldata order) public override whenNotPaused nonReentrant {
    _cancelOrder(order);
  }

  function fillOrder(Order calldata order) public payable override whenNotPaused nonReentrant {
    _fillOrder(order);
  }

  function executeOrders(ExecuteOrderAction[] calldata orders) external payable override whenNotPaused nonReentrant {
    uint256 totalPrice = 0;
    for (uint256 i = 0; i < orders.length; i++) {
      if (orders[i].action == OrderAction.FILL && orders[i].order.params.currency == address(0)) {
        totalPrice += _calcOrderPrice(orders[i].order);
      }
    }

    if (totalPrice > 0 && msg.value < totalPrice) {
      revert('NFTMarket: PayAmountNotEnough');
    }

    for (uint256 i = 0; i < orders.length; i++) {
      if (orders[i].action == OrderAction.CANCEL) {
        _cancelOrder(orders[i].order);
      } else if (orders[i].action == OrderAction.FILL) {
        _fillOrder(orders[i].order);
      }
    }
  }

  function matchOrders(Order calldata sellOrder, Order calldata buyOrder) external override whenNotPaused nonReentrant {
    _matchOrders(sellOrder, buyOrder);
  }

  /**
   * @dev Cancel all current orders for a user, preventing them from being matched. Must be called by the owner of the order
   */
  function incrementUserNonce() external override whenNotPaused nonReentrant {
    userNonce[msg.sender] += 1;
    emit IncrementUserNonce(msg.sender, userNonce[msg.sender]);
  }

  //////////////////////////
  ///////  internal  ///////
  //////////////////////////

  function _cancelOrder(Order calldata order) internal {
    bytes32 orderHashToSign = _hashOrderToSign(order);
    uint256 hashId = uint256(orderHashToSign);

    if (isOrderExecuted[hashId]) {
      return;
    }

    if (order.userId == address(0) || msg.sender != order.userId) {
      revert('NFTMarket: InvalidSigner');
    }

    isOrderExecuted[hashId] = true;
    executedOrders.push(ExecutedOrder(hashId, OrderResult.CANCELLED, block.number, block.timestamp));

    emit CancelOrder(
      hashId,
      order.nft,
      order.tokenId,
      order.userId,
      order.amount,
      order.params.currency,
      order.params.price1,
      order.params.price2
    );
  }

  function _fillOrderCheck(Order calldata order, bool checkServerSignature) internal view returns (uint256) {
    if (order.chainId != block.chainid) {
      revert('NFTMarket: InvalidChain');
    }
    if (order.nonce != userNonce[order.userId]) {
      revert('NFTMarket: InvalidNonce');
    }
    if (block.timestamp <= order.params.createdAt || order.params.createdAt >= order.params.expiredAt) {
      revert('NFTMarket: OrderNotActive');
    }

    if (order.typ == OrderType.ENGLISH_AUCTION) {
      if (block.timestamp <= order.params.expiredAt) {
        revert('NFTMarket: OrderNotActive');
      }
    } else {
      if (block.timestamp >= order.params.expiredAt) {
        revert('NFTMarket: OrderExpired');
      }
    }

    if (
      order.params.currency == address(0) && (order.side != OrderSide.SELL || order.typ == OrderType.ENGLISH_AUCTION)
    ) {
      revert('NFTMarket: InvalidCurrency');
    }

    bytes32 orderHashToSign = _hashOrderToSign(order);
    uint256 hashId = uint256(orderHashToSign);

    if (isOrderExecuted[hashId]) {
      revert('NFTMarket: OrderCancelledOrFilled');
    }
    if (!_isValidOrderSignature(order.userId, orderHashToSign, order.userSignature)) {
      revert('NFTMarket: InvalidSignature');
    }
    if (checkServerSignature && !_isValidOrderSignature(signer, orderHashToSign, order.serverSignature)) {
      revert('NFTMarket: InvalidSignature');
    }

    return hashId;
  }

  function _calcOrderPrice(Order calldata order) internal view returns (uint256) {
    if (order.typ == OrderType.FIXED) {
      return order.params.price1;
    }
    if (order.typ == OrderType.DUTCH_AUCTION) {
      if (block.timestamp <= order.params.createdAt) {
        return order.params.price1;
      }
      if (block.timestamp >= order.params.expiredAt) {
        return order.params.price2;
      }
      uint256 priceDiff = order.params.price1 - order.params.price2;
      uint256 currentDuration = block.timestamp - order.params.createdAt;
      uint256 auctionDuration = order.params.expiredAt - order.params.createdAt;
      uint256 price = order.params.price1 - (priceDiff * currentDuration) / auctionDuration;
      return Math.max(price, order.params.price2);
    }
    if (order.typ == OrderType.ENGLISH_AUCTION) {
      return Math.max(order.params.price1, order.params.price2);
    }
    return 0;
  }

  function _transferFundsAndFees(
    address seller,
    address buyer,
    address currency,
    uint256 price,
    FeeConfig[] calldata fees
  ) internal {
    uint256 totalFee = 0;
    for (uint8 i = 0; i < fees.length; i++) {
      uint256 fee = (price * fees[i].rate) / 10000;
      _transferToken(currency, buyer, fees[i].to, fee);
      totalFee += fee;
    }

    if (totalFee > price) {
      revert('NFTMarket: OrderFeesOverPrice');
    }

    _transferToken(currency, buyer, seller, price - totalFee);
  }

  function _transferToken(address currency, address from, address to, uint256 amount) internal {
    if (amount == 0) {
      return;
    }
    if (currency == address(0)) {
      payable(to).sendValue(amount);
    } else {
      IERC20(currency).safeTransferFrom(from, to, amount);
    }
  }

  function _transferNonFungibleToken(
    address seller,
    address buyer,
    address nft,
    uint256 tokenId,
    uint256 amount
  ) internal {
    if (nft.supportsInterface(IERC721_IID)) {
      IERC721(nft).safeTransferFrom(seller, buyer, tokenId);
      return;
    }
    if (nft.supportsInterface(ERC1155_IID)) {
      IERC1155(nft).safeTransferFrom(seller, buyer, tokenId, amount, '');
      return;
    }
    revert('InterfaceNotSupported');
  }

  function _fillOrder(Order calldata order) internal {
    if (order.typ == OrderType.ENGLISH_AUCTION) {
      revert('NFTMarket: OrderNotActive');
    }
    if (order.params.toUserId != address(0) && msg.sender != order.params.toUserId) {
      revert('NFTMarket: InvalidSigner');
    }

    uint256 hashId = _fillOrderCheck(order, false);

    isOrderExecuted[hashId] = true;
    executedOrders.push(ExecutedOrder(hashId, OrderResult.FILLED, block.number, block.timestamp));

    (address seller, address buyer) = order.side == OrderSide.SELL
      ? (order.userId, msg.sender)
      : (msg.sender, order.userId);
    uint256 price = _calcOrderPrice(order);

    if (price <= 0) {
      revert('NFTMarket: InvalidPrice');
    }
    if (order.params.currency == address(0) && msg.value < price) {
      revert('NFTMarket: PayAmountNotEnough');
    }

    _transferFundsAndFees(seller, buyer, order.params.currency, price, order.fees);
    _transferNonFungibleToken(seller, buyer, order.nft, order.tokenId, order.amount);

    emit FillOrder(
      hashId,
      order.nft,
      order.tokenId,
      msg.sender,
      seller,
      buyer,
      order.amount,
      order.params.currency,
      price
    );
  }

  function _matchOrders(Order calldata sellOrder, Order calldata buyOrder) internal {
    if (
      sellOrder.nft != buyOrder.nft ||
      sellOrder.tokenId != buyOrder.tokenId ||
      sellOrder.amount != buyOrder.amount ||
      sellOrder.side != OrderSide.SELL ||
      buyOrder.side != OrderSide.BUY ||
      sellOrder.params.currency != buyOrder.params.currency ||
      (sellOrder.params.toUserId != address(0) && sellOrder.params.toUserId != buyOrder.userId) ||
      (buyOrder.params.toUserId != address(0) && buyOrder.params.toUserId != sellOrder.userId)
    ) {
      revert('NFTMarket: OrdersNotMatch');
    }

    if (buyOrder.params.currency == address(0)) {
      revert('NFTMarket: InvalidCurrency');
    }

    uint256 sellHashId = _fillOrderCheck(sellOrder, true);
    uint256 buyHashId = _fillOrderCheck(buyOrder, true);

    uint256 sellPrice = _calcOrderPrice(sellOrder);
    uint256 buyPrice = _calcOrderPrice(buyOrder);
    if (sellPrice <= 0 || sellPrice > buyPrice) {
      revert('NFTMarket: InvalidPrice');
    }

    isOrderExecuted[sellHashId] = true;
    isOrderExecuted[buyHashId] = true;
    executedOrders.push(ExecutedOrder(sellHashId, OrderResult.FILLED, block.number, block.timestamp));
    executedOrders.push(ExecutedOrder(buyHashId, OrderResult.FILLED, block.number, block.timestamp));

    _transferFundsAndFees(sellOrder.userId, buyOrder.userId, buyOrder.params.currency, buyPrice, buyOrder.fees);
    _transferNonFungibleToken(sellOrder.userId, buyOrder.userId, buyOrder.nft, buyOrder.tokenId, buyOrder.amount);

    emit FillOrder(
      sellHashId,
      sellOrder.nft,
      sellOrder.tokenId,
      msg.sender,
      sellOrder.userId,
      buyOrder.userId,
      sellOrder.amount,
      sellOrder.params.currency,
      buyPrice
    );

    emit FillOrder(
      buyHashId,
      buyOrder.nft,
      buyOrder.tokenId,
      msg.sender,
      sellOrder.userId,
      buyOrder.userId,
      buyOrder.amount,
      buyOrder.params.currency,
      buyPrice
    );
  }

  //////////////////////////
  /////////  view  /////////
  //////////////////////////

  function getExecutedOrdersLength() external view returns (uint256) {
    return executedOrders.length;
  }

  function getExecutedOrders(uint256 startIndex, uint256 endIndex) external view returns (ExecutedOrder[] memory) {
    if (executedOrders.length == 0) {
      return new ExecutedOrder[](0);
    }

    if (endIndex == 0) {
      endIndex = executedOrders.length - 1;
    }

    ExecutedOrder[] memory orders = new ExecutedOrder[](endIndex - startIndex + 1);

    for (uint256 index = startIndex; index <= endIndex; index++) {
      orders[index - startIndex] = executedOrders[index];
    }

    return orders;
  }

  /////////////////////////
  ////////  admin  ////////
  /////////////////////////

  function pause() external onlyOwner {
    _pause();
  }

  function unpause() external onlyOwner {
    _unpause();
  }

  function setSigner(address newSigner) public onlyOwner {
    if (signer == newSigner) return;
    signer = newSigner;
    emit UpdateSigner(newSigner);
  }

  function ownerWithdraw(address token, address to, uint256 amount) external onlyOwner {
    if (token == address(0)) {
      payable(to).sendValue(amount);
    } else {
      IERC20(token).safeTransfer(to, amount);
    }
  }
}
"
    }
  },
  "settings": {
    "metadata": {
      "bytecodeHash": "none"
    },
    "optimizer": {
      "enabled": true,
      "runs": 800
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
