    "contracts/OatIssuer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./library/SafeMath.sol";
import "./interface/IArborswapOat.sol";

contract OatIssuer is AccessControl {
    using Counters for Counters.Counter;
    using SafeMath for uint256;

    IArborswapOat public arborswapOat;

    uint256 public constant LAST_ID = 19;

    mapping(address => mapping(uint => bool)) private _isWhitelisted;
    mapping(address => mapping(uint => bool)) private _isAlreadyClaimed;

    Counters.Counter private _tokenIdCounter;

    bytes32 public constant CREATOR_ADMIN_SERVER =
        keccak256("CREATOR_ADMIN_SERVER");

    constructor(address minter, address _arborswapOat) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(CREATOR_ADMIN_SERVER, minter);
        arborswapOat = IArborswapOat(_arborswapOat);

        for (uint i = 0; i < LAST_ID; i++) {
            _tokenIdCounter.increment();
        }
    }

    event ClaimOat(uint256 tokenId, address owner, uint256 campaignId);

    /**
     * default state of oat
     */

    struct CampaignInfo {
        uint256 id;
        string name;
    }

    CampaignInfo[] public allCampaigns;

    mapping(uint256 => CampaignInfo) public campaignInfo;

    function getCampaign(
        uint256 id
    ) external view returns (CampaignInfo memory) {
        return campaignInfo[id];
    }

    function check_id() external view returns (uint256) {
        return _tokenIdCounter.current();
    }

    function claimOat(uint256 _campaignId) public {
        require(_campaignId > 0, "campaignId must be greater than 0");
        CampaignInfo memory campaign = campaignInfo[_campaignId];
        require(
            keccak256(bytes(campaign.name)) != keccak256(bytes("")),
            "campaign not found"
        );
        require(_isWhitelisted[msg.sender][_campaignId], "not whitelisted");
        require(!_isAlreadyClaimed[msg.sender][_campaignId], "already claimed");

        _tokenIdCounter.increment();
        uint256 newTokenId = _tokenIdCounter.current();
        arborswapOat.safeMint(msg.sender, newTokenId);

        _isAlreadyClaimed[msg.sender][_campaignId] = true;

        arborswapOat.addCampaign(newTokenId, _campaignId, campaign.name);

        // campaignInfo[newTokenId] = CampaignInfo(_campaignId, campaign.name);
        emit ClaimOat(newTokenId, msg.sender, _campaignId);
    }

    function addCampaign(
        uint256 _campaignId,
        string memory _campaignName
    ) public onlyRole(CREATOR_ADMIN_SERVER) {
        require(_campaignId > 0, "campaignId must be greater than 0");
        require(
            keccak256(bytes(_campaignName)) != keccak256(bytes("")),
            "campaign name cannot be empty"
        );
        require(
            keccak256(bytes(campaignInfo[_campaignId].name)) ==
                keccak256(bytes("")),
            "campaign already exists"
        );
        allCampaigns.push(CampaignInfo(_campaignId, _campaignName));
        campaignInfo[_campaignId] = CampaignInfo(_campaignId, _campaignName);
    }

    function setBaseURI(
        string memory _baseUri
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        arborswapOat.setBaseURI(_baseUri);
    }

    /**
     * utility functions
     */

    function whitelistMultipleUsers(
        address[] memory _users,
        uint _campaignId
    ) public onlyRole(CREATOR_ADMIN_SERVER) {
        for (uint i = 0; i < _users.length; i++) {
            _isWhitelisted[_users[i]][_campaignId] = true;
        }
    }

    function whitelistUser(
        address _user,
        uint _campaignId
    ) public onlyRole(CREATOR_ADMIN_SERVER) {
        _isWhitelisted[_user][_campaignId] = true;
    }

    function isWhitelisted(
        address _user,
        uint _campaignId
    ) public view returns (bool) {
        return _isWhitelisted[_user][_campaignId];
    }

    function isAlreadyClaimed(
        address _user,
        uint _campaignId
    ) public view returns (bool) {
        return _isAlreadyClaimed[_user][_campaignId];
    }

    function blockUser(
        address _user,
        uint _campaignId
    ) public onlyRole(CREATOR_ADMIN_SERVER) {
        _isWhitelisted[_user][_campaignId] = false;
    }

    function blockMultipleUsers(
        address[] memory _users,
        uint _campaignId
    ) public onlyRole(CREATOR_ADMIN_SERVER) {
        for (uint i = 0; i < _users.length; i++) {
            _isWhitelisted[_users[i]][_campaignId] = false;
        }
    }

    function getPaginateCampaign(
        uint256 startIndex,
        uint256 endIndex
    ) external view returns (CampaignInfo[] memory) {
        require(endIndex > startIndex, "BAD_INPUT");
        require(endIndex <= allCampaigns.length, "OUT_OF_RANGE");

        CampaignInfo[] memory _campaignInfo = new CampaignInfo[](
            endIndex - startIndex
        );
        uint256 index = 0;

        for (uint256 i = startIndex; i < endIndex; i++) {
            _campaignInfo[index] = allCampaigns[i];
            index++;
        }

        return _campaignInfo;
    }

    function getAllCampaign() external view returns (CampaignInfo[] memory) {
        uint256 endIndex = allCampaigns.length;
        uint256 startIndex = 0;

        CampaignInfo[] memory _campaignInfo = new CampaignInfo[](
            endIndex - startIndex
        );
        uint256 index = 0;

        for (uint256 i = startIndex; i < endIndex; i++) {
            _campaignInfo[index] = allCampaigns[i];
            index++;
        }

        return _campaignInfo;
    }

    function getTotalCampaign() external view returns (uint256) {
        return allCampaigns.length;
    }
}
"
    }
