    "contracts/LockTokens.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "./KPNToken.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract LockTokens{
    // owners address
    address public owner;
    // KPN Token Contract
    KPNToken private KPN;
    uint256 public totalLockedTokens; // total value locked
    address private USDT; // USDT token address - can be replaced with whatever ERC20 token you would like to use

    // tier cut off amounts
    uint256 public tier1Cutoff = 500 * (10**18);
    uint256 public tier2Cutoff = 1000 * (10**18);
    uint256 public tier3Cutoff = 1500 * (10**18);
    uint256 public tier4Cutoff = 2000 * (10**18);
    uint256 public tier5Cutoff = 2500 * (10**18);
    uint256 public tier6Cutoff = 3000 * (10**18);

    // tier time locks
    uint256 public locktime1 = 180 days; 
    uint256 public locktime2 = 365 days;
    uint256 public locktime3 = 730 days;

    // reward apy
    uint256 public tier1Reward = 2; // 2% apy
    uint256 public tier2Reward = 3; // 3% apy
    uint256 public tier3Reward = 4; // 4%apy

    // struct to track locked stakes
    struct LockAmount{
        uint256 lockTime;
        uint256 amount;
        uint256 timestamp;
    }

    //mapping of user address to locked amount struct for locked up tokens
    mapping(address=>LockAmount) public lockedBalances;

    // events for locking withdrawing and paying rewards
    event TokensLocked(address indexed sender, uint256 lockDuration);
    event TokensWithdrawn(address indexed sender, uint256 timestamp);
    event RewardsPaid(address indexed receiver, uint256 amount);

    // sets kpn token and rewards token and deployer as owner
    constructor(address kpntoken, address rewardsToken){
        owner = msg.sender;
        KPN = KPNToken(kpntoken);
        USDT = rewardsToken;

    }

    // staking function
    // automatically determines stake legnth and apy based on stake amount
    // amount is params of token staked
     function lockTokens(uint256 amount) public {
        require(amount >= 0, "ERC20: Cannot send 0 tokens");
        require(amount >= tier1Cutoff, "Locking ERC20: Please send minimum token amount");

        // this refunds the current tokens staked and resets the lock time to avoid vulnerability
        if(lockedBalances[msg.sender].amount > 0){
            uint256 refund = lockedBalances[msg.sender].amount;
            lockedBalances[msg.sender].amount = 0;
            lockedBalances[msg.sender].lockTime = 0;
            KPN.transfer(msg.sender, refund);
            emit TokensWithdrawn(msg.sender, block.timestamp);
        }

        uint lockDuration;

        if(amount >= tier1Cutoff && amount <tier2Cutoff){
            uint256 extra = amount - tier1Cutoff; // get extra off of sent amount
            uint256 lockAmount = amount - extra; // calculate the lock amount back to tier amount
            lockDuration = locktime1;
            LockAmount memory newLock = LockAmount(locktime1, lockAmount, block.timestamp); // set the tokens into locked status

            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= lockAmount;
            KPN.transferFrom(msg.sender, address(this), lockAmount);

        }else if (amount >= tier2Cutoff && amount <tier3Cutoff){
            uint256 extra = amount - tier2Cutoff; // get extra off of sent amount
            uint256 lockAmount = amount - extra; // calculate the lock amount back to tier amount
            LockAmount memory newLock = LockAmount(locktime1, lockAmount, block.timestamp); // set the tokens into locked status
            lockDuration = locktime1;

            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= lockAmount;
            KPN.transferFrom(msg.sender, address(this), lockAmount);

        }else if (amount >= tier3Cutoff && amount < tier4Cutoff){
            uint256 extra = amount - tier3Cutoff; // get extra off of sent amount
            uint256 lockAmount = amount - extra; // calculate the lock amount back to tier amount
            LockAmount memory newLock = LockAmount(locktime2, lockAmount, block.timestamp); // set the tokens into locked status
            lockDuration = locktime2;
            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= lockAmount;
            KPN.transferFrom(msg.sender, address(this), lockAmount);

        } else if (amount >= tier4Cutoff && amount < tier5Cutoff){
            uint256 extra = amount - tier4Cutoff; // get extra off of sent amount
            uint256 lockAmount = amount - extra; // calculate the lock amount back to tier amount
            LockAmount memory newLock = LockAmount(locktime2, lockAmount, block.timestamp); // set the tokens into locked status
            lockDuration = locktime2;

            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= lockAmount;
            KPN.transferFrom(msg.sender, address(this), lockAmount);

        } else if (amount >= tier5Cutoff && amount < tier6Cutoff){
            uint256 extra = amount - tier5Cutoff; // get extra off of sent amount
            uint256 lockAmount = amount - extra; // calculate the lock amount back to tier amount
            LockAmount memory newLock = LockAmount(locktime3, lockAmount, block.timestamp); // set the tokens into locked status
            lockDuration = locktime3;
            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= lockAmount;
            KPN.transferFrom(msg.sender, address(this), lockAmount);

        } else {
            // top tier no lock no lock cap
            LockAmount memory newLock = LockAmount(locktime3, amount, block.timestamp); // set the tokens into locked status
            lockDuration = locktime3;
            lockedBalances[msg.sender] = newLock;
            totalLockedTokens+= amount;
            KPN.transferFrom(msg.sender, address(this), amount);

        }
        emit TokensLocked(msg.sender, lockDuration);
    }

    // staking function -- does not auto pick lock time based on tokens sent
    // params-- amount of tokens and timeframe of lock
    function stake(uint tokenAmount, uint256 timeFrame) public{
        require(tokenAmount >= 0, "ERC20: Cannot send 0 tokens");
        require(tokenAmount >= tier1Cutoff, "Locking ERC20: Please send minimum token amount");
        require(timeFrame == locktime1 || timeFrame == locktime2 || timeFrame == locktime3, "Locking ERC20: Please select proper time frame");

        if(lockedBalances[msg.sender].amount > 0){
            uint256 refund = lockedBalances[msg.sender].amount;
            lockedBalances[msg.sender].amount = 0;
            lockedBalances[msg.sender].lockTime = 0;
            KPN.transfer(msg.sender, refund);
            emit TokensWithdrawn(msg.sender, block.timestamp);
        }

        LockAmount memory newLock = LockAmount(timeFrame, tokenAmount, block.timestamp); // set the tokens into locked status

        lockedBalances[msg.sender] = newLock;
        totalLockedTokens+= tokenAmount;
        KPN.transferFrom(msg.sender, address(this), tokenAmount);
        emit TokensLocked(msg.sender, timeFrame);

    }

    // withdraw function 
    // checks for duration of time passed -reverts reverts on failure
    // checks user has funds deposits -reverts on failure
    // checks balance of contract -reverts on failure
    function withdrawLocked() public {
        require(block.timestamp > lockedBalances[msg.sender].lockTime + lockedBalances[msg.sender].timestamp, "ERC20: Time Lock not complete");
        require(lockedBalances[msg.sender].amount > 0, "ERC20: No Funds Locked");
        uint256 tokenAmount = lockedBalances[msg.sender].amount; // gets local copy of variable to save on gas cost
        require(KPN.balanceOf(address(this)) >= tokenAmount, "ERC20: Contract Balance Too Low"); 
        // gets local copy of variable to save on gas cost
        uint256 lockTime = lockedBalances[msg.sender].lockTime;
        // reduces locked tokens in contract tracker
        totalLockedTokens -= tokenAmount;
        // deletes the staking struct
        delete lockedBalances[msg.sender];
        // approves and transfers kpn balance from this contract
        KPN.approve(address(this), tokenAmount);
        KPN.transferFrom(address(this), msg.sender, tokenAmount);
        // automatically pay out rewards
        _payRewards(tokenAmount, lockTime, msg.sender);
        // emit withdrawn event
        emit TokensWithdrawn(msg.sender, block.timestamp);

    }

    // internal pay rewards helper function

    function _payRewards(uint256 amount, uint256 lockTime , address _target) internal {
        uint256 rewardAmount = _calculateRewards(amount, lockTime);
        IERC20(USDT).transfer(_target, rewardAmount);
        emit RewardsPaid(_target, amount);
    }

    // internal helper function for calculating the rewards based on amount staked, reward tier and lock time passed into other help function
    // this function determines which reward tier to calculate the rewards on
    function _calculateRewards(uint256 amountStaked, uint256 stakeDuration) internal view returns(uint256) {
        if(stakeDuration == locktime1){
            return _calculateTotalRewards(amountStaked, tier1Reward, locktime1);
        }else if(stakeDuration == locktime2){
            return _calculateTotalRewards(amountStaked, tier2Reward, locktime2);
        }else if(stakeDuration == locktime3){
            return _calculateTotalRewards(amountStaked, tier3Reward, locktime3);
        }else {
            return 0;
        }
    }

    // calculate rewards with num of kpn tokens the reward rate and the duration passed in from other helper function
    function _calculateTotalRewards(uint256 kpntokens, uint256 rewardRate, uint256 timeDuration) internal pure returns (uint256) {
        return kpntokens * rewardRate * timeDuration / (365 days * 10_000);
    }


    // set the rewards token address labeled as USDT
    function setUSDTAddress(address _usdt) public{
        require(msg.sender == owner, "ERC20 Locking: Only owner");
        USDT = _usdt;
    }

    // function to set the reward rate for tier 1
    function setTier1RewardRate(uint256 newRate) public {
        require(msg.sender == owner, "ERC20 Locking: Only owner");
        tier1Reward = newRate;
    }
    // function to set the reward rate for tier 2
    function setTier2RewardRate(uint256 newRate) public {
        require(msg.sender == owner, "ERC20 Locking: Only owner");
        tier2Reward = newRate;
    }
    // function to set the reward rate for tier 3
    function setTier3RewardRate(uint256 newRate) public {
        require(msg.sender == owner, "ERC20 Locking: Only owner");
        tier3Reward = newRate;
    }

}
