    "contracts/BuyLandMatic.sol": {
      "content": "//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "./BuyLand.sol";

contract BuyLandMatic is BuyLand {
    string public name = "BuyLand Matic";
    string public version = "1.0";

    constructor(
        address _merchant,
        address _nftAddress,
        address _ownerVaultAddress,
        address _commissionVaultAddress,
        address _charityVaultAddress,
        uint16 _charityPercent
    ) BuyLand(
        _merchant,
        _nftAddress,
        _ownerVaultAddress,
        _commissionVaultAddress,
        _charityVaultAddress,
        _charityPercent
    ) {}
}"
