    "contracts/BuyLand.sol": {
      "content": "//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./interfaces/IBuyLand.sol";
import "./interfaces/IBuyLandVault.sol";
import "./ne-nft/NFT_v3.sol";

abstract contract BuyLand is Ownable, Pausable, IBuyLand {
    using ECDSA for bytes32;

    address public merchant;
    address public ownerVaultAddress;
    address public commissionVaultAddress;
    address public charityVaultAddress;
    uint16 public charityPercent;
    NFT_v3 nft;

    event MerchantChanged(address oldMerchant, address newMerchant);
    event OwnerVaultChanged(address oldVault, address newAddress);
    event CommissionVaultChanged(address oldVault, address newAddress);
    event CharityVaultChanged(address oldVault, address newAddress);
    event CharityPercentChanged(uint16 oldPercent, uint16 newPercent);

    constructor(
        address _merchant,
        address _nftAddress,
        address _ownerVaultAddress,
        address _commissionVaultAddress,
        address _charityVaultAddress,
        uint16 _charityPercent
    ) Ownable() {
        _setMerchant(_merchant);
        _setOwnerVault(_ownerVaultAddress);
        _setCommissionVault(_commissionVaultAddress);
        _setCharityVault(_charityVaultAddress);
        _setCharityPercent(_charityPercent);
        nft = NFT_v3(_nftAddress);
    }

    /* Public functions */

    /**
     * @dev Buy land by a user
     * @param signature Signature
     * @param message Message to check: (to, tokenId, fullPrice, discountPercent, commissionPercent)
     */
    function buyLand(bytes memory message, bytes memory signature)
        whenNotPaused
        external
        virtual
        payable
    {
        (
            address to,
            uint256 tokenId,
            uint256 fullPrice,
            uint16 discountPercent,
            uint16 commissionPercent
        ) = _decodeMessage(message, signature);

        require(msg.sender == to, "BuyLand: Must call by token minter");

        _buyLand(to, tokenId, fullPrice, discountPercent, commissionPercent);
    }

    function nftAddress() public view returns (address) {
        return address(nft);
    }

    /* Private functions */

    function _decodeMessage(bytes memory message, bytes memory signature)
        internal
        view
        returns (
            address to,
            uint256 tokenId,
            uint256 fullPrice,
            uint16 discountPercent,
            uint16 commissionPercent
        )
    {
        (
            address _to,
            uint256 _tokenId,
            uint256 _fullPrice,
            uint16 _discountPercent,
            uint16 _commissionPercent
        ) = abi.decode(message, (address, uint256, uint256, uint16, uint16));

        address recovered = keccak256(
            abi.encodePacked(_to, _tokenId, _fullPrice, _discountPercent, _commissionPercent)
        ).toEthSignedMessageHash().recover(signature);

        require(recovered == merchant, "BuyLand: invalid merchant signature");
        return (_to, _tokenId, _fullPrice, _discountPercent, _commissionPercent);
    }

    function _buyLand(
        address to,
        uint256 tokenId,
        uint256 fullPrice,
        uint16 discountPercent,
        uint16 commissionPercent
        ) internal virtual
    {
        // here, a percent of 125 for eg means 12.5% as 125/1000 represents a
        // 0.125 multiplier
        require(
            discountPercent + commissionPercent + charityPercent <= 1000,
            "BuyLand: Allocation cannot be more than 100 percent"
            );
        uint256 discountPrice = fullPrice - ((fullPrice * discountPercent)/1000);
        require(msg.value >= discountPrice, "BuyLand: not enough funds");

        uint256 commissionShare = ((fullPrice * commissionPercent)/1000);
        uint256 charityShare = ((fullPrice * charityPercent)/1000);
        uint256 ownerShare = msg.value - commissionShare - charityShare;

        if(commissionShare > 0)
            IBuyLandVault(commissionVaultAddress).deposit{value: commissionShare}();
        if(charityShare > 0)
            IBuyLandVault(charityVaultAddress).deposit{value: charityShare}();
        if(ownerShare > 0)
            IBuyLandVault(ownerVaultAddress).deposit{value: ownerShare}();

        uint256[] memory tokens = new uint256[](1);
        tokens[0] = tokenId;
        nft.safeMintTo(to, tokens);

        emit LandPurchase(to, tokenId, fullPrice, discountPrice);
    }

    /* Utility functions */

    function pause() onlyOwner public {
        _pause();
    }

    function unpause() onlyOwner public {
        _unpause();
    }

    function setMerchant(address newMerchant) onlyOwner public {
        _setMerchant(newMerchant);
    }

    function _setMerchant(address newMerchant) internal virtual {
        require(address(0) != newMerchant, "BuyLand: Merchant can't be the zero address");
        address oldMerchant = merchant;
        merchant = newMerchant;
        emit MerchantChanged(oldMerchant, newMerchant);
    }

    function setOwnerVault(address newAddress) onlyOwner public {
        _setOwnerVault(newAddress);
    }

    function _setOwnerVault(address newAddress) internal virtual {
        require(_checkIBuyLandVault(newAddress), "BuyLand: Vault must implement IBuyLandVault");
        address oldVault = ownerVaultAddress;
        ownerVaultAddress = newAddress;
        emit OwnerVaultChanged(oldVault, newAddress);
    }

    function setCommissionVault(address newAddress) onlyOwner public {
        _setCommissionVault(newAddress);
    }

    function _setCommissionVault(address newAddress) internal virtual {
        require(_checkIBuyLandVault(newAddress), "BuyLand: Vault must implement IBuyLandVault");
        address oldVault = commissionVaultAddress;
        commissionVaultAddress = newAddress;
        emit CommissionVaultChanged(oldVault, newAddress);
    }

    function setCharityVault(address newAddress) onlyOwner public {
        _setCharityVault(newAddress);
    }

    function _setCharityVault(address newAddress) internal virtual {
        require(_checkIBuyLandVault(newAddress), "BuyLand: Vault must implement IBuyLandVault");
        address oldVault = charityVaultAddress;
        charityVaultAddress = newAddress;
        emit CharityVaultChanged(oldVault, newAddress);
    }

    function setCharityPercent(uint16 newPercent) onlyOwner public {
        _setCharityPercent(newPercent);
    }

    function _setCharityPercent(uint16 newPercent) internal virtual {
        require(newPercent <= 1000, "BuyLand: Invalid percent, must be lte 1000");
        uint16 oldPercent = charityPercent;
        charityPercent = newPercent;
        emit CharityPercentChanged(oldPercent, newPercent);
    }

    function _checkIBuyLandVault(address addr) internal pure returns (bool) {
        require(address(0) != addr, "BuyLand: Vault can't be the zero address");
        return true; // TODO: check really
    }

    // we send back accidentally sent ethers
    receive() external payable { revert(); }

}"
