pragma solidity ^0.6.0;

import './IERC20.sol';
import './Ownable.sol';
import './SafeMath.sol';
import './Address.sol';

abstract contract Token is Ownable, IERC20 {
    using SafeMath for uint256;
    using Address for address;

    string public override name;
    string public override symbol;
    uint256 public override totalSupply;
    uint8 public constant override decimals = 18;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _totalSupply
    ) public {
        name = _name;
        symbol = _symbol;
        totalSupply = _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) external override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) external view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(amount, 'ERC20: transfer amount exceeds allowance')
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), 'ERC20: transfer from the zero address');
        require(recipient != address(0), 'ERC20: transfer to the zero address');
        require(this.balanceOf(sender) >= amount, 'ERC20: transfer amount exceeds balance');

        _balances[sender] = _balances[sender].sub(amount, 'ERC20: transfer amount exceeds balance');
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), 'ERC20: approve from the zero address');
        require(spender != address(0), 'ERC20: approve to the zero address');

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), 'ERC20: mint to the zero address');

        totalSupply = totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), 'ERC20: burn from the zero address');
        require(this.balanceOf(account) >= amount, 'ERC20: burn amount exceeds balance');

        _balances[account] = _balances[account].sub(amount, 'ERC20: burn amount exceeds balance');
        totalSupply = totalSupply.sub(amount, 'ERC20: burn amount exceeds balance');
        emit Transfer(account, address(0), amount);
    }
}
