import '@openzeppelin/contracts/proxy/UpgradeableProxy.sol';
import '@openzeppelin/contracts/proxy/Initializable.sol';
import '@openzeppelin/contracts/utils/Address.sol';
import '../interfaces/IProxyImplementation.sol';
import '../interfaces/IProxyFactory.sol';

contract OwnableDelegateProxy is UpgradeableProxy, Initializable {
    using Address for address;

    constructor() UpgradeableProxy(address(msg.sender), new bytes(0)) { //first it is not avaliable, only after initialize it is avaliable
    }

    function initialize (IProxyImplementation _impl, address _user, address _factory) external initializer {
        _upgradeTo(address(_impl));
        address(_impl).functionDelegateCall(abi.encodeWithSignature("initialize(address,address)", _user, _factory));
    }

    function implementation() external view returns(address) {
        return _implementation();
    }

}
