// File: IVN.sol

 
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17; 










  

interface IUniswapV2Factory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IUniswapV2Pair {
    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

contract FateIVN is ERC721, ERC721Enumerable, Pausable, Ownable, ERC721Burnable {
    bool private busy;
    modifier inProgress() {
        require(!busy, "Reentrant call"); 
        busy = true;
        _;    
        busy = false;        
    } 

    address public BURN; 

    bool public token=false;
    address public TOKEN; 
    bool public pair=false;
    address public PAIR;  

    IUniswapV2Router02 private uniswapRouter;
    address public ROUTER_ADDRESS;  

    using Counters for Counters.Counter;
    Counters.Counter private _tid;

    bool public treasuryOnly;

    bool public autoDistribute;
    bool public autoBuy;
    bool public autoReturn;  
    bool public autoBounty;   

    uint256 public fee;  
    uint256 public min;
    uint256 public odds;
    uint256 public bountyDiv; 
    uint256 public bountyShare;      
    uint256 public tokenAmount; 
    uint256 public buyLevel;    
    uint256 public totalRedeemed;

    struct IVN {
        uint256 id; 
        address addr;
        string code;
        uint256 age;
        uint256 entry;
        uint256 floor;
        uint256 value;
        uint256 level;
        uint256 actions;
        uint256 rewards;
        uint256 luck;
        uint256 bounties;
        uint256 tokens;
        uint256 timestamp; 
    }
    struct Balance {
        uint256 liquidity;
        uint256 bounty;
        uint256 treasury;
    }
    struct Holder {
        address addr;
        uint256 actions;
        uint256 max;
    }

    IVN[] public ivn;
    Holder[] public holder;
    Balance public balance;

    mapping(uint256 => address) private _owner;
    mapping(address => uint256) private _actions;
    mapping(uint256 => string) private _code;
    mapping(uint256 => bool) private _redeemed;
    mapping(address => uint256) private _redeems;
    mapping(uint256 => uint256) private _perc;
    mapping(uint256 => address) private _bounties;

    mapping(address => uint256) public max;


    string public URI; 
    uint256 public tokenLevel;
    bool public ready = false;

    constructor() ERC721("Fate Shield IVN", "FATE-IVN") payable {
        BURN = 0x000000000000000000000000000000000000dEaD;
        ROUTER_ADDRESS = 0xBb5e1777A331ED93E07cF043363e48d320eb96c4 ;

        uniswapRouter = IUniswapV2Router02(ROUTER_ADDRESS);
        URI ="https://fs.fatelabz.com/"; 
        tokenLevel = num(100_000);
        token = false;
        autoBounty=false;
        autoDistribute = false;
        autoBuy = false;
        autoReturn = false;   
        fee = 0.01 ether; 
        min = 1000; 
        balance.bounty= msg.value/2;
        balance.treasury= msg.value/2;
        odds = 369;
        treasuryOnly=false;
        bountyShare=10;
        bountyDiv=totalSupply()+bountyShare;
        tokenAmount=0;
        buyLevel=100;
    }

    function mintIVN(string memory code) public payable inProgress {
        require(msg.value >= fee, "Insufficient Funds");
        if(max[msg.sender]==0){ max[msg.sender]=1;}
        if(msg.sender != owner()){
           // require(balanceOf(msg.sender) < max[msg.sender], "Max");
        }

            uint256 tid = _tid.current();
            _owner[tid] = msg.sender;
            _code[tid]  = code;

            uint256 value = msg.value; 
            uint256 div = value/10;
            uint256 entry = div * 6;
            uint256 tokens = 0; 
            if(token){tokens = tokenBalance(msg.sender);}

            balance.liquidity += entry;
            if(totalSupply()>3){
                balance.bounty += div;
                balance.treasury += div;
                divvy(div * 2); 
            }else{
                balance.bounty += div*2;
                balance.treasury += div*2;
            }
            ivn.push(IVN(tid, msg.sender, code, 0, msg.value, entry, entry,6,0, 0,0,0,tokens,block.timestamp));

        _tid.increment();
        _safeMint(msg.sender, tid);
    }

    function airdropIVN(string memory code, address to, uint256 level) public payable onlyOwner {
        if(!ready){
            require(msg.value >= fee, "Insufficient Funds");
            uint256 tid = _tid.current(); 
            _actions[to] = 20;
            max[to]=12; 

            _owner[tid] = to;
            _code[tid]  = code;

            uint256 tokens = 0;
            if(token){tokens = tokenBalance(to);}

            uint256 value = msg.value; 
            uint256 entry = value/5;

            balance.liquidity += value;

            ivn.push(IVN(tid, to, code, 0, entry, entry, msg.value, level, 20, 0,0,0,tokens,block.timestamp)); 

            _tid.increment();
            _safeMint(to, tid);
        }
    }

    function redeem(uint256 tid) external inProgress {
        require(_owner[tid]==msg.sender, "Must be the owner"); 
        require(!_redeemed[tid], "Token already redeemed");

        uint256 value  = ivn[tid].value;
        uint256 div = value/10;
        uint256 amount = div * 9;

        balance.liquidity -= value; 
        balance.bounty += div/2;
        balance.treasury += div/2;
        divvy(value/10); 
 
        _actions[msg.sender] += 1;
        _owner[tid] = address(0);
        _redeemed[tid] = true;

        delete ivn[tid];
        super._burn(tid);

        payable(msg.sender).transfer(amount); 
        totalRedeemed+=1;
        if(totalSupply()==0){
            balance.bounty = 0;
            balance.liquidity = 0;
            balance.treasury = address(this).balance;
        }
    }
    function redeemProfit(uint256 tid)external inProgress{
        require(_owner[tid]==msg.sender, "Must be the owner"); 
        require(!_redeemed[tid], "Token already redeemed");
        require(ivn[tid].level<=2, "Must be Level 4 (7-level)");

        uint256 amount = ivn[tid].value-ivn[tid].floor;
        uint256 div = amount/5;

        ivn[tid].value -= amount;
        ivn[tid].level = 6;
        ivn[tid].luck = 0;
        ivn[tid].actions = 0;
        ivn[tid].bounties = 0;
        
        balance.liquidity -= amount;
        balance.bounty += div/2;
        balance.treasury += div/2;

        divvy(div); 
        _actions[msg.sender] += 1;
        payable(msg.sender).transfer(div*4); 
    }
    function transferIVN(uint256 tid, address to) internal {
        if(max[to]==0){ max[to]=1;}
        require(balanceOf(to) < max[to], "Max");
        uint256 amount = ivn[tid].value/10; 

        ivn[tid].value -= amount;
        ivn[tid].addr=to;
        _owner[tid] = to; 

        balance.liquidity -= amount;
        balance.bounty += amount/3;
        balance.treasury += amount/3;

        divvy(amount/3);
    }
    function action(uint256 tid) public payable inProgress{
        require(_owner[tid]==msg.sender, "Must be the owner");
        require(!_redeemed[tid], "Token already redeemed");
        _actions[msg.sender] += 1;
        ivn[tid].actions += 1;

        uint256 div = msg.value/10;

        if(_actions[msg.sender]==5){max[msg.sender]=3;}
        if(_actions[msg.sender]==10){max[msg.sender]=6;}
        if(_actions[msg.sender]==15){max[msg.sender]=9;}
        if(_actions[msg.sender]==20){max[msg.sender]=12;}

        if(ivn[tid].actions==5){ivn[tid].level=5;}
        if(ivn[tid].actions==10){ivn[tid].level=4;}
        if(ivn[tid].actions==15){ivn[tid].level=3;}
        if(ivn[tid].actions==20){ivn[tid].level=2;}
        if(ivn[tid].actions==25){ivn[tid].level=1;}

        balance.bounty += div*2;
        balance.treasury += div*2;

        divvy(div*6);

        ivn[tid].luck+=1;
        if(ivn[tid].luck>90){ivn[tid].luck=90;}
        if(autoBounty){
            if(roll(tid)){bounty(tid);} 
        }
    }
    function target(uint256 tid) public payable inProgress{
        uint256 ivnBalance = balanceOf(msg.sender);
        require(!_redeemed[tid], "Token already redeemed");
        require(ivnBalance > 0, "Holder does not own an IVN");
        uint256 fid = tokenOfOwnerByIndex(msg.sender, ivnBalance - 1);
        require(fid!=tid, "Can not be the owner of target.");
        _actions[msg.sender] += 1;
        ivn[tid].actions += 1;

        uint256 div = msg.value/10;

        if(_actions[msg.sender]==5){max[msg.sender]=3;}
        if(_actions[msg.sender]==10){max[msg.sender]=6;}
        if(_actions[msg.sender]==15){max[msg.sender]=9;}
        if(_actions[msg.sender]==20){max[msg.sender]=12;}

        if(ivn[tid].actions==5){ivn[tid].level=5;}
        if(ivn[tid].actions==10){ivn[tid].level=4;}
        if(ivn[tid].actions==15){ivn[tid].level=3;}
        if(ivn[tid].actions==20){ivn[tid].level=2;}
        if(ivn[tid].actions==25){ivn[tid].level=1;}

        balance.bounty += div*2;
        balance.treasury += div*2;

        ivn[tid].value += div*2;
        balance.liquidity += div*2;

        //divvy(div*4);

        ivn[tid].luck+=1;
        if(ivn[tid].luck>90){ivn[tid].luck=90;}
        if(autoBounty){
            if(roll(tid)){bountySplit(tid,fid);} 
        }
    }

    function divvy(uint256 value) internal {
        if (autoDistribute && totalSupply() > 1 && ivn.length > 0) {
            uint256 div = value / 4;
            uint256 pool = div * 3;
            uint256 buyback = div; 
            tokenAmount+=buyback; 

            balance.liquidity += pool;

            uint256 amount = pool / totalSupply();
            uint256 nonRedeemedCount = ivn.length - totalRedeemed;
            uint256 totalValue = pool;

            if (nonRedeemedCount > 0) {
                amount = pool / nonRedeemedCount;
                for (uint256 i = 0; i < ivn.length; i++) {
                    if (!_redeemed[i]) {
                        uint256 diff = block.timestamp - ivn[i].timestamp;
                        uint256 age = diff / 86400;
                        uint256 cap = amount > ivn[i].floor ? ivn[i].floor : amount;
                        uint256 rate = cap / ivn[i].level;
                        
                        if(token){
                            uint256 tokens = tokenBalance(ivn[i].addr);
                            ivn[i].tokens=tokens;
                            if(tokens<min){ rate = rate/2;}
                            if(tokens>=min){
                                ivn[i].rewards += rate/2;
                            }
                        }
                        ivn[i].age = age;
                        ivn[i].value += rate;  
                        totalValue -= rate;
                    }
                }
            }
      
            balance.bounty += totalValue/2;
            balance.treasury += totalValue/2;
        
            if(token && autoBuy && tokenAmount>=buyLevel){
                buy(TOKEN,tokenAmount);
                tokenAmount=0;
                if(autoReturn){
                    returnTokens();
                }
            }
        }
    }
    function deposit() public payable inProgress{
         _actions[msg.sender] += 1;
        divvy(msg.value);
    }
    function distribute() public payable {
        divvy(msg.value);  
    }
    function getTokenPrice(address _token, address _pair) public view returns (uint256) {
        address pairAddress = IUniswapV2Factory(uniswapRouter.factory()).getPair(_token, _pair);
        require(pairAddress != address(0), "Pair does not exist");
        (uint112 tokenReserve, uint112 ethReserve, ) = IUniswapV2Pair(pairAddress).getReserves();
        return uint256(ethReserve) * 1e18 / uint256(tokenReserve);
    }
    function buyTokens(address addr, uint256 amount)  public onlyOwner {
        buy(addr,amount);
    }
    function buy(address addr, uint256 amount)  internal {
        uint deadline = block.timestamp + 300; 
        uint ethAmount = amount;
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = addr;
        uint estimatedTokenAmount = estimateTokensOut(path[0], path[1], ethAmount);
        uint minTokenAmount = estimatedTokenAmount * (100 - 20) / 100;
        uint256 gasLimit = estimatedTokenAmount * 5000;
        if (gasLimit < 8000000) {
            gasLimit = 8000000;
        }
        uniswapRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{value: ethAmount, gas: gasLimit}(
            minTokenAmount,
            path,
            address(this),
            deadline
        ); 
    }
    function sellTokens(address addr, uint256 amount)  public onlyOwner {
        sell(addr,amount);
    }
    function sell(address addr,uint256 amount) internal {
        address[] memory path = new address[](2); 
        path[0] = addr;
        path[1] = uniswapRouter.WETH(); 
        uint256 minEthAmount = 0; 
        IERC20(addr).approve(address(uniswapRouter), amount); 
        uniswapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount,
            minEthAmount,
            path,
            address(this),
            block.timestamp + 300
        );
    }
    function roll(uint256 tid)internal view returns(bool){
        uint256 _odds = odds-ivn[tid].luck;
        uint256 n = generateRandomNumber(_odds>9 ? 10 : _odds);
        return n==3?true:false;
    } 
    function manualBounty(uint256 tid) external onlyOwner{
        bounty(tid);
    }
    function bounty(uint256 tid) internal {
        uint256 amount = balance.bounty/bountyDiv; 
        if(ivn[tid].tokens<min){ amount = amount/2;}
        if(ivn[tid].value<amount){amount=ivn[tid].value;}

        balance.bounty-=amount;
        balance.liquidity+=amount;
        ivn[tid].value+=amount;
        //Create a record to prove payment
        ivn[tid].bounties+=1;
        ivn[tid].luck=0;
    }
    function bountySplit(uint256 to,uint256 from) internal {
        uint256 amount = (balance.bounty/bountyDiv)/2; 
        uint256 amount_to = amount;
        uint256 amount_from = amount;

        if(ivn[to].tokens<min){ amount_to = amount/2;}
        if(ivn[to].value<amount){amount_to=ivn[to].value;}
        
        if(ivn[from].tokens<min){ amount_from = amount/2;}
        if(ivn[from].value<amount){amount_from=ivn[to].value;}

        balance.bounty-=amount_to+amount_from;
        balance.liquidity+=amount_to+amount_from;
        ivn[to].value+=amount_to;
        ivn[from].value+=amount_from;
        //Create a record to prove payment
        ivn[to].bounties+=1; 
        ivn[from].bounties+=1;
        ivn[to].luck=0;
    }
    function manualReturn() public onlyOwner {
        returnTokens();
    }
    function returnTokens() internal {
        IERC20 _token = IERC20(TOKEN);
        uint256 bal = _token.balanceOf(address(this));
        if(bal>= tokenLevel){
            _token.transfer(TOKEN, bal);
        }
    }
    function estimateTokensOut(address tokenIn, address tokenOut, uint amountIn) public view returns (uint) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        uint[] memory amountsOut = uniswapRouter.getAmountsOut(amountIn, path);
        return amountsOut[1]; 
    } 
    function tokenBalance(address addr) public view returns (uint256) {
        IERC20 fez23 = IERC20(TOKEN);
        return fez23.balanceOf(addr); 
    }
    function recover(uint amount) public onlyOwner {
        require(amount <= address(this).balance, "Insufficient balance in contract");
        if(treasuryOnly){
            require(amount <= balance.treasury, "Treasury access only");
        }
        balance.liquidity=0;
        balance.bounty=0;
        balance.treasury=0;
        payable(msg.sender).transfer(amount);
    }
    function generateRandomNumber(uint256 upperLimit) public view returns (uint256) {
        uint256 randomNumber = uint256(keccak256(abi.encodePacked(blockhash(block.number - 1), msg.sender, block.timestamp)));
        return (randomNumber % upperLimit) + 1;
    }
    function num(uint256 _int) internal pure returns (uint256 _num){
       return _int * (10 ** 18);
    }
    function getIVNs(address owner) public view returns (uint256[] memory) {
        uint256[] memory ownedIds;
        uint256 counter = 0;
        for (uint256 i = 0; i < ivn.length; i++) { 
            if (ivn[i].addr == owner) { 
                counter++;
            }
        }
        ownedIds = new uint256[](counter);
        counter = 0;
        for (uint256 i = 0; i < ivn.length; i++) {
            if (ivn[i].addr == owner) {
                ownedIds[counter] = ivn[i].id;
                counter++;
            }
        }
        return ownedIds;
    }
    function _baseURI() internal view override returns (string memory) {
        return URI; 
    }
    function setURI(string memory _uri) external onlyOwner {
        URI = _uri;
    }
    function setToken(address addr) external onlyOwner {
        token=true;
        TOKEN = addr;
    }
    function setPair(address addr) external onlyOwner {
        pair=true;
        PAIR = addr;
    }
    function setMin(uint256 _min) external onlyOwner {
        min=_min;
    }
    function setAutoBuy(bool state) external onlyOwner {
        autoBuy=state;
    }
    function setAutoBounty(bool state) external onlyOwner {
        autoBounty=state;
    }
    function setAutoReturn(bool state) external onlyOwner {
        autoReturn=state;
    }
    function setAutoDistribute(bool state) external onlyOwner {
        autoDistribute=state;
    }
    function setAutoStates(bool state) external onlyOwner {
        autoBuy=state;
        autoBounty=state;
        autoReturn=state;
        autoDistribute=state;
    }
    function setTreasury() external onlyOwner {
        treasuryOnly=true;
    }
    function setReady() external onlyOwner {
        ready=true;
    }
    function setOdds(uint256 _odds) external onlyOwner {
        odds=_odds;
    }
    function setBuyLevel(uint256 level) external onlyOwner {
        buyLevel=level;
    }
    function adjustBounty(uint256 split) external onlyOwner {
        bountyDiv=totalSupply()+split;
    }
    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory _data) public virtual override(ERC721, IERC721) {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        transferIVN(tokenId,to);
        _safeTransfer(from, to, tokenId, _data);
    }
    function safeTransferFrom(address from, address to, uint256 tokenId) public virtual override(ERC721, IERC721) {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        transferIVN(tokenId,to);
        safeTransferFrom(from, to, tokenId, "");
    }
    function transferWithGas(address from, address to, uint256 tokenId) public payable{
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        if(msg.value>0){
             payable(to).transfer(msg.value/2);
        }
        transferFrom(from, to,tokenId);
    }
    function transferFrom(address from, address to, uint256 tokenId) public virtual override(ERC721, IERC721) {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        transferIVN(tokenId,to);
        _transfer(from, to, tokenId);
    }
    function _beforeTokenTransfer(address from, address to, uint256 tokenId, uint256 batchSize)internal whenNotPaused override(ERC721, ERC721Enumerable){
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }
    function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC721, ERC721Enumerable)
    returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
    function getContractBalance() public view returns(uint256) {
        return address(this).balance;
    }
    receive() external payable {} 
}
