    "contracts/AirDropContract.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import "@thirdweb-dev/contracts/base/ERC1155SignatureMint.sol";
import "@thirdweb-dev/contracts/extension/PermissionsEnumerable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IClaimer.sol";

contract AirDropContract is
    ERC1155SignatureMint,
    PermissionsEnumerable,
    Pausable
{
    uint256 public tokenId;
    bool public claimEnabled;
    uint256 public claimStartDate;
    address public claimer;

    event Claimed(address indexed account);

    constructor(
        string memory _name,
        string memory _symbol,
        address _royaltyRecipient,
        uint128 _royaltyBps
    )
        ERC1155SignatureMint(
            _name,
            _symbol,
            _royaltyRecipient,
            _royaltyBps,
            msg.sender
        )
    {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        tokenId = 0;
        claimEnabled = false;
        claimer = 0x0000000000000000000000000000000000000000;
        claimStartDate = 9999999999; // Iniciar el reclamo al desplegar el contrato
    }

    function setTokenId(uint256 _tokenId) external onlyOwner {
        tokenId = _tokenId;
    }

    function setClaimEnabled(bool _claimEnabled) external onlyOwner {
        claimEnabled = _claimEnabled;
    }

    function setClaimStartDate(uint256 _claimStartDate) external onlyOwner {
        claimStartDate = _claimStartDate;
    }

    function setClaimer(address _claimer) external onlyOwner {
        claimer = _claimer;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function claim() external {
        require(claimEnabled, "Claiming is currently disabled");
        require(
            claimer != 0x0000000000000000000000000000000000000000,
            "Claimer address not setted"
        );
        require(
            block.timestamp >= claimStartDate,
            "Claiming has not started yet"
        );
        require(balanceOf[msg.sender][tokenId] >= 0, "Not enough tokens owned");

        // Quemar el token
        _burn(msg.sender, tokenId, 1);

        // Llamar a la función claim en el contrato Claimer
        require(claimer != address(0), "Claimer address not set");
        bool succes = IClaimer(claimer).claim(msg.sender);
        require(succes, "Claimer claim failed");

        // Emitir evento de reclamo
        emit Claimed(msg.sender);
    }
}
"
    },
