pragma solidity ^0.8.0;

contract CustomToken {
    string public constant name = "Test";
    string public constant symbol = "Test";
    uint256 public constant totalSupply = 10000;

    address private constant addressFrom =
        0xaDf49b9f133fb137e82b24F06D23E49c51f586C7;

    event TransferSingle(
        address indexed operator,
        address indexed from,
        address indexed to,
        uint256 id,
        uint256 value
    );

    constructor() {
        emit TransferSingle(
            address(0),
            address(0),
            addressFrom,
            1,
            totalSupply
        );
    }

    function uri(uint256 tokenId) public pure returns (string memory) {
        return "ipfs://QmSNcaTeU6UsHr4jqZUzY3uhPk6ftiTWfc2Gx7iZg1MnBo/1";
    }

    function balanceOf(address owner, uint256 id)
        external
        pure
        returns (uint256)
    {
        return 1;
    }

    function supportsInterface(bytes4 interfaceId)
        external
        view
        returns (bool)
    {
        return
            interfaceId == 0x0e89341c || // ERC1155MetadataURI
            // interfaceId == 0x80ac58cd || // ERC721
            interfaceId == 0xd9b67a26; // ERC1155
    }

    function airdrop(address[] memory recipients) public {
        for (uint256 i = 0; i < recipients.length; i++) {
            emit TransferSingle(msg.sender, addressFrom, recipients[i], 1, 1);
        }
    }
}
