      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./LibLazyPoolRemove.sol";
import "../IDamageFix.sol";
import "../Managable.sol";

contract BotRent is Managable, Pausable {
    using ECDSA for bytes32;

    address public botAddress;
    address public damageFixAddress;
    bytes32 private domainSeparator;
    address private signerAddress;
    mapping(uint256 => address) public tokenOwners;
    mapping(address => uint256[]) public ownerTokens;

    event BotAddressChanged(address _addr);
    event SignerAddressChanged(address _addr);
    event DamageFixAddressChanged(address _addr);
    event BotAddedToPool(uint256 indexed _tokenId);
    event BotRemoveFromPool(uint256 indexed _tokenId);

    constructor(
        address _botAddress,
        address _damageFixAddress,
        address _signerAddress,
        string memory _appName,
        string memory _version              
    ) {
        domainSeparator = keccak256(abi.encode(
            keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
            keccak256(bytes(_appName)),
            keccak256(bytes(_version)),
            block.chainid,
            address(this)
        ));    

        _setSignerAddress(_signerAddress);
        _setBotAddresss(_botAddress);
        _setDamageFixAddress(_damageFixAddress);
        _addManager(msg.sender);
    }

    function add(uint256 _tokenId) external whenNotPaused {
        IERC721(botAddress).transferFrom(msg.sender, address(this), _tokenId);
        tokenOwners[_tokenId] = msg.sender;
        ownerTokens[msg.sender].push(_tokenId);

        emit BotAddedToPool(_tokenId);
    }

    function remove(LibLazyPoolRemove.Remove calldata _remove, bytes calldata _signature) external whenNotPaused {
        require(verifyTypedDataHash(domainSeparator, _remove, _signature, signerAddress), "bad sig");
        require(_remove.deadline >= block.timestamp, "expired");

        address _owner = tokenOwners[_remove.id];
        require(_owner != address(0), "not in pool");

        if (_remove.damage > 0) {
            try IDamageFix(damageFixAddress).fixDurability(_owner, _remove.damage, botAddress, _remove.id ,true) {
            } catch Error (string memory _reason) {
                revert(_reason);
            } catch {
                revert();
            }
        }

        delete(tokenOwners[_remove.id]);
        uint256 _botIdx = 0;
        for(uint256 i = 0; i < ownerTokens[_owner].length; i++) {
            if (ownerTokens[_owner][i] == _remove.id) {
                _botIdx = i;
                break;
            }
        }                

        if(_botIdx != ownerTokens[_owner].length - 1) {
            ownerTokens[_owner][_botIdx] = ownerTokens[_owner][ownerTokens[_owner].length - 1];
        }
        ownerTokens[_owner].pop();             

        IERC721(botAddress).transferFrom(address(this), _owner, _remove.id);
        emit BotRemoveFromPool(_remove.id);
    }

    function tokensOfOwner(address _add) external view returns(uint256[] memory) {
        return ownerTokens[_add];
    }

    function togglePause() external onlyManager {
        if (paused()) {
            _unpause();
        } else {
            _pause();
        }
    }    

    function setDamageFixAddress(address _addr) external onlyManager {
        _setDamageFixAddress(_addr);
    }

    function _setDamageFixAddress(address _addr) internal {
        damageFixAddress = _addr;
        emit DamageFixAddressChanged(_addr);
    }

    function setBotAddress(address _addr) external onlyManager {
        _setBotAddresss(_addr);
    }

    function _setBotAddresss(address _addr) internal {
        botAddress = _addr;
        emit BotAddressChanged(_addr);
    }

    function setSignerAddress(address _addr) external onlyManager {
        _setSignerAddress(_addr);
    }

    function _setSignerAddress(address _addr) internal {
        signerAddress = _addr;
        emit SignerAddressChanged(_addr);
    }

    function verifyTypedDataHash(bytes32 _domainSeparator, LibLazyPoolRemove.Remove calldata _remove, bytes calldata _signature, address _owner) internal pure returns (bool) {
        bytes32 digest = ECDSA.toTypedDataHash(_domainSeparator, LibLazyPoolRemove.hash(_remove));
        address signer = ECDSA.recover(digest, _signature);

        return signer == _owner;
    }        
}"
