    "contracts2/MCHLandPoolPolygon.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./LandSectorHelperPolygon.sol";

contract MCHLandPoolPolygon is Ownable, Pausable, ReentrancyGuard, LandSectorHelperPolygon {
    address public validater;
    address public currency;

    mapping(uint16 => uint256) private landTypeToTotalAmount;
    mapping(uint256 => uint256) private landSectorToWithdrawnAmount;
    mapping(address => bool) private allowedAddresses;

    event EthAddedToPool(
        uint16 indexed landType,
        address txSender,
        address indexed purchaseBy,
        uint256 value,
        uint256 at
    );

    event WithdrawEther(
        uint256 indexed landSector,
        address indexed lord,
        uint256 value,
        uint256 at
    );

    event AllowedAddressSet(
        address allowedAddress,
        bool allowedStatus
    );

    constructor(
        address _validater,
        address _currency
    ) {
        setValidater(_validater);
        setCurrency(_currency);
    }

    function setValidater(address _validater) public onlyOwner() {
        validater = _validater;
    }

    function setCurrency(address _currency) public onlyOwner() {
        currency = _currency;
    }

    // Sets address of GumGateway contract that is allowed to use addToLandPool
    function setAllowedAddress(address _address, bool desired) external onlyOwner() {
        allowedAddresses[_address] = desired;
        emit AllowedAddressSet(
            _address,
            desired
        );
    }

    function addToLandPool(uint256 addAmount, uint16 _landType, address _purchaseBy) external payable whenNotPaused() nonReentrant() {
        require(_landType >= 1 && _landType <= 9, "Invalid _landType");
        require(allowedAddresses[msg.sender], "Address is not allowed");
        landTypeToTotalAmount[_landType] += addAmount;

        // Transfer Erc20 from purchaser to this contract
        require(
            IERC20(currency).allowance(msg.sender, address(this)) >= addAmount,
            "must be approved"
        );
        IERC20(currency).transferFrom(msg.sender, address(this), addAmount);

        emit EthAddedToPool(
            _landType,
            msg.sender,
            _purchaseBy,
            addAmount,
            block.timestamp
        );
    }

    function withdrawMyAllRewards(
        uint256[] memory _landSectors,
        bytes[] calldata _signatures
    ) external whenNotPaused() nonReentrant() {
        require(_landSectors.length > 0, "No landsectors passed");
        require(_landSectors.length == _signatures.length, "LandSector and Signatures lenght doesn't match");
        require(getWithdrawableBalance(_landSectors) > 0, "Nothing to withdraw"); // Break out early to save gas if not withdrawable

        uint256 withdrawValue;

        for (uint256 i = 0; i < _landSectors.length; i++) {
            require(validateSig(encodeData(_landSectors[i], msg.sender, block.chainid), _signatures[i]), "Invalid signature");
            uint256 landSector = _landSectors[i];

            uint256 tmpAmount = getLandSectorWithdrawableBalance(landSector);
            withdrawValue += tmpAmount;
            landSectorToWithdrawnAmount[landSector] += tmpAmount;

            emit WithdrawEther(
                landSector,
                msg.sender,
                tmpAmount,
                block.timestamp
            );
        }
        IERC20(currency).transfer(msg.sender, withdrawValue);
    }

    function withdrawMyReward(
        uint256 _landSector,
        bytes calldata _signature
    ) external whenNotPaused() nonReentrant() {
        require(validateSig(encodeData(_landSector, msg.sender, block.chainid), _signature), "Invalid signature");
        uint256 withdrawableAmount = getLandSectorWithdrawableBalance(_landSector);
        require(withdrawableAmount > 0, "Nothing to withdraw");

        landSectorToWithdrawnAmount[_landSector] += withdrawableAmount;
        IERC20(currency).transfer(msg.sender, withdrawableAmount);

        emit WithdrawEther(
            _landSector,
            msg.sender,
            withdrawableAmount,
            block.timestamp
        );
    }

    function getAllowedAddress(address _address) public view returns (bool) {
        return allowedAddresses[_address];
    }

    function getTotalEthBackAmountPerLandType(uint16 _landType) public view returns (uint256) {
        return landTypeToTotalAmount[_landType];
    }

    function getLandSectorWithdrawnAmount(uint256 _landSector) public view returns (uint256) {
        return landSectorToWithdrawnAmount[_landSector];
    }

    function getLandSectorWithdrawableBalance(uint256 _landSector) public view returns (uint256) {
        require(isValidLandSector(_landSector), "Land Sector invalid");
        uint16 _landType = getSectorLandType(_landSector);

        (uint256 shareRate, uint256 decimal) = getShareRateWithDecimal(_landSector);

        uint256 maxAmount = landTypeToTotalAmount[_landType] * shareRate / decimal;
        return maxAmount - landSectorToWithdrawnAmount[_landSector];
    }

    function getWithdrawableBalance(
        uint256[] memory _landSectors
    ) public view returns (uint256) {
        uint256 balance = 0;

        for (uint256 i = 0; i < _landSectors.length; i++) {
            balance += getLandSectorWithdrawableBalance(_landSectors[i]);
        }

        return balance;
    }

    function encodeData(uint256 _landSector, address _landSectorOwner, uint256 _chainId) public pure returns (bytes32) {
        return keccak256(
            abi.encode(
                _landSector,
                _landSectorOwner,
                _chainId
            )
        );
    }

    function validateSig(bytes32 _message, bytes memory _signature) public view returns (bool) {
        require(validater != address(0), "validater must be set");
        address signer = ECDSA.recover(ECDSA.toEthSignedMessageHash(_message), _signature);
        return (signer == validater);
    }

    function recover(bytes32 _message, bytes memory _signature) public pure returns (address) {
        address signer = ECDSA.recover(ECDSA.toEthSignedMessageHash(_message), _signature);
        return signer;
    }
}
"
