    "contracts2/LandSectorHelperPolygon.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import "hardhat/console.sol";

contract LandSectorHelperPolygon {
    uint256 public constant SHARE_RATE_DECIMAL = 10**18;

  uint16 public constant LEGENDARY_RARITY = 5;
  uint16 public constant EPIC_RARITY = 4;
  uint16 public constant RARE_RARITY = 3;
  uint16 public constant UNCOMMON_RARITY = 2;
  uint16 public constant COMMON_RARITY = 1;

  uint16 public constant NO_LAND = 0;

  mapping(uint16 => uint256) private rarityToSectorVolume;
  mapping(uint16 => mapping(uint16 => uint256)) private landTypeAndRarityToSectorSupply;
  mapping(uint16 => uint256) private landTypeToTotalVolume;

  constructor() {
    rarityToSectorVolume[5] = 100;
    rarityToSectorVolume[4] = 20;
    rarityToSectorVolume[3] = 5;
    rarityToSectorVolume[2] = 2;
    rarityToSectorVolume[1] = 1;

    // Take values from ethereum contract and hardcode
    // https://etherscan.io/address/0x617913dd43dbdf4236b85ec7bdf9adfd7e35b340#readContract
    landTypeToTotalVolume[NO_LAND] = 0;
    landTypeToTotalVolume[1] = 1000;
    landTypeToTotalVolume[2] = 1000;
    landTypeToTotalVolume[3] = 1000;
    landTypeToTotalVolume[4] = 1000;
    landTypeToTotalVolume[5] = 829;
    landTypeToTotalVolume[6] = 859;
    landTypeToTotalVolume[7] = 805;
    landTypeToTotalVolume[8] = 773;
    landTypeToTotalVolume[9] = 742;

    // Take values from ethereum contract and hardcode
    // https://etherscan.io/address/0x617913dd43dbdf4236b85ec7bdf9adfd7e35b340#readContract
    landTypeAndRarityToSectorSupply[1][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[1][EPIC_RARITY] = 5;
    landTypeAndRarityToSectorSupply[1][RARE_RARITY] = 20;
    landTypeAndRarityToSectorSupply[1][UNCOMMON_RARITY] = 200;
    landTypeAndRarityToSectorSupply[1][COMMON_RARITY] = 300;

    landTypeAndRarityToSectorSupply[2][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[2][EPIC_RARITY] = 15;
    landTypeAndRarityToSectorSupply[2][RARE_RARITY] = 40;
    landTypeAndRarityToSectorSupply[2][UNCOMMON_RARITY] = 100;
    landTypeAndRarityToSectorSupply[2][COMMON_RARITY] = 200;

    landTypeAndRarityToSectorSupply[3][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[3][EPIC_RARITY] = 15;
    landTypeAndRarityToSectorSupply[3][RARE_RARITY] = 40;
    landTypeAndRarityToSectorSupply[3][UNCOMMON_RARITY] = 100;
    landTypeAndRarityToSectorSupply[3][COMMON_RARITY] = 200;

    landTypeAndRarityToSectorSupply[4][LEGENDARY_RARITY] = 2;
    landTypeAndRarityToSectorSupply[4][EPIC_RARITY] = 20;
    landTypeAndRarityToSectorSupply[4][RARE_RARITY] = 80;
    landTypeAndRarityToSectorSupply[4][UNCOMMON_RARITY] = 0;
    landTypeAndRarityToSectorSupply[4][COMMON_RARITY] = 0;

    landTypeAndRarityToSectorSupply[5][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[5][EPIC_RARITY] = 13;
    landTypeAndRarityToSectorSupply[5][RARE_RARITY] = 33;
    landTypeAndRarityToSectorSupply[5][UNCOMMON_RARITY] = 80;
    landTypeAndRarityToSectorSupply[5][COMMON_RARITY] = 144;

    landTypeAndRarityToSectorSupply[6][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[6][EPIC_RARITY] = 13;
    landTypeAndRarityToSectorSupply[6][RARE_RARITY] = 34;
    landTypeAndRarityToSectorSupply[6][UNCOMMON_RARITY] = 85;
    landTypeAndRarityToSectorSupply[6][COMMON_RARITY] = 159;

    landTypeAndRarityToSectorSupply[7][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[7][EPIC_RARITY] = 13;
    landTypeAndRarityToSectorSupply[7][RARE_RARITY] = 31;
    landTypeAndRarityToSectorSupply[7][UNCOMMON_RARITY] = 76;
    landTypeAndRarityToSectorSupply[7][COMMON_RARITY] = 138;

    landTypeAndRarityToSectorSupply[8][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[8][EPIC_RARITY] = 12;
    landTypeAndRarityToSectorSupply[8][RARE_RARITY] = 30;
    landTypeAndRarityToSectorSupply[8][UNCOMMON_RARITY] = 75;
    landTypeAndRarityToSectorSupply[8][COMMON_RARITY] = 133;

    landTypeAndRarityToSectorSupply[9][LEGENDARY_RARITY] = 1;
    landTypeAndRarityToSectorSupply[9][EPIC_RARITY] = 11;
    landTypeAndRarityToSectorSupply[9][RARE_RARITY] = 29;
    landTypeAndRarityToSectorSupply[9][UNCOMMON_RARITY] = 73;
    landTypeAndRarityToSectorSupply[9][COMMON_RARITY] = 131;
  }

  function getSectorLandType(uint256 _landSector) public pure returns (uint16) {
    uint16 _landType = uint16(_landSector / 10000 % 1000);
    return _landType;
  }

  function getSectorRarity(uint256 _landSector) public pure returns (uint16) {
    return uint16(_landSector / (10**7));
  }

  function isValidLandSector(uint256 _tokenId) public view returns (bool) {
    uint16 rarity = getSectorRarity(_tokenId);
    if (!(rarityToSectorVolume[rarity] > 0)) {
      return false;
    }
    uint16 landType = getSectorLandType(_tokenId);
    if (!(landType >= 1 && landType <= 9)) {
      return false;
    }
    uint256 serial = _tokenId % 10000;
    if (serial == 0) {
      return false;
    }
    if (serial > landTypeAndRarityToSectorSupply[landType][rarity]) {
      return false;
    }
    return true;
  }

  function getSectorVolumeByRarity(uint16 _rarity) public view returns (uint256) {
    return rarityToSectorVolume[_rarity];
  }

  function getTotalVolume(uint16 _landType) public view returns (uint256) {
    return landTypeToTotalVolume[_landType];
  }

  function getShareRateWithDecimal(uint256 _landSector) public view returns (uint256, uint256) {
    return (
      getSectorVolumeByRarity(getSectorRarity(_landSector)) * SHARE_RATE_DECIMAL / (getTotalVolume(getSectorLandType(_landSector))),
      SHARE_RATE_DECIMAL
    );
  }
}"
