pragma solidity ^0.8.17;









/// @title Hunters
/// @notice Contract for Hunters On Chain playable hunters
/// @author Tansel Kaya, zetsub0ii.eth
/// @dev Compiled using OpenZeppelin v4.8.0
contract Hunters is ERC721, AccessControl, Pausable, DefaultOperatorFilterer {
    using Strings for uint256;
    using Counters for Counters.Counter;

    event DefaultAdminTransferStarted(address oldAdmin, address newAdmin);
    event DefaultAdminTransferred(address oldAdmin, address newAdmin);

    event HunterLocked(address account, uint256 id);
    event HunterUnlocked(address account, uint256 id);

    // Roles
    address private _defaultAdmin;
    address private _assignedDefaultAdmin = address(0x0);

    // Royalty
    address public royaltyReceiver;
    uint256 public royaltyPercentage;

    // Hunter ID -> Locker or 0x0
    mapping(uint256 => address) public lockedHunters;

    // Hunter ID -> Boom power stored in hunter
    mapping(uint256 => uint256) public boomPower;

    Counters.Counter private supply;

    // Can pause the contract, set the metadata and withdraw
    bytes32 constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // Can mint hunters, lock them and increase their boom powers
    // Reserved to the studio operators who oversee the game
    bytes32 constant STUDIO_ROLE = keccak256("STUDIO_ROLE");

    string public uriPrefix = "";
    string public uriSuffix = "";

    constructor(address _admin, address _studio, address _royaltyReceiver, uint256 _royaltyPercentage)
        ERC721("Hunters", "HUNTER")
    {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ADMIN_ROLE, _admin);
        _setupRole(STUDIO_ROLE, _studio);

        royaltyReceiver = _royaltyReceiver;

        require(_royaltyPercentage <= 10000, "Royalty percentage cannot be more than 10000 (100.00%)");
        royaltyPercentage = _royaltyPercentage;

        _defaultAdmin = msg.sender;
    }

    /// @notice Initiates a request to transfer DEFAULT_ADMIN_ROLE
    /// @param newAdmin New admin
    /// @dev This won't be used unless one of the default admins is compromised
    function transferDefaultAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newAdmin != address(0x0), "New admin cannot be 0x0");
        require(newAdmin != _assignedDefaultAdmin, "New admin cannot be the same as the old one");

        _assignedDefaultAdmin = newAdmin;
        emit DefaultAdminTransferStarted(msg.sender, newAdmin);
    }

    /// @notice Cancels a request to transfer DEFAULT_ADMIN_ROLE
    function cancelDefaultAdminTransfer() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _assignedDefaultAdmin = address(0x0);
    }

    /// @notice Claims DEFAULT_ADMIN_ROLE
    /// @dev If user doesn't claim the role, default admin doesn't change
    function claimDefaultAdmin() external {
        require(msg.sender == _assignedDefaultAdmin, "You are not assigned to be the new default admin");

        _grantRole(DEFAULT_ADMIN_ROLE, _assignedDefaultAdmin);
        _revokeRole(DEFAULT_ADMIN_ROLE, _defaultAdmin);

        emit DefaultAdminTransferred(_defaultAdmin, _assignedDefaultAdmin);

        _assignedDefaultAdmin = address(0x0);
        _defaultAdmin = msg.sender;
    }

    /// @notice Mints hunter as studio
    /// @dev Requires STUDIO_ROLE
    /// @param _to Recipient
    /// @param _mintAmount Amount to mint
    function studioMint(address _to, uint256 _mintAmount) external onlyRole(STUDIO_ROLE) {
        _mintLoop(_to, _mintAmount);
    }

    /// @dev Internal loop to mint hunters
    /// @param _receiver Receiver
    /// @param _mintAmount Mint amount
    function _mintLoop(address _receiver, uint256 _mintAmount) internal {
        for (uint256 i = 0; i < _mintAmount; i++) {
            supply.increment();
            _safeMint(_receiver, supply.current());
        }
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        require(_exists(_tokenId), "Non-existent hunter token given!");

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, _tokenId.toString(), uriSuffix))
            : "";
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uriPrefix;
    }

    function totalSupply() external view returns (uint256) {
        return supply.current();
    }

    function setUriPrefix(string memory _uriPrefix) external onlyRole(ADMIN_ROLE) {
        uriPrefix = _uriPrefix;
    }

    function setUriSuffix(string memory _uriSuffix) external onlyRole(ADMIN_ROLE) {
        uriSuffix = _uriSuffix;
    }

    /// @notice Withdraws all balance to the withdrawal address
    /// @dev Requires ADMIN_ROLE
    function withdrawAll() external onlyRole(ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(balance > 0);

        (bool success,) = payable(msg.sender).call{value: balance}("");
        require(success, "Transfer failed.");
    }

    //	██╗      ██████╗  ██████╗██╗  ██╗██╗███╗   ██╗ ██████╗
    //	██║     ██╔═══██╗██╔════╝██║ ██╔╝██║████╗  ██║██╔════╝
    //	██║     ██║   ██║██║     █████╔╝ ██║██╔██╗ ██║██║  ███╗
    //	██║     ██║   ██║██║     ██╔═██╗ ██║██║╚██╗██║██║   ██║
    //	███████╗╚██████╔╝╚██████╗██║  ██╗██║██║ ╚████║╚██████╔╝
    //	╚══════╝ ╚═════╝  ╚═════╝╚═╝  ╚═╝╚═╝╚═╝  ╚═══╝ ╚═════╝

    /// @notice Locks a hunter
    /// @param account Address of owner
    /// @param id Hunter ID
    function lockHunter(address account, uint256 id) external onlyRole(STUDIO_ROLE) {
        // Check if hunter is owned by user
        require(ownerOf(id) == account, "Account doesn't own hunter");

        lockedHunters[id] = account;
        emit HunterLocked(account, id);
    }

    /// @notice Unlocks a hunter
    /// @param account Address of owner
    /// @param id Hunter ID
    function unlockHunter(address account, uint256 id) external onlyRole(STUDIO_ROLE) {
        // Check if hunter is owned by user
        require(ownerOf(id) == account, "Account doesn't own hunter");

        lockedHunters[id] = address(0x0);
        emit HunterUnlocked(account, id);
    }

    /// @notice Returns if a hunter is locked
    /// @param id Hunter ID
    function isHunterLocked(uint256 id) public view returns (bool) {
        return (lockedHunters[id] != address(0x0));
    }

    /// @notice Returns owner if not locked
    /// @dev If there's no owner returns zero address
    /// @param id Hunter ID
    /// @return Owner address / zero address
    function getHunterLocker(uint256 id) public view returns (address) {
        return lockedHunters[id];
    }

    //	██████╗  ██████╗  ██████╗ ███╗   ███╗    ██████╗  ██████╗ ██╗    ██╗███████╗██████╗
    //	██╔══██╗██╔═══██╗██╔═══██╗████╗ ████║    ██╔══██╗██╔═══██╗██║    ██║██╔════╝██╔══██╗
    //	██████╔╝██║   ██║██║   ██║██╔████╔██║    ██████╔╝██║   ██║██║ █╗ ██║█████╗  ██████╔╝
    //	██╔══██╗██║   ██║██║   ██║██║╚██╔╝██║    ██╔═══╝ ██║   ██║██║███╗██║██╔══╝  ██╔══██╗
    //	██████╔╝╚██████╔╝╚██████╔╝██║ ╚═╝ ██║    ██║     ╚██████╔╝╚███╔███╔╝███████╗██║  ██║
    //	╚═════╝  ╚═════╝  ╚═════╝ ╚═╝     ╚═╝    ╚═╝      ╚═════╝  ╚══╝╚══╝ ╚══════╝╚═╝  ╚═╝

    /// @notice Increments Boom power of an account
    /// @param hunterId ID of the hunter
    /// @param incrementAmount Boom power
    function incrementBoomPower(uint256 hunterId, uint256 incrementAmount) external onlyRole(STUDIO_ROLE) {
        boomPower[hunterId] += incrementAmount;
    }

    // 	███████╗████████╗ ██████╗ ██████╗     ██████╗  █████╗ ██╗   ██╗███████╗███████╗
    // 	██╔════╝╚══██╔══╝██╔═══██╗██╔══██╗    ██╔══██╗██╔══██╗██║   ██║██╔════╝██╔════╝
    // 	███████╗   ██║   ██║   ██║██████╔╝    ██████╔╝███████║██║   ██║███████╗█████╗
    // 	╚════██║   ██║   ██║   ██║██╔═══╝     ██╔═══╝ ██╔══██║██║   ██║╚════██║██╔══╝
    // 	███████║   ██║   ╚██████╔╝██║         ██║     ██║  ██║╚██████╔╝███████║███████╗
    // 	╚══════╝   ╚═╝    ╚═════╝ ╚═╝         ╚═╝     ╚═╝  ╚═╝ ╚═════╝ ╚══════╝╚══════╝

    /// @notice Pauses the contract
    /// @dev Requires ADMIN_ROLE
    function pause() external onlyRole(ADMIN_ROLE) whenNotPaused {
        _pause();
    }

    /// @notice Resumes the contract
    /// @dev Requires ADMIN_ROLE
    function resume() external onlyRole(ADMIN_ROLE) whenPaused {
        _unpause();
    }

    //	██████╗  ██████╗ ██╗   ██╗ █████╗ ██╗     ████████╗██╗   ██╗
    //	██╔══██╗██╔═══██╗╚██╗ ██╔╝██╔══██╗██║     ╚══██╔══╝╚██╗ ██╔╝
    //	██████╔╝██║   ██║ ╚████╔╝ ███████║██║        ██║    ╚████╔╝
    //	██╔══██╗██║   ██║  ╚██╔╝  ██╔══██║██║        ██║     ╚██╔╝
    //	██║  ██║╚██████╔╝   ██║   ██║  ██║███████╗   ██║      ██║
    //	╚═╝  ╚═╝ ╚═════╝    ╚═╝   ╚═╝  ╚═╝╚══════╝   ╚═╝      ╚═╝

    /// @notice Sets royaltyReceiver
    /// @param _royaltyReceiver Address of the royalty receiver
    /// @dev Requires ADMIN_ROLE
    function setRoyaltyReceiver(address _royaltyReceiver) external onlyRole(ADMIN_ROLE) {
        royaltyReceiver = _royaltyReceiver;
    }

    /// @notice Sets royaltyPercentage
    /// @param _royaltyPercentage Percentage of the royalty
    /// @dev Requires ADMIN_ROLE
    function setRoyaltyPercentage(uint256 _royaltyPercentage) external onlyRole(ADMIN_ROLE) {
        require(_royaltyPercentage <= 10000, "Royalty percentage cannot be more than 10000 (100.00%)");

        royaltyPercentage = _royaltyPercentage;
    }

    /// @notice Implements EIP-2981 royalty standard
    /// @param tokenId ID of the token
    /// @param salePrice Sale price of the token
    /// @return receiver Address of the royalty receiver
    /// @return royaltyAmount Amount of the royalty
    function royaltyInfo(uint256 tokenId, uint256 salePrice)
        external
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        require(_exists(tokenId), "Token doesn't exist");
        return (royaltyReceiver, (salePrice * royaltyPercentage) / 10000);
    }

    //	 ██████╗ ██╗   ██╗███████╗██████╗ ██████╗ ██╗██████╗ ███████╗███████╗
    //	██╔═══██╗██║   ██║██╔════╝██╔══██╗██╔══██╗██║██╔══██╗██╔════╝██╔════╝
    //	██║   ██║██║   ██║█████╗  ██████╔╝██████╔╝██║██║  ██║█████╗  ███████╗
    //	██║   ██║╚██╗ ██╔╝██╔══╝  ██╔══██╗██╔══██╗██║██║  ██║██╔══╝  ╚════██║
    //	╚██████╔╝ ╚████╔╝ ███████╗██║  ██║██║  ██║██║██████╔╝███████╗███████║
    //	 ╚═════╝   ╚═══╝  ╚══════╝╚═╝  ╚═╝╚═╝  ╚═╝╚═╝╚═════╝ ╚══════╝╚══════╝

    /// @notice Checks if hunter is locked before the transfer
    function _beforeTokenTransfer(address from, address to, uint256 tokenId, uint256 batchSize)
        internal
        override
        whenNotPaused
    {
        require(!isHunterLocked(tokenId), "Hunter is locked");
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function supportsInterface(bytes4 interfaceId) public view override (ERC721, AccessControl) returns (bool) {
        return (interfaceId == type(IERC2981).interfaceId || super.supportsInterface(interfaceId));
    }

    // CORI Royalty overrides

    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function approve(address operator, uint256 tokenId) public override onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public
        override
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
