    "contracts/EventManager.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.14;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

import "./IOurEventTicket.sol";
import { GruveEventTicket } from "./OurEventTicket.sol";

contract GruveEventManager is Initializable, AccessControlUpgradeable, UUPSUpgradeable, ERC1155HolderUpgradeable {

    struct Event {
        address creator;
        uint96 ticketType;
        address tokenAddress;
        bool claimedInitialDeposit;
        uint256 initialDeposit;
        uint256 eventTime;
    }

    struct ContractMode {
        bool requiresInitialDeposit;
        uint200 currentInitalDeposit;
    }

    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant SETUP_MANAGER_ROLE = keccak256("SETUP_MANAGER_ROLE");
    uint256 public constant DENOMINATOR = 1_000_000;

    mapping(address => uint256) private stableTokenRate;
    mapping(address => Event) private events;
    mapping(bytes32 => address) private EventNameToAddress;
    mapping(uint96 => bytes32) private typeToCodeHash;
    mapping(address => mapping(uint256 => uint256)) private basePrices;
    ContractMode mode;
    bytes32 public constant WHITELIST_ROLE = keccak256("WHITELIST_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    mapping(address => bool) private whitelist;

    event RegisteredEvent(address indexed contractAddress, address indexed registeredBy, bytes32 indexed eventId);
    event WithdrawEvent(address indexed token, address indexed to, uint amount, bytes data);
    event PurchaseTicketsEvent(
        address indexed contractAddress,
        address indexed purchasedBy,
        uint256 cost,
        uint256[] tokenIds,
        uint256[] quantity
    );

    function initialize() public initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);
        _grantRole(SETUP_MANAGER_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}

    function setMode(ContractMode calldata mode_) public onlyRole(SETUP_MANAGER_ROLE) {
        mode = mode_;
    }

    function setWhiteList(address user, bool whitelistStatus) public onlyRole(SETUP_MANAGER_ROLE) {
        whitelist[user] = whitelistStatus;
    }

    /**
     * @dev
     * ```Add Token```
     *  Set rate for tokens

     * 
     * @param _tokenAddress token address on the Network
     * @param rate a preset rate 
     *
     *
     * Requirement:
     * - only callable by owner
     * - rate can't be set to Zero
     *
     * Note: rate is to 6 decimal place
     *  this implies if rate is @ 0.992 per $
     *  rate should be set to 0.992*10^6 = 922000
     */
    function addToken(address _tokenAddress, uint256 rate) external onlyRole(SETUP_MANAGER_ROLE) {
        require(_tokenAddress != address(0), "Address can't be zero");
        require(rate != 0, "rate can't be zero");
        stableTokenRate[_tokenAddress] = rate;
    }

    function getContractAddress(string calldata eventName_) external view returns (address) {
        return EventNameToAddress[keccak256(abi.encodePacked(eventName_))];
    }

    /**
     * @dev
     * ```Remove Token```

     * @param _tokenAddress token on the Network
     *
     *
     * Requirement:
     * - only callable by owner
     * - revert if the tokenAddress rate is already set to zero
     *
     */
    function removeToken(address _tokenAddress) external onlyRole(SETUP_MANAGER_ROLE) {
        require(_tokenAddress != address(0), "Address can't be zero");
        require(stableTokenRate[_tokenAddress] != 0, "token already disabled");
        stableTokenRate[_tokenAddress] = 0;
    }

    function setApprovedTicketType(uint96 ticketId, address sample) external onlyRole(SETUP_MANAGER_ROLE) {
        typeToCodeHash[ticketId] = sample.codehash;
    }

    function createEvents(
        IOurEventTicket ticketAddress,
        uint96 ticketType_,
        uint256 eventTime_,
        uint256[] calldata tokenIds,
        uint256[] calldata mintQuantity,
        uint256[] calldata prices,
        address tokenAddress_
    ) external {
        assert(checkName(ticketAddress.name()));
        require(typeToCodeHash[ticketType_] == address(ticketAddress).codehash, "GEM: unapproved Contract");
        require(
            EventNameToAddress[keccak256(abi.encodePacked(ticketAddress.name()))] == address(0),
            "GEM: An Event with this Name Already Exists"
        );
        ticketAddress.mintBatch(address(this), tokenIds, mintQuantity, bytes(""));
        assert(mintQuantity.length == prices.length);
        for (uint256 i = 0; i < prices.length; i++) {
            basePrices[address(ticketAddress)][tokenIds[i]] = prices[i];
        }
        EventNameToAddress[keccak256(abi.encodePacked(ticketAddress.name()))] = address(ticketAddress);
        if (!mode.requiresInitialDeposit) {
            events[address(ticketAddress)] = Event(_msgSender(), ticketType_, tokenAddress_, false, 0, eventTime_);
        } else {
            require(stableTokenRate[tokenAddress_] > 0, "GEM: UnApprovedToken");
            if (IERC20Metadata(tokenAddress_).transferFrom(_msgSender(), address(this), mode.currentInitalDeposit))
                events[address(ticketAddress)] = Event(
                    _msgSender(),
                    ticketType_,
                    tokenAddress_,
                    true,
                    mode.currentInitalDeposit,
                    eventTime_
                );
        }
        emit RegisteredEvent(address(ticketAddress), _msgSender(), keccak256(abi.encodePacked(ticketAddress.name())));
    }

    function getCost(
        address tokenAddress_,
        address eventAddress_,
        uint256[] calldata tokenIds_,
        uint256[] calldata tokenQuantities_
    ) public view returns (uint256 cost, uint256 costInTokens) {
        cost = 0;
        for (uint256 j = 0; j < tokenQuantities_.length; j++) {
            require(
                tokenQuantities_[j] <= IERC1155(eventAddress_).balanceOf(address(this), tokenIds_[j]),
                "GEM: Quantity Request Not Available"
            );
            cost += (basePrices[eventAddress_][tokenIds_[j]] * tokenQuantities_[j]);
        }
        if (cost > 0) {
            costInTokens = (cost * (10 ** IERC20Metadata(tokenAddress_).decimals())) / (stableTokenRate[tokenAddress_]);
        } else {
            costInTokens = 0;
        }
    }

    function purchaseTickets(
        address tokenAddress_,
        string calldata eventName_,
        uint256[] calldata tokenIds_,
        uint256[] calldata tokenQuantities_,
        bytes calldata data
    ) public {
        require(EventNameToAddress[keccak256(abi.encodePacked(eventName_))] != address(0), "GEM: Event doesnt exist");
        require(tokenIds_.length == tokenQuantities_.length, "GEM: tokenQuantitys and tokenIds length mismatch");
        (uint256 cost, uint256 amount) = getCost(
            tokenAddress_,
            EventNameToAddress[keccak256(abi.encodePacked(eventName_))],
            tokenIds_,
            tokenQuantities_
        );
        if (cost > 0) {
            uint256 rate = stableTokenRate[tokenAddress_];
            require(rate > 0, "GEM: this is not a free Event");
            require(
                IERC20(tokenAddress_).balanceOf(_msgSender()) >= amount &&
                    amount >= IERC20(tokenAddress_).allowance(_msgSender(), address(this)),
                "insufficent Balance or token Allowance"
            );
            _purchaseTickets(
                EventNameToAddress[keccak256(abi.encodePacked(eventName_))],
                tokenAddress_,
                _msgSender(),
                tokenIds_,
                tokenQuantities_,
                amount,
                data
            );
        } else {
            _purchaseTickets(
                EventNameToAddress[keccak256(abi.encodePacked(eventName_))],
                address(0),
                _msgSender(),
                tokenIds_,
                tokenQuantities_,
                0,
                data
            );
        }
    }

    function managerPurchase(
        address holder,
        address tokenAddress_,
        string calldata eventName_,
        uint256[] calldata tokenIds_,
        uint256[] calldata tokenQuantities_,
        bytes calldata data
    ) external onlyRole(MANAGER_ROLE) {
        if (
            !IAccessControlUpgradeable(EventNameToAddress[keccak256(abi.encodePacked(eventName_))]).hasRole(
                WHITELIST_ROLE,
                holder
            ) && whitelist[holder]
        ) {
            IAccessControlUpgradeable(EventNameToAddress[keccak256(abi.encodePacked(eventName_))]).grantRole(
                WHITELIST_ROLE,
                holder
            );
        }
        getCost(
            tokenAddress_,
            EventNameToAddress[keccak256(abi.encodePacked(eventName_))],
            tokenIds_,
            tokenQuantities_
        );
        _purchaseTickets(
            EventNameToAddress[keccak256(abi.encodePacked(eventName_))],
            tokenAddress_,
            holder,
            tokenIds_,
            tokenQuantities_,
            0,
            data
        );
    }

    function _purchaseTickets(
        address contactAddress,
        address tokenAddress_,
        address to,
        uint256[] calldata tokenIds_,
        uint256[] calldata tokenQuantities_,
        uint256 amount,
        bytes calldata data
    ) internal {
        if (amount > 0) {
            IERC20(tokenAddress_).transferFrom(to, address(this), amount);
        }
        IERC1155(contactAddress).safeBatchTransferFrom(address(this), to, tokenIds_, tokenQuantities_, data);
        emit PurchaseTicketsEvent(contactAddress, to, amount, tokenIds_, tokenQuantities_);
    }


    function approve(address token, address to, uint256 amount, bytes calldata data) external onlyRole(UPGRADER_ROLE) {
        IERC20(token).approve(to, amount);
        emit WithdrawEvent(token, to, amount, data);
    }

    bytes private constant ALLOWED_CHAR = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ-";

    function checkName(string memory _name) public pure returns (bool) {
        uint256 allowedChars = 0;
        bytes memory byteString = bytes(_name);
        for (uint256 i = 0; i < byteString.length; i++) {
            for (uint256 j = 0; j < ALLOWED_CHAR.length; j++) {
                if (byteString[i] == ALLOWED_CHAR[j]) {
                    allowedChars++;
                }
            }
        }
        if (allowedChars < byteString.length) {
            return false;
        }
        return true;
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(AccessControlUpgradeable, ERC1155ReceiverUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
"
