    "contracts/OurEventTicket.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.14;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

interface IManager {
    function checkName(string memory _name) external pure returns (bool);
}

contract GruveEventTicket is ERC1155, AccessControl, Pausable, ERC1155Supply {
    bytes32 public constant DEPLOYER_ROLE = keccak256("DEPLOYER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant WHITELIST_ROLE = keccak256("WHITELIST_ROLE");
    string private _EVENT_NAME;
    mapping(uint256 => uint256) maxAmountOfTokens;

    constructor(
        string memory url_,
        string memory name_,
        address[3] memory addressToAssign,
        uint256[] memory tokenIds,
        uint256[] memory maxQuantities
    ) ERC1155(url_) {
        require(IManager(addressToAssign[0]).checkName(name_));
        _EVENT_NAME = name_;
        // _grantRole(DEFAULT_ADMIN_ROLE, addressToAssign[0]);
        _grantRole(MINTER_ROLE, addressToAssign[0]);
        _grantRole(DEPLOYER_ROLE, addressToAssign[0]);
        _grantRole(DEPLOYER_ROLE, addressToAssign[1]);
        _grantRole(DEPLOYER_ROLE, addressToAssign[2]);
        _grantRole(WHITELIST_ROLE, addressToAssign[0]);
        _grantRole(WHITELIST_ROLE, addressToAssign[2]);
        _setRoleAdmin(WHITELIST_ROLE, DEPLOYER_ROLE);
        _setTokenMax(tokenIds, maxQuantities);
    }

    function setTokenMax(uint256[] memory tokenIds, uint256[] memory maxQuantities) public {
        if (!(hasRole(DEPLOYER_ROLE, _msgSender()) && tokenIds.length == maxQuantities.length)) {
            revert();
        }
        _setTokenMax(tokenIds, maxQuantities);
    }

    function _setTokenMax(uint256[] memory tokenIds, uint256[] memory maxQuantities) internal {
        for (uint256 j = 0; j < tokenIds.length; j++) {
            maxAmountOfTokens[tokenIds[j]] = maxQuantities[j];
        }
    }

    function setURI(string calldata newuri) public onlyRole(DEPLOYER_ROLE) {
        _setURI(newuri);
    }

    function name() external view returns (string memory) {
        return _EVENT_NAME;
    }

    function freezeTransfers(bool status) public onlyRole(DEPLOYER_ROLE) {
        if (status) {
            _pause();
        } else {
            _unpause();
        }
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyRole(MINTER_ROLE) {
        _mintBatch(to, ids, amounts, data);
    }

    function burnBatch(address account, uint256[] memory ids, uint256[] memory values) public virtual {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not token owner nor approved"
        );

        _burnBatch(account, ids, values);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155, ERC1155Supply) whenNotPaused {
        for (uint256 j = 0; j < ids.length; j++) {
            if (maxAmountOfTokens[ids[j]] != 0 && !hasRole(WHITELIST_ROLE, to)) {
                require(
                    balanceOf(to, ids[j]) + amounts[j] <= maxAmountOfTokens[ids[j]],
                    "GET: receiver cant exceed maxThreshold"
                );
            }
        }
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function uri(uint256 _tokenid) public view override returns (string memory) {
        return string(abi.encodePacked(super.uri(0), Strings.toString(_tokenid), ".json"));
    }

    // // The following functions are overrides required by Solidity.

    function supportsInterface(bytes4 interfaceId) public view override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
"
