// EDAT Token Stake Contract
contract EnvidaStake is Context, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct Stake {
        address user;
        uint256 amount;
        uint256 since;
        uint256 rewardRatio;
        uint256 rate;
        bool isActive;
    }

    uint256 public stakeId;
    uint256[] private allStakeIds;

    uint256 public totalStakedAmount;
    uint256 public totalStakeRewardClaimed;

    mapping(address => uint256[]) private stakesByAddress;
    mapping(address => uint256) private stakePlusAddress;

    mapping(uint256 => Stake) public stakedToken;
    mapping(uint256 => uint256) public stakeRates;
    mapping(uint256 => uint256) private totalStakesByRate;

    event Staked(
        address indexed user,
        uint256 amount,
        uint256 timestamp,
        uint256 rewardRatio,
        uint256 rate,
        bool isActive
    );

    event EmergenyWithdrawed(address indexed user, uint256 amount);
    event Harvested(address indexed user, uint256 amount);
    event Exited(address indexed user, uint256 amount);

    event StakeFeeChanged(uint256 feepercent);
    event StakeRateSetted(uint256 amount);
    event EmergenyWithdrawFeeChanged(uint256 percent);
    event StakePlusAmountChanged(uint256 percent);
    event EnvidaTokenAddressChanged(address newToken);

    IERC20 public envidaToken;
    address public stakeFeeAddress;
    uint256 public constant PERCENT_DIVIDER = 10000;

    uint256 public emergencyFee = 2500; // 25%
    uint256 public stakeFee = 100; // 1%
    uint256 public stakePlus = 200; // 2%
    uint256 public maxEmergencyWithdrawRate = 6; // 6 months
    uint256 public minHarvestRate = 6; // 6 months

    constructor(address envidaToken_) {
        require(
            envidaToken_ != address(0),
            "EDAT Stake : contract adress is cant be zero address"
        );
        envidaToken = IERC20(envidaToken_);
        stakeFeeAddress = _msgSender();
    }

    modifier onlyEOA() {
        require(tx.origin == _msgSender(), "EDAT Stake : should be EOA");
        _;
    }

    modifier onlyStakeExist() {
        require(
            totalStakedAmount > 0,
            "ZD Stake: there are no stake in contract"
        );
        _;
    }

    // @dev emergeny withdraw rate limit changer
    function setMaxEmergencyWithdrawRate(uint256 rate) external {
        require(rate > 0 && rate <= 6, "EDAT Stake: max emergeny rate can set 6m");
        require(
            stakeRates[rate] != 0,
            "EDAT Stake: there is no stake reward ratio for this rate"
        );
        maxEmergencyWithdrawRate = rate;
    }

    // @dev harvest rate limit changer
    function setMinHarvestRate(uint256 rate) external {
        require(
            rate > 0 && rate <= 12,
            "EDAT Stake: min harvest rate can set to 12m"
        );
        require(
            stakeRates[rate] != 0,
            "EDAT Stake: there is no stake reward ratio for this rate"
        );
        minHarvestRate = rate;
    }

    // @dev add stake plus addresses to mapping
    function addStakePlusAddress(
        address[] calldata wallets
    ) external onlyOwner {
        require(
            wallets.length < 100,
            "EDAT Stake: Max 100 person can add one time"
        );
        for (uint i = 0; i < wallets.length; i++) {
            stakePlusAddress[wallets[i]] = 1;
        }
    }

    // @dev remove address from stake plus mapping cant get plus earning
    function removeAddressFromStakePlus(address wallet) external {
        stakePlusAddress[wallet] = 0;
    }

    // @dev check address stake plus status
    function isStakePlusAddress(
        address wallet
    ) external view returns (uint256) {
        return stakePlusAddress[wallet];
    }

    // @dev setting stake rates
    function setStakeRate(uint256 rate, uint256 rewardRatio) public onlyOwner {
        require(rate != 0, "EDAT Stake: zero rate can not set");
        require(rewardRatio != 0, "EDAT Stake: reward rate can not be zero");
        // rate is month rewardRate calculation over PERCENT_DIVIDER be careful
        stakeRates[rate] = rewardRatio;
        emit StakeRateSetted(rate);
    }

    // @dev seting set plus amount
    function setStakePlus(uint256 amount) external onlyOwner {
        // calculation over PERCENT_DIVIDER be careful
        stakePlus = amount;
        emit StakePlusAmountChanged(amount);
    }

    // @dev setting emergeny withdraw fee if user want to exit before time limit should pay fee
    function setEmergencyWithdrawFee(uint256 fee) external onlyOwner {
        require(fee <= 2500, "EDAT Stake: max withdraw fee can be 25%");
        emergencyFee = fee;
        emit EmergenyWithdrawFeeChanged(fee);
    }

    // @dev if setted every user should pay fee for stake
    function setStakeFee(uint256 fee) external onlyOwner {
        require(fee <= 2500, "EDAT Stake: max stake fee can be 25%");
        stakeFee = fee;
        emit StakeFeeChanged(fee);
    }

    // @dev setting stakeing fee address
    function setStakeFeeAddress(address wallet) external onlyOwner {
        require(
            wallet != address(0),
            "EDAT Stake : user adress is zero address"
        );
        stakeFeeAddress = wallet;
    }

    // @dev this function is for frontend easy calculations
    function getTotalStakesByRate(
        uint256 rate
    ) external view returns (uint256) {
        return totalStakesByRate[rate];
    }

    // @dev stakeing
    function stake(
        uint256 amount,
        uint256 rate
    ) external nonReentrant onlyEOA returns (uint256) {
        require(amount > 0, "EDAT Stake : you cant stake zero");
        uint256 _addressBalance = envidaToken.balanceOf(_msgSender());
        require(
            _addressBalance >= amount,
            "EDAT Stake: cannot stake more than you own"
        );

        return _stake(_msgSender(), amount, rate);
    }

    // @dev stakeing internal
    function _stake(
        address wallet,
        uint256 amount,
        uint256 rate
    ) internal returns (uint256) {
        require(
            stakeRates[rate] != 0,
            "EDAT Stake: there is no stake reward ratio for this rate"
        );

        uint256 stakeFeeAmount = 0;
        uint256 stakeAmount = amount;

        if (stakeFee > 0) {
            stakeFeeAmount = amount.mul(stakeFee).div(PERCENT_DIVIDER);
            require(stakeFeeAmount > 0, "EDAT Stake : fee calculation error");
            stakeAmount = stakeAmount.sub(stakeFeeAmount);
        }

        uint256 _id = ++stakeId;
        allStakeIds.push(_id);
        stakesByAddress[wallet].push(_id);
        totalStakesByRate[rate] = totalStakesByRate[rate].add(stakeAmount);

        totalStakedAmount = totalStakedAmount.add(stakeAmount);

        stakedToken[_id].user = wallet;
        stakedToken[_id].amount = stakeAmount;
        stakedToken[_id].since = block.timestamp;
        stakedToken[_id].rewardRatio = stakeRates[rate];
        stakedToken[_id].rate = rate;
        stakedToken[_id].isActive = true;

        if (stakeFeeAmount > 0) {
            // user send tokens to fee address
            envidaToken.safeTransferFrom(
                _msgSender(),
                stakeFeeAddress,
                stakeFeeAmount
            );
        }

        // user send tokens to stake contract
        envidaToken.safeTransferFrom(_msgSender(), address(this), stakeAmount);

        emit Staked(
            _msgSender(),
            stakeAmount,
            block.timestamp,
            stakeRates[rate],
            rate,
            true
        );

        return _id;
    }

    // @dev user can stake for another user
    function stakeFor(
        address wallet,
        uint256 amount,
        uint256 rate
    ) external nonReentrant onlyEOA returns (uint256) {
        require(
            wallet != address(0),
            "EDAT Stake : user adress is zero address"
        );
        require(amount > 0, "EDAT Stake : you cant stake zero");
        uint256 _addressBalance = envidaToken.balanceOf(_msgSender());
        require(
            _addressBalance >= amount,
            "EDAT Stake: cannot stake more than you own"
        );

        return _stake(wallet, amount, rate);
    }

    // @dev get rewards in pool
    function getRewardsLeft() public view returns (uint256) {
        uint256 contractTotalBalance = envidaToken.balanceOf(address(this));
        return contractTotalBalance.sub(totalStakedAmount);
    }

    // @dev calculate stake reward
    function calculateStakeRewards(uint256 id) public view returns (uint256) {
        uint256 rewardAmount = stakedToken[id]
            .rewardRatio
            .mul(stakedToken[id].amount)
            .div(PERCENT_DIVIDER);

        return rewardAmount;
    }

    // @dev user can harvest tokens every 30d
    function harvest(uint256 id) external nonReentrant onlyEOA onlyStakeExist {
        require(
            _msgSender() == stakedToken[id].user,
            "EDAT Stake : this is not your token"
        );
        require(stakedToken[id].isActive, "EDAT Stake : not active");
        require(
            stakedToken[id].rate >= minHarvestRate,
            "EDAT Stake : monthy claim closed to this stake rate"
        );
        require(
            block.timestamp > stakedToken[id].since + 30 days,
            "EDAT Stake : monthly claim time not yet"
        );

        uint256 stakeMonthlyReward = _harvest(id);
        require(stakeMonthlyReward > 0, "EDAT Stake : monthly reward is zero");

        // total rewards update
        totalStakeRewardClaimed = totalStakeRewardClaimed.add(
            stakeMonthlyReward
        );

        // stake time update with timestamp
        stakedToken[id].since = block.timestamp;

        // wallet recive tokens
        envidaToken.safeTransfer(_msgSender(), stakeMonthlyReward);

        emit Harvested(_msgSender(), stakeMonthlyReward);
    }

    // @dev harvest internal
    function _harvest(uint256 id) internal view returns (uint256) {
        uint256 rewardsLeftInContract = getRewardsLeft();
        require(
            rewardsLeftInContract > 0,
            "EDAT Stake : no reward left in contract"
        );

        uint256 stakePlusReward = 0;

        // divide should be apply to calculate day as integer
        uint256 totalDaysPast = block.timestamp.sub(stakedToken[id].since).div(
            1 days
        );

        require(
            totalDaysPast > 0,
            "EDAT Stake : total day past must bigger then 0"
        );

        uint256 eRewardAmount = stakedToken[id]
            .amount
            .mul(totalDaysPast.mul(stakedToken[id].rewardRatio))
            .div(stakedToken[id].rate.mul(30))
            .div(PERCENT_DIVIDER);

        if (stakePlusAddress[stakedToken[id].user] > 0) {
            stakePlusReward = stakedToken[id].amount.mul(stakePlus).div(
                PERCENT_DIVIDER
            );
        }

        eRewardAmount = eRewardAmount.add(stakePlusReward);

        require(
            eRewardAmount > 0,
            "EDAT Stake : reward should be bigger then zero"
        );

        if (rewardsLeftInContract < eRewardAmount) {
            eRewardAmount = rewardsLeftInContract;
        }

        return eRewardAmount;
    }

    // @dev user can exit from pool when stake finish
    function exit(uint256 id) external nonReentrant onlyEOA onlyStakeExist {
        require(stakedToken[id].isActive, "EDAT Stake : not active");

        require(
            _msgSender() == stakedToken[id].user,
            "EDAT Stake : this is not your token"
        );

        require(
            block.timestamp >
                stakedToken[id].since.add(stakedToken[id].rate.mul(30 days)),
            "EDAT Stake : time not yet"
        );

        stakedToken[id].isActive = false;

        uint256 stakeAndReward = _exit(id);

        envidaToken.safeTransfer(_msgSender(), stakeAndReward);

        emit Exited(_msgSender(), stakeAndReward);
    }

    // @dev exit internal
    function _exit(uint256 id) internal returns (uint256) {
        uint256 stakeAmount = stakedToken[id].amount;
        uint256 rewardAmount = calculateStakeRewards(id);

        uint256 rewardsLeftInContract = getRewardsLeft();

        if (rewardsLeftInContract < rewardAmount) {
            rewardAmount = rewardsLeftInContract;
        }

        totalStakedAmount = totalStakedAmount.sub(stakedToken[id].amount);

        totalStakesByRate[stakedToken[id].rate] = totalStakesByRate[
            stakedToken[id].rate
        ].sub(stakedToken[id].amount);

        totalStakeRewardClaimed = totalStakeRewardClaimed.add(rewardAmount);

        return stakeAmount.add(rewardAmount);
    }

    // @dev user can withdraw before time finish but should pay fee
    function emergencyWithdraw(
        uint256 id
    ) external nonReentrant onlyEOA onlyStakeExist {
        require(
            envidaToken.balanceOf(address(this)) >= stakedToken[id].amount,
            "EDAT Stake : can not withdraw more then contract have"
        );
        require(stakedToken[id].isActive, "Stake : stake is not active");
        require(
            _msgSender() == stakedToken[id].user,
            "EDAT Stake : this is not your token"
        );
        require(
            stakedToken[id].rate <= maxEmergencyWithdrawRate,
            "EDAT Stake : cant emergency withdraw"
        );

        stakedToken[id].isActive = false;

        totalStakedAmount = totalStakedAmount.sub(stakedToken[id].amount);

        totalStakesByRate[stakedToken[id].rate] = totalStakesByRate[
            stakedToken[id].rate
        ].sub(stakedToken[id].amount);

        uint256 feeAmount = 0;

        if (emergencyFee > 0) {
            feeAmount = stakedToken[id].amount.mul(emergencyFee).div(
                PERCENT_DIVIDER
            );
            // send fee to fee address
            envidaToken.safeTransfer(stakeFeeAddress, feeAmount);
        }

        uint256 afterFee = stakedToken[id].amount.sub(feeAmount);
        // send balance after fee staked tokens to user
        envidaToken.safeTransfer(_msgSender(), afterFee);

        emit EmergenyWithdrawed(_msgSender(), stakedToken[id].amount);
    }

    // @dev easy for frontend all ids
    function getAddressAllStakeIds(
        address wallet
    ) external view returns (uint256[] memory) {
        return stakesByAddress[wallet];
    }

    // @dev easy for frontend stake details
    function getSingleStakeDetails(
        uint256 id
    )
        external
        view
        returns (
            address user,
            uint256 amount,
            uint256 since,
            uint256 rewardRatio,
            uint256 rate,
            bool isActive
        )
    {
        return (
            stakedToken[id].user,
            stakedToken[id].amount,
            stakedToken[id].since,
            stakedToken[id].rewardRatio,
            stakedToken[id].rate,
            stakedToken[id].isActive
        );
    }

    // @dev easy for frontend get total stake amount
    function getAddressActiveTotalStakedAmount(
        address wallet
    ) external view returns (uint256) {
        uint256[] memory userStakes = stakesByAddress[wallet];

        uint256 totalStaked = 0;
        for (uint256 i = 0; i < userStakes.length; i++) {
            if (stakedToken[userStakes[i]].isActive) {
                totalStaked = totalStaked.add(
                    stakedToken[userStakes[i]].amount
                );
            }
        }
        return totalStaked;
    }
}
