      "content": "// SPDX-License-Identifier: Unlicensed
pragma solidity >=0.8.18;

interface IERC721 {
    function transferFrom(address from, address to, uint256 tokenId) external;
    function ownerOf(uint256 tokenId) external view returns (address owner);
}

contract airdrop{
    address immutable public holderNFT;
    address immutable public newNFT;
    uint256 public currentHolderTokenId;
    constructor(address _holderNFT,address _newNFT){
        (holderNFT,newNFT) = (_holderNFT,_newNFT);
    }
    function send(uint256 amount) external {
        require(5000 >= amount + currentHolderTokenId,"maximuim send exceeded");
        uint i=currentHolderTokenId;
        address sender = msg.sender;
        address holder;
        for(;i<currentHolderTokenId+amount;i++){
            holder = IERC721(holderNFT).ownerOf(currentHolderTokenId);
            IERC721(newNFT).transferFrom(sender,holder,i+20);
        }
        currentHolderTokenId = amount + currentHolderTokenId;
    }
}"
