    "contracts/PolyFriendTech.sol": {
      "content": "pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";

contract PolyFriendTech is Ownable {
	address public platformAddress;
	address public protocolFeeDestination;
	uint256 public protocolFeePercent;
	uint256 public userFeePercent;
	bool public sellLock = true;

	event Trade(
		address trader,
		address indexed user,
		bool isBuy,
		uint256 amount,
		uint256 ethAmount,
		uint256 protocolEthAmount,
		uint256 userEthAmount,
		uint256 supply,
		uint256 timestamp
	);

	event UsernameSet(address indexed user, string indexed username);
	// Subject => (Holder => Balance)
	mapping(address => mapping(address => uint256)) public friendsBalance;

	// Subject => Supply
	mapping(address => uint256) public friendsSupply;

	// Subject => Supply
	mapping(address => string) public addressUsernameMap;

	function setPlatformAddress(address _platformAddress) public onlyOwner {
		platformAddress = _platformAddress;
	}

	function setFeeDestination(address _feeDestination) public onlyOwner {
		protocolFeeDestination = _feeDestination;
	}

	function setProtocolFeePercent(uint256 _feePercent) public onlyOwner {
		protocolFeePercent = _feePercent;
	}

	function setSubjectFeePercent(uint256 _feePercent) public onlyOwner {
		userFeePercent = _feePercent;
	}

	function setUsername(
		string calldata username,
		bytes calldata platformSignature
	) public {
		require(
			bytes(addressUsernameMap[msg.sender]).length == 0,
			"Username already set"
		);

		bytes32 messageHash = keccak256(
			abi.encodePacked(
				username,
				"Username Verified for PolyFriendTech",
				msg.sender
			)
		);
		bytes32 prefixedMessageHash = keccak256(
			abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
		);
		bool isSignatureValid = SignatureChecker.isValidSignatureNow(
			platformAddress,
			prefixedMessageHash,
			platformSignature
		);
		require(isSignatureValid, "Invalid signature");
		addressUsernameMap[msg.sender] = username;
		emit UsernameSet(msg.sender, username);
	}

	function getUsername(address user) public view returns (string memory) {
		return addressUsernameMap[user];
	}

	function unlockSell() public {
		require(block.timestamp >= 1694390400, "Too early to unlock");
		sellLock = false;
	}

	function getPrice(
		uint256 supply,
		uint256 amount
	) public pure returns (uint256) {
		uint256 sum1 = supply == 0
			? 0
			: ((supply - 1) * (supply) * (2 * (supply - 1) + 1)) / 6;
		uint256 sum2 = supply == 0 && amount == 1
			? 0
			: ((supply - 1 + amount) *
				(supply + amount) *
				(2 * (supply - 1 + amount) + 1)) / 6;
		uint256 summation = sum2 - sum1;
		return (summation * 1 ether) / 5;
	}

	function getBuyPrice(
		address user,
		uint256 amount
	) public view returns (uint256) {
		return getPrice(friendsSupply[user], amount);
	}

	function getSellPrice(
		address user,
		uint256 amount
	) public view returns (uint256) {
		return getPrice(friendsSupply[user] - amount, amount);
	}

	function getBuyPriceAfterFee(
		address user,
		uint256 amount
	) public view returns (uint256) {
		uint256 price = getBuyPrice(user, amount);
		uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
		uint256 userFee = (price * userFeePercent) / 1 ether;
		return price + protocolFee + userFee;
	}

	function getSellPriceAfterFee(
		address user,
		uint256 amount
	) public view returns (uint256) {
		uint256 price = getSellPrice(user, amount);
		uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
		uint256 userFee = (price * userFeePercent) / 1 ether;
		return price - protocolFee - userFee;
	}

	function buy(address user, uint256 amount) public payable {
		uint256 supply = friendsSupply[user];
		require(
			supply > 0 || user == msg.sender,
			"Only the user can buy the first supply"
		);
		uint256 price = getPrice(supply, amount);
		uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
		uint256 userFee = (price * userFeePercent) / 1 ether;
		require(
			msg.value >= price + protocolFee + userFee,
			"Insufficient payment"
		);
		friendsBalance[user][msg.sender] =
			friendsBalance[user][msg.sender] +
			amount;
		friendsSupply[user] = supply + amount;
		emit Trade(
			msg.sender,
			user,
			true,
			amount,
			price,
			protocolFee,
			userFee,
			supply + amount,
			block.timestamp
		);
		(bool success1, ) = protocolFeeDestination.call{ value: protocolFee }(
			""
		);
		(bool success2, ) = user.call{ value: userFee }("");
		require(success1 && success2, "Unable to send funds");
	}

	function sell(address user, uint256 amount) public payable {
		require(sellLock == false, "Selling is locked");
		uint256 supply = friendsSupply[user];
		require(supply > amount, "Cannot sell the last supply");
		uint256 price = getPrice(supply - amount, amount);
		uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
		uint256 userFee = (price * userFeePercent) / 1 ether;
		require(
			friendsBalance[user][msg.sender] >= amount,
			"Insufficient balance"
		);
		friendsBalance[user][msg.sender] =
			friendsBalance[user][msg.sender] -
			amount;
		friendsSupply[user] = supply - amount;
		emit Trade(
			msg.sender,
			user,
			false,
			amount,
			price,
			protocolFee,
			userFee,
			supply - amount,
			block.timestamp
		);
		(bool success1, ) = msg.sender.call{
			value: price - protocolFee - userFee
		}("");
		(bool success2, ) = protocolFeeDestination.call{ value: protocolFee }(
			""
		);
		(bool success3, ) = user.call{ value: userFee }("");
		require(success1 && success2 && success3, "Unable to send funds");
	}
}
