pragma solidity ^0.8.4;





interface spendableInterface {
    function spend(address account, uint256 id, uint256 value) external;
    function spendBatch(address account, uint256[] memory ids, uint256[] memory values) external;
}

contract CrazyBearsGacha is ERC1155, AccessControl, Pausable, ERC1155Burnable {
    bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");
    bytes32 public constant COLLECTION_SETTER_ROLE = keccak256("COLLECTION_SETTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    struct Collection {
        uint id;
        uint[] queue;
        uint[] tokenIds;
        uint[] tokenMaxSupply;
        uint maxMintPerWallet;
        mapping(address => uint) mintedPerWallet; // { wallet: minted }
        uint maxSupply;
        uint minted;
        mapping(uint => int) cost; // { ItemId: Amount }
        bool active;
    }

    mapping(uint => string) public tokenURI;
    mapping(uint => Collection) collections;
    uint[] public activeCollections;
    address public costContractAddress;
    ERC1155 public costContract;

    constructor() ERC1155("") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(URI_SETTER_ROLE, msg.sender);
        _grantRole(COLLECTION_SETTER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
    }

    function setCostContract(address _address) public onlyRole(COLLECTION_SETTER_ROLE) {
        costContractAddress = _address;
        costContract = ERC1155(costContractAddress);
    }

    function setCost(uint256 _collectionId, uint256[] memory _tokenIds, int256[] memory _amount) public onlyRole(COLLECTION_SETTER_ROLE) {
        require(_tokenIds.length == _amount.length, "Array length does not much.");
        for (uint i = 0; i < _tokenIds.length; i++) {
            collections[_collectionId].cost[_tokenIds[i]] = _amount[i];
        }
    }

    function setURI(uint256 _tokenID, string memory _uri) public onlyRole(URI_SETTER_ROLE) {
        tokenURI[_tokenID] = _uri;
        emit URI(_uri, _tokenID);
    }

    function uri(uint _tokenID) public override view returns (string memory) {
        return tokenURI[_tokenID];
    }

    function isActive(uint _collectionId) public view returns (bool) {
        return collections[_collectionId].active;
    }

    function maxSupply(uint _collectionId) public view returns (uint) {
        return collections[_collectionId].maxSupply;
    }

    function tokenIds(uint _collectionId) public view returns (uint[] memory) {
        return collections[_collectionId].tokenIds;
    }

    function tokenMaxSupply(uint _collectionId) public view returns (uint[] memory) {
        return collections[_collectionId].tokenMaxSupply;
    }

    function minted(uint _collectionId) public view returns (uint) {
        return collections[_collectionId].minted;
    }

    function cost(uint _collectionId, uint _tokenId) public view returns (int) {
        return collections[_collectionId].cost[_tokenId];
    }

    function maxMintPerWallet(uint _collectionId) public view returns (uint) {
        return collections[_collectionId].maxMintPerWallet;
    }

    function mintedPerWallet(uint _collectionId, address _account) public view returns (uint) {
        return collections[_collectionId].mintedPerWallet[_account];
    }

    function tokenMinted(uint _collectionId, uint _tokenId) public view returns (uint) {
        uint total = 0;
        if (collections[_collectionId].minted == 0) {
            return 0;
        }

        for (uint i = 0; i < collections[_collectionId].minted; i++) {
            if (_tokenId == collections[_collectionId].queue[i]) {
                total++;
            }
        }
        return total;
    }

    function setCollection(uint _collectionId, uint[] memory _tokenIds, uint[] memory _maxSupply) public onlyRole(COLLECTION_SETTER_ROLE) {
        require(_tokenIds.length == _maxSupply.length, "Array length does not much.");
        uint total = 0;
        for (uint i = 0; i < _maxSupply.length; i++) {
            total += _maxSupply[i];
        }
        require(total > 0, "Invalid max supply.");

        uint[] memory ids = new uint[](total);
        uint temp = 0;
        for (uint i = 0; i < _tokenIds.length; i++) {
            for (uint j = 0; j < _maxSupply[i]; j++) {
                ids[temp] = _tokenIds[i];
                temp++;
            }
        }

        ids = shuffle(ids);
        collections[_collectionId].id = _collectionId;
        collections[_collectionId].queue = ids;
        collections[_collectionId].tokenIds = _tokenIds;
        collections[_collectionId].tokenMaxSupply = _maxSupply;
        collections[_collectionId].maxSupply = total;
    }

    function setMaxMintPerWallet(uint _collectionId, uint _max) public onlyRole(COLLECTION_SETTER_ROLE) {
        require(collections[_collectionId].id != 0, "Invalid collection id.");
        collections[_collectionId].maxMintPerWallet = _max;
    }

    function activate(uint _collectionId) public onlyRole(COLLECTION_SETTER_ROLE) {
        collections[_collectionId].active = true;
        activeCollections.push(_collectionId);
    }

    function inactivate(uint _collectionId) public onlyRole(COLLECTION_SETTER_ROLE) {
        collections[_collectionId].active = false;
        uint index;
        for (index = 0; index < activeCollections.length; index ++){
            if (activeCollections[index] == _collectionId) {
                break;
            }
        }
        require(index < activeCollections.length, "Collection with that ID was not found.");
        activeCollections[index] = activeCollections[(activeCollections.length - 1)];
        activeCollections.pop();
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function mint(address _account, uint _collectionId, uint _amount, uint _costTokenId)
        public
        whenNotPaused
    {
        require(collections[_collectionId].active, "Not active collection.");
        require((collections[_collectionId].minted + _amount) <= collections[_collectionId].maxSupply, "There is not enough stock.");
        require(costContract.isApprovedForAll(_account, address(this)), "Is not approved for all.");
        if (collections[_collectionId].maxMintPerWallet > 0) {
            require((collections[_collectionId].mintedPerWallet[_account] + _amount) <= collections[_collectionId].maxMintPerWallet, "It exceeds the mint limit for this wallet.");
        }

        int costAmount = collections[_collectionId].cost[_costTokenId];

        require(costAmount != 0, "This token ID is not available for minting.");
        if (costAmount > 0) {
            uint totalCost = uint(costAmount) * _amount;
            require(costContract.balanceOf(_account, _costTokenId) >= totalCost, "The balance is not sufficient.");
            spendableInterface(costContractAddress).spend(_account, _costTokenId, totalCost);
        }

        for (uint256 i = 0; i < _amount; i++) {
            uint tokenId = collections[_collectionId].queue[collections[_collectionId].minted + i];
            _mint(_account, tokenId, 1, '');
        }
        collections[_collectionId].minted += _amount;
        collections[_collectionId].mintedPerWallet[_account] += _amount;
    }

    function ownerMint(address _account, uint _collectionId, uint _amount)
        public
        onlyRole(MINTER_ROLE)
    {
        require((collections[_collectionId].minted + _amount) <= collections[_collectionId].maxSupply, "There is not enough stock.");
        for (uint i = 0; i < _amount; i++) {
            uint tokenId = collections[_collectionId].queue[collections[_collectionId].minted + i];
            _mint(_account, tokenId, 1, '');
        }
        collections[_collectionId].minted += _amount;
    }

    function directMint(address _account, uint _tokenId, uint _amount)
        public
        onlyRole(MINTER_ROLE)
    {
        _mint(_account, _tokenId, _amount, '');
    }

    function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        internal
        whenNotPaused
        override(ERC1155)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    // The following functions are overrides required by Solidity.

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function shuffle(uint[] memory numberArr) internal view returns (uint[] memory) {
        for (uint256 i = 0; i < numberArr.length; i++) {
            uint256 n = i + uint256(keccak256(abi.encodePacked(block.timestamp))) % (numberArr.length - i);
            uint256 temp = numberArr[n];
            numberArr[n] = numberArr[i];
            numberArr[i] = temp;
        }
        return numberArr;
    }

    function systemBurn(
        address account,
        uint256 id,
        uint256 value
    ) public onlyRole(BURNER_ROLE) {
        require(
            isApprovedForAll(account, msg.sender),
            "ERC1155: caller is not token owner nor approved"
        );
        _burn(account, id, value);
    }

    function systemBurnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public onlyRole(BURNER_ROLE) {
        require(
            isApprovedForAll(account, msg.sender),
            "ERC1155: caller is not token owner nor approved"
        );
        _burnBatch(account, ids, values);
    }
}
