    //None group doesn't exist and non claimable
    enum VestingGroup{None, Team, Partners, Seed, Strategic, Ambasador, PreSale, PublicSale, Marketing, Rewards, Liquidity}

    //In one vesting group might be more than one addresses.
    //Addresses in one group have their own consensus about future claims.
    mapping(address => VestingGroup) public vestingAddresses;
    //VestingGroup => (TimeStamp => Available amount)
    mapping(VestingGroup => mapping(uint256 => uint256)) public vestingRule;
    //VestingGroup => Claimed amount
    mapping(VestingGroup => uint256) public vestingClaimed;

    constructor(address _trc, address _from) {
        initializeTimestamps();
        initializeClaimed();
        initializeVestingGroup();
        trc = IERC20(_trc);
        sendFrom = _from;
    }

    modifier addressInVesting() {
        require(vestingAddresses[msg.sender] != VestingGroup.None, "You are not in Vesting group");
        _;
    }

    function claimVesting(uint256 _amount) external addressInVesting nonReentrant {
        uint256 tokens = getAvailableTokens(msg.sender);
        require(_amount <= tokens, "Amount too much for claim");
        SafeERC20.safeTransferFrom(trc, sendFrom, msg.sender, _amount);
        vestingClaimed[vestingAddresses[msg.sender]] += _amount;
    }

    function getAvailableTokens(address _claimer) public view returns (uint256) {
        require(vestingAddresses[_claimer] != VestingGroup.None, "Not allowed for vesting");
        uint256 timeStamp = getClosestBefore();
        uint256 availableTokens = 0;

        availableTokens += vestingRule[vestingAddresses[_claimer]][timeStamp];
        if (availableTokens < vestingClaimed[vestingAddresses[_claimer]]) {
            return 0;
        }
        availableTokens -= vestingClaimed[vestingAddresses[_claimer]];

        return availableTokens;
    }

    //Starts from 0 as 1 October 2023, timestamp would be 1696118400, see code below
    function createVestingRule(VestingGroup _group, uint256 _period, uint256 _amounts) external onlyOwner {
        require(_group != VestingGroup.None, "None group is non claimable");
        vestingRule[_group][timeStamps[_period]] = _amounts;
    }

    function addToVestingGroup(VestingGroup _group, address _addr) external onlyOwner {
        require(_group != VestingGroup.None, "None group is non claimable");
        require(vestingAddresses[_addr] == VestingGroup.None, "Already exist");
        vestingAddresses[_addr] = _group;
    }

    function changeVestingAddress(address _newAddr) external addressInVesting {
        VestingGroup group = vestingAddresses[msg.sender];
        delete vestingAddresses[msg.sender];
        vestingAddresses[_newAddr] = group;
    }

    function deleteFromVestingGroup(address _addr) external onlyOwner {
        require(vestingAddresses[_addr] != VestingGroup.None, "Doesn't exist");
        delete vestingAddresses[_addr];
    }

    function changeSenderFrom(address _newSendFrom) external onlyOwner {
        sendFrom = _newSendFrom;
    }

