    function getClosestBefore() public view returns (uint256) {
        uint256 closestTimestamp = 0;
        for (uint256 i = 0; i < timeStamps.length; i++) {
            if (timeStamps[i] < block.timestamp && (closestTimestamp == 0 || timeStamps[i] > closestTimestamp)) {
                closestTimestamp = timeStamps[i];
            }
        }
        require(closestTimestamp != 0, "No timestamp found before the current block timestamp.");
        return closestTimestamp;
    }

