pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/finance/PaymentSplitter.sol";
import "./AllowList.sol";

/**
 * @dev {ERC1155} token
 */
contract CloserToNicole is ERC1155, Ownable, PaymentSplitter, AllowList {
    string public name = "Closer To Nicole";
    string public symbol = "CTN";

    //EIP-2981
    uint256 private _royaltyBps;
    address private _royaltyRecipient;
    bytes4 private constant _INTERFACE_ID_ROYALTIES_EIP2981 = 0x2a55205a;

    bytes32 private allowList; // merkle root of the allow list including each address and its token allowance
    address[] private payeeAddresses; // addresses which can withdraw from the contract funds
    string public contractURI; // contract level metadata for supporting platforms

    uint16 private constant MINT_ALLOWANCE = 10;

    uint16 private constant SERIES_NUM = 5; // Number of token series
    uint16[] private SERIES_SUPPLY = [1394, 1394, 1393, 1394, 1394]; // Array of the supply of tokens in each series

    mapping(uint256 => uint256) public seriesNumMinted; // Number of tokens each series has minted
    mapping(address => bool) public fanMinted; // Mapping of fans who have minted

    uint256 private mintPriceInWei = 169 ether;

    bool public fanMintingEnabled = false; // toggle to enable the fan presale
    bool public mintingEnabled = false; // toggle to enable the public mint

    mapping(address => uint256) public addressToMinted; // Number of tokens each address has minted from allow list

    /**
     * @param uri Token URI
     * @param _payeeAddresses Addresses which can withdraw funds from this contract
     * @param payeeShares Allotments of each address which can withdraw funds from the contract
     * @param _allowList Merkle proof of the allow list addresses and their allotment
     */
    constructor(
        string memory uri,
        address[] memory _payeeAddresses,
        uint256[] memory payeeShares,
        bytes32 _allowList
    ) ERC1155(uri) PaymentSplitter(_payeeAddresses, payeeShares) {
        allowList = _allowList;
        payeeAddresses = _payeeAddresses;
    }

    /*
     * Mint functions
     */

    /// Mint an AccessToken from the AllowList
    /// @notice Mint an AccessToken if you're in the fan snapshot. Must pay mint fee.
    /// @param merkleProof Merkle proof for your address in the snapshot
    /// @param _id Token series id
    /// @param _count Number of tokens within the series to mint
    /// @param allowance Total number of tokens this address is allowed to mint
    function fanMint(
        bytes32[] memory merkleProof,
        uint256 _id,
        uint256 _count,
        uint256 allowance
    ) external payable {
        require(fanMintingEnabled, "fanMintingEnabled");
        require(_id < SERIES_NUM, "SERIES_NUM");
        require(
            seriesNumMinted[_id] + _count <= SERIES_SUPPLY[_id],
            "SERIES_MAX"
        );
        require(mintPriceInWei * _count == msg.value, "mintPriceInWei");
        require(
            addressToMinted[_msgSender()] + _count <= allowance,
            "Exceeds allowList allowance"
        );
        require(
            isAllocationOnList(allowList, merkleProof, _msgSender(), allowance),
            "allowList"
        );

        seriesNumMinted[_id] += _count;
        addressToMinted[_msgSender()] += _count;
        _mint(_msgSender(), _id, _count, "");
    }

    /// Mint an AccessToken
    /// @notice Mint an AccessToken
    /// @param _id Token series id
    /// @param _count Number of tokens within the series to mint
    function mint(uint256 _id, uint256 _count) public payable {
        require(mintingEnabled, "mintingEnabled");
        require(_id < SERIES_NUM, "SERIES_NUM");
        require(
            seriesNumMinted[_id] + _count <= SERIES_SUPPLY[_id],
            "SERIES_MAX"
        );
        require(mintPriceInWei * _count == msg.value, "mintPriceInWei");
        require(
            addressToMinted[_msgSender()] + _count <= MINT_ALLOWANCE,
            "Exceeds minting allowance"
        );

        seriesNumMinted[_id] += _count;
        addressToMinted[_msgSender()] += _count;
        _mint(_msgSender(), _id, _count, "");
    }

    /// Mint an AccessToken without a mint fee
    /// @notice Mint an AccessToken without a mint fee for giveaways, treasury allocation, etc.
    /// @param _id Token series id
    /// @param _count Number of tokens within the series to mint
    /// @param to Address to send the tokens to
    function mintGiveaway(
        uint256 _id,
        uint256 _count,
        address to
    ) public onlyOwner {
        require(_id < SERIES_NUM, "SERIES_NUM");
        require(
            seriesNumMinted[_id] + _count <= SERIES_SUPPLY[_id],
            "SERIES_MAX"
        );

        seriesNumMinted[_id] += _count;
        _mint(to, _id, _count, "");
    }

    /*
     * View functions
     */

    /// Get the mint price
    /// @notice Gets the token mint price
    /// @return mintPriceInWei token mint price in WEI
    function getMintPriceInWei() public view returns (uint256) {
        return mintPriceInWei;
    }

    /// @dev See {IERC165-supportsInterface}.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            super.supportsInterface(interfaceId) ||
            interfaceId == _INTERFACE_ID_ROYALTIES_EIP2981;
    }

    /// Get the EIP-2981 royalty data
    /// @notice Get the EIP-2981 royalty data
    /// @return _royaltyRecipient Royalty recipient address
    /// @return value value to send to recipient address
    function royaltyInfo(uint256, uint256 value)
        external
        view
        returns (address, uint256)
    {
        return (_royaltyRecipient, (value * _royaltyBps) / 10000);
    }

    /*
     * Owner functions
     */

    /// Set the allowlist merkle root
    /// @notice Set the allow list merkle root
    /// @param _allowList New merkleroot for allowlist use
    function setAllowList(bytes32 _allowList) external onlyOwner {
        allowList = _allowList;
    }

    /// Set the base URI
    /// @notice Set the contract base URI using ERC1155 _setURI
    /// @param _baseURI New base URI. ex: https://example.com/\{id\}.json
    function setBaseURI(string memory _baseURI) external onlyOwner {
        _setURI(_baseURI);
    }

    /// Set contract URI
    /// @notice Set contract URI for supporting platforms
    function setContractURI(string calldata _contractURI) external onlyOwner {
        contractURI = _contractURI;
    }

    /// Update the minting states
    /// @notice Change whether the fan mint or public mint is enabled
    /// @param _fanMintingEnabled Turn the fan mint on or off
    /// @param _mintingEnabled Turn the public mint on or off
    function setMintingStates(bool _fanMintingEnabled, bool _mintingEnabled)
        public
        onlyOwner
    {
        fanMintingEnabled = _fanMintingEnabled;
        mintingEnabled = _mintingEnabled;
    }

    /// Update the token mint price
    /// @notice Sets a new mint price for every token
    /// @param _mintPriceInWei Updated mint price in ETH
    function setMintPriceInWei(uint256 _mintPriceInWei) public onlyOwner {
        mintPriceInWei = _mintPriceInWei;
    }

    /// Update the EIP-2981 royalty data
    /// @notice Update the EIP-2981 royalty data
    /// @param recipient Royalty recipient address
    /// @param bps Royalty rate in basis points
    function setRoyalties(address payable recipient, uint256 bps)
        external
        onlyOwner
    {
        _royaltyRecipient = recipient;
        _royaltyBps = bps;
    }

    /// Withdraw funds
    /// @notice Withdraws using OpenZeppelin PaymentSplitter. Any address initialized on the splitter
    ///         can withdraw their allotment at any time.
    function withdraw() external {
        release(payable(_msgSender()));
    }
}
