    "contracts/Betting.sol": {
      "content": "pragma solidity ^0.8.9;

import { IBetting } from "./interfaces/IBetting.sol";
import { ERC2771ContextUpgradeable } from "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Betting is IBetting, ERC2771ContextUpgradeable {
  IERC20 public token;
  address public admin;
  address public paymaster;

  // gameId -> created
  mapping(string => bool) public games;
  // gameId -> number of bets made
  mapping(string => uint) public betsMadePerGame;
  // bettor -> gameId -> amount -> picks
  mapping(address => mapping(string => mapping(uint => uint[]))) public bets;
  // gameId -> bettors
  mapping(string => address[]) public bettors;
  // gameId -> bettor -> amount
  mapping(string => mapping(address => uint)) betAmounts;

  /*
   * @dev this is necessary to allow relayed calls from biconomy
   * @param _trustedForwarder - the forwarding contract authorised to relay transactions for this contract
   * @param _token - the token used for zedrun (should be WETH)
   * @param _perionTreasury - the perion treasury contract that collects commission
   */
  constructor(
    address _trustedForwarder,
    IERC20 _token,
    address _paymaster,
    address _admin
  ) ERC2771ContextUpgradeable(_trustedForwarder) {
    token = _token;
    paymaster = _paymaster;
    admin = _admin;
  }

  /*
   * @dev see IBetting.sol
   */
  function changePaymaster(address newPaymaster) external {
    require(
      _msgSender() == admin,
      "Betting: only admin can change the paymaster"
    );
    paymaster = newPaymaster;
  }

  /*
   * @dev create a new bet pool for a particular game and amount
   * @dev reverts if this pool already exists
   * @param gameId - the string identifying the game
   * @returns - true if successful
   */
  function _createBetPool(string calldata gameId) private returns (bool) {
    require(!games[gameId], "Betting: duplicate bet pool");
    games[gameId] = true;
    emit CreatedBetPool(gameId);

    return true;
  }

  /*
   * @dev see IBetting.sol
   */
  function closeBetPool(string calldata gameId) external returns (bool) {
    require(
      _msgSender() == paymaster,
      "Betting: only paymaster can close pools"
    );
    require(
      betsMadePerGame[gameId] == 1,
      "Betting: cannot close pool with bets"
    );
    // refund the sole bettor
    address bettor = bettors[gameId][0];
    uint amount = betAmounts[gameId][bettor];
    token.transfer(bettor, amount);
    // prevent accidental re-closure of the betting pool
    betsMadePerGame[gameId] = 0;
    emit PoolClosed(gameId);

    return true;
  }

  /*
   * @dev see IBetting.sol
   */
  function bet(
    string calldata gameId,
    uint amount,
    uint pick
  ) external returns (bool) {
    if (!games[gameId]) {
      _createBetPool(gameId);
    }
    require(token.transferFrom(_msgSender(), address(this), amount));
    bets[_msgSender()][gameId][amount].push(pick);
    betsMadePerGame[gameId] += 1;
    bettors[gameId].push(_msgSender());
    betAmounts[gameId][_msgSender()] += amount;
    emit Bet(gameId, _msgSender(), amount);

    return true;
  }

  /*
   * @dev see IBetting.sol
   */
  function getBets(
    address bettor,
    string calldata gameId,
    uint amount
  ) external view returns (uint[] memory) {
    uint length = bets[bettor][gameId][amount].length;
    uint[] memory result = new uint256[](length);
    for (uint i = 0; i < length; i++) {
      result[i] = bets[bettor][gameId][amount][i];
    }

    return result;
  }

  /*
   * @dev see IBetting.sol
   */
  function payout(
    string calldata gameId,
    address[] calldata recipients,
    uint[] calldata amounts
  ) external returns (bool) {
    require(
      _msgSender() == paymaster,
      "Betting: only the paymaster can reward winners"
    );
    for (uint i = 0; i < recipients.length; i++) {
      require(token.transfer(recipients[i], amounts[i]));
      emit RewardPaid(gameId, recipients[i], amounts[i]);
    }

    return true;
  }
}
