      "content": "pragma solidity ^0.8.9;

interface IBetting {
  event CreatedBetPool(string indexed gameId);
  event Bet(string indexed gameId, address indexed bettor, uint amount);
  event PoolClosed(string indexed gameId);
  event RewardPaid(string indexed gameId, address indexed payee, uint amount);

  /*
   * @dev helper function to retrieve bet data from the bets mapping
   * @param bettor - the bettor address
   * @param gameId - the string identifying the game
   * @param amount - the pot amount e.g. $5
   * @returns - An array containing the number of bets and picks
   */
  function getBets(
    address bettor,
    string calldata gameId,
    uint amount
  ) external view returns (uint[] memory);

  /*
   * @dev closes the bet pool and issues a refund if there is only one bettor
   * @dev anyone can call
   * @dev reverts if the time limit has not been reached
   * @param gameId - the string identifying the game
   * @returns - true if successful
   */
  function closeBetPool(string calldata gameId) external returns (bool);

  /*
   * @dev bet on a particular game
   * @dev reverts if the time limit has been reached
   * @param gameId - the string identifying the game
   * @param amount - the pot amount e.g. 0.001 WETH
   * @param pick - the bettor's pick
   * @returns - true if successful
   */
  function bet(
    string calldata gameId,
    uint amount,
    uint pick
  ) external returns (bool);

  /*
   * @dev distribute payouts for a game
   * @dev can only be called by the admin
   * @param gameId - the string identifying the game
   * @param recipients - an array of recipients to be paid out (including commission)
   * @param amounts - the amount that each recipient should receive
   * @returns - true if successful
   */
  function payout(
    string calldata gameId,
    address[] calldata recipients,
    uint[] calldata amounts
  ) external returns (bool);

  /*
