pragma solidity 0.8.7;









/// @title Team token creator contract
contract USFLTeam is ERC1155, NativeMetaTransaction, ContextMixin, Royalties, Ownable, ReentrancyGuard, Pausable {
    uint256 public immutable tokenPrice;
    uint256 public immutable maxPurchaseSupply;
    uint256 public immutable maxFanSupply;
    uint256 public immutable maxTokenId;
    address public openseaProxyAddress;
    address public fanTokenAddress;
    address public bclWalletAddress;
    string public name;
    string public symbol;

    mapping(uint256 => uint256) public swappedFanTokens;
    mapping(uint256 => uint256) public totalPurchaseSupply;
    mapping(uint256 => uint256) public totalFanSupply;

    struct DeploymentParams {
        uint256 maxPurchaseSupply;
        uint256 maxFanSupply;
        uint256 maxTokenId;
        uint256 tokenPrice;
        uint96 royaltyFraction;
        address fanTokenAddress;
        address openseaProxyAddress;
        address bclWalletAddress;
        address adminKey;
        string name;
        string symbol;
        string version;
        string baseURI;
    }

    constructor(DeploymentParams memory data)
        ERC1155(data.baseURI)
        NativeMetaTransaction(data.name, data.version)
        Royalties(
            data.royaltyFraction,
            data.bclWalletAddress // BCL address
        )
    {
        maxPurchaseSupply = data.maxPurchaseSupply;
        maxFanSupply = data.maxFanSupply;
        maxTokenId = data.maxTokenId;
        tokenPrice = data.tokenPrice;
        fanTokenAddress = data.fanTokenAddress;
        openseaProxyAddress = data.openseaProxyAddress;
        bclWalletAddress = data.bclWalletAddress;
        name = data.name;
        symbol = data.symbol;

        _transferOwnership(data.adminKey);
    }

    /**
     * @dev Updates token base URI
     * @param _baseURI New URI to be set
     */
    function setURI(string memory _baseURI) external onlyOwner {
        _setURI(_baseURI);
    }

    /**
     * @dev Returns metadata URI for the given token ID
     * @param tokenId Token ID
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked(super.uri(tokenId), Strings.toString(tokenId)));
    }

    /**
     * @dev Updates opensea proxy address
     * @param _openseaProxyAddress New opensea proxy address
     */
    function setOpenseaProxyAddress(address _openseaProxyAddress) external onlyOwner {
        openseaProxyAddress = _openseaProxyAddress;
    }

    /**
     * @dev Accepts payment tokens and mints token of token type `tokenId`
     * @param account Account address for which tokens are created
     * @param tokenId Token ID
     */
    function mint(address account, uint256 tokenId) external payable nonReentrant whenNotPaused {
        require(tokenPrice <= msg.value, "Incorrect price");
        require(tokenId <= maxTokenId, "Incorrect token ID");
        
        // Comparison happens BEFORE increment, post-increment will update storage value after check
        require(totalPurchaseSupply[tokenId]++ < maxPurchaseSupply, "Purchase would exceed max purchase supply");

        _mint(account, tokenId, 1, "");

        (bool sent, ) = payable(bclWalletAddress).call{value: tokenPrice}("");
        require(sent, "Failed to send Ether");
    }

    /**
     * @dev Swaps a fan pass token for a team token of type `tokenId`
     * @param tokenId Token ID
     */
    function swapFanPassToken(uint256 tokenId) external nonReentrant whenNotPaused {
        require(tokenId <= maxTokenId, "Incorrect token ID");
        
        // Comparison happens BEFORE increment, post-increment will update storage value after check
        require(totalFanSupply[tokenId]++ < maxFanSupply, "Purchase would exceed max fan supply");

        IUSFLFanPass(fanTokenAddress).burn(_msgSender());

        _mint(_msgSender(), tokenId, 1, "");
    }

    /**
     * @dev Destroys a token from address `account` of token type `tokenId`
     * @param account Account address
     * @param tokenId Token ID
     */
    function burn(address account, uint256 tokenId) external onlyOwner {
        _burn(account, tokenId, 1);
    }

    /**
     * @dev Transfers available payment tokens from contract address to the contact owner address
     * @param _paymentTokenAddress Payment token address
     */
    function withdrawContractTokens(address _paymentTokenAddress) external onlyOwner nonReentrant {
        require(
            IERC20(_paymentTokenAddress).transfer(_msgSender(), IERC20(_paymentTokenAddress).balanceOf(address(this))),
            "Failed to withdraw"
        );
    }

    /**
     * @dev Transfers contract available ether balance to the contact owner address
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool sent, ) = payable(_msgSender()).call{value: balance}("");
            require(sent, "Failed to send Ether");
        }
    }

    /**
     * @dev Unpause the contract
     */
    function activateSale() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Pause the contract
     */
    function pauseSale() external onlyOwner {
        _pause();
    }

    /**
     * @dev Sets royalty information - royalty receiver and fraction
     * @param _receiver Royalty amount receiver
     * @param _royaltyFraction Royalty fraction to be received
     */
    function setRoyaltyInfo(address _receiver, uint96 _royaltyFraction) external onlyOwner {
        _setDefaultRoyalty(_receiver, _royaltyFraction);
    }

    /**
     * @dev Sets BCL wallet address
     * @param _bclWalletAddress New BCL address
     */
    function setBCLWalletAddress(address _bclWalletAddress) external onlyOwner {
        bclWalletAddress = _bclWalletAddress;
    }

    /**
     * @dev Sets BCL wallet address
     * @param _fanTokenAddress New fan pass token contract address address
     */
    function setFanTokenAddress(address _fanTokenAddress) external onlyOwner {
        fanTokenAddress = _fanTokenAddress;
    }

    /**
     * @dev This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
     */
    function _msgSender() internal view override returns (address sender) {
        return ContextMixin.msgSender();
    }

    /**
     * @dev As another option for supporting trading without requiring meta transactions, override isApprovedForAll to whitelist OpenSea proxy accounts on Matic
     */
    function isApprovedForAll(address _owner, address _operator) public view override returns (bool isOperator) {
        // if OpenSea's ERC1155 Proxy Address is detected, auto-return true
        if (_operator == openseaProxyAddress) {
            return true;
        }

        return ERC1155.isApprovedForAll(_owner, _operator);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view override(Royalties, ERC1155) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
