// File: contracts/TESTINGERC721.sol

pragma solidity ^0.8.15;

contract Racemates is ERC721, ERC721Enumerable, Pausable, Ownable, ReentrancyGuard, ERC2981 {
    using Counters for Counters.Counter;
    using Strings for uint256;

    bool public isOperatorProxyActive = true;
    bool public publicSale = false;
    // Public Minting Variables
    uint256 public price = 1000 ether;
    uint256 public maxMintingNumAllowed = 1;
    // URL
    string public baseURI;
    string public fileExtension = ".json";
    // Proxy Address
    address public operatorProxyAddress;

    Counters.Counter private _tokenIdCounter;

    constructor(address _newOwner) ERC721("Racemates", "RM") {
        operatorProxyAddress = 0x58807baD0B376efc12F5AD86aAc70E78ed67deaE;
        setDefaultRoyalty(_newOwner, 1000);
        transferOwnership(_newOwner);
    }

    // Public Mint Function
    function mint(uint256 _quantity) external payable {
        require(publicSale, "Public sale not started");
        require(_quantity > 0, "need to mint at least 1 NFT");
        require(
            _quantity <= maxMintingNumAllowed,
            "exceeds max NFTs per transaction"
        );
        uint256 totalCost = price * _quantity;
        require(totalCost <= msg.value, "insufficient funds");
        for (uint256 i = 0; i < _quantity; i++) {
            safeMint(msg.sender);
        }
    }

    // Airdrop Function
    function airdrop(address _to, uint256 _quantity) external onlyOwner {
        require(_quantity > 0, "need to mint at least 1 NFT");
        for (uint256 i = 0; i < _quantity; i++) {
            safeMint(_to);
        }
    }

    // Mint as Owner
    function mintOwner(uint256 _quantity) external onlyOwner {
        require(_quantity > 0, "need to mint at least 1 NFT");
        for (uint256 i = 0; i < _quantity; i++) {
            safeMint(msg.sender);
        }
    }

    // Set Base URI
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    // Set File Extension
    function setFileExtension(string memory _fileExtension) public onlyOwner {
        fileExtension = _fileExtension;
    }

    function _baseURI() internal pure override returns (string memory) {
        return "CHANGE BEFORE DEPLOYMENT";
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function safeMint(address to) internal {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
    }

    // get Token URI
    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        string memory _tokenURI = string(
            abi.encodePacked(baseURI, _tokenId.toString(), fileExtension)
        );

        return _tokenURI;
    }

        // Set the allowed proxy address
    function setOperatorProxyAddress(address _operatorProxyAddress)
        public
        onlyOwner
    {
        operatorProxyAddress = _operatorProxyAddress;
    }

    // Set the operator proxy active or inactive
    function setIsOperatorProxyActive() external onlyOwner {
        isOperatorProxyActive = !isOperatorProxyActive;
    }

    // Set the public sale active or inactive
    function setPublicSale() external onlyOwner {
        publicSale = !publicSale;
    }

    // Set the price
    function setPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function setMaxMintingNumAllowed(uint256 _maxMintingNumAllowed)
        public
        onlyOwner
    {
        maxMintingNumAllowed = _maxMintingNumAllowed;
    }

    // Withdraw funds
    function withdraw() public onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No polygon left to withdraw");
        (bool success, ) = (msg.sender).call{value: balance}("");
        require(success, "Transfer failed.");
    }

    // Withdraw ERC20 tokens
    function withdrawTokens(address _addr) public onlyOwner nonReentrant {
        IERC20 _token = IERC20(_addr);
        uint256 balance = _token.balanceOf(address(this));
        require(balance > 0, "No tokens left to withdraw");
        bool success = _token.transfer(msg.sender, balance);
        require(success, "Transfer failed.");
    }

    // Set the royaltys
    function setDefaultRoyalty(address receiver, uint96 feeNumerator)
        public
        onlyOwner
    {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    // Is contract or address approved to transfer token
    function isApprovedForAll(address _owner, address _operator)
        public
        view
        override(ERC721, IERC721)
        returns (bool isOperator)
    {
        if (
            isOperatorProxyActive && _operator == address(operatorProxyAddress)
        ) {
            return true;
        }
        return ERC721.isApprovedForAll(_owner, _operator);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721, ERC721Enumerable) whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    // The following functions are overrides required by Solidity.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
