    "contracts/Steakhouse/ZoomiesSwap.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

interface IRouter {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function getAmountsOut(
        uint256 amountIn,
        address[] calldata path
    ) external view returns (uint256[] memory amounts);
}

interface NftS {
    function ownerOf(uint256 _tokenId) external view returns (address);
    function balanceOf(address _account) external view returns (uint256);
}

contract ZoomiesSwap is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    NftS public Nfts;
    address private nftAddress;

    struct NftCondition {
        address nftAddress;
        uint256 balanceThreshold;
        uint fee;
    }

    struct LeaderboardEntry {
        address user;
        address token;
        uint256 amountReceived;
    }

    LeaderboardEntry[] public leaderboard;

    NftCondition[] public nftConditions;

    uint public feePercent = 100; // 1%

    address public feeAccount;

    event TokensSwapped(
        address indexed sender,
        address tokenA,
        address tokenB,
        uint256 amountIn
    );

    address public router;
    address public native = 0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270;
    address public usdc;
    address public zoom = 0x698816984bF59f647C0f16869eC3f8CB815CcCbb;

    mapping(address => bool) public whitelistedTokens;

    bool private locked;

    constructor(
        address _router,
        address _usdc
    ) Ownable() {
        feeAccount = msg.sender;
        router = _router;
        usdc = _usdc;
    }

    function setTokenAddresses(
        address _native,
        address _usdc,
        address _zoom
    ) external onlyOwner {
        native = _native;
        usdc = _usdc;
        zoom = _zoom;
    }

    function addNftCondition(
        address _nftAddress,
        uint256 _balanceThreshold,
        uint256 _fee
    ) external onlyOwner {
        nftConditions.push(
            NftCondition({
                nftAddress: _nftAddress,
                balanceThreshold: _balanceThreshold,
                fee: _fee
            })
        );
    }

    function updateNftCondition(
        uint256 _index,
        address _nftAddress,
        uint256 _balanceThreshold,
        uint256 _fee
    ) external onlyOwner {
        require(_index < nftConditions.length, "Invalid index");

        nftConditions[_index] = NftCondition({
            nftAddress: _nftAddress,
            balanceThreshold: _balanceThreshold,
            fee: _fee
        });
    }

    function deleteNftCondition(uint256 _index) external onlyOwner {
        require(_index < nftConditions.length, "Invalid index");

        if (_index != nftConditions.length - 1) {
            nftConditions[_index] = nftConditions[nftConditions.length - 1];
        }

        nftConditions.pop();
    }

    function getTop25Leaderboard(
        address _token
    ) public view returns (LeaderboardEntry[] memory) {
        LeaderboardEntry[] memory topEntries = new LeaderboardEntry[](25);
        uint256 numEntries = 0;

        for (uint256 i = 0; i < leaderboard.length && numEntries < 25; i++) {
            if (leaderboard[i].token == _token) {
                topEntries[numEntries] = leaderboard[i];
                numEntries++;
            }
        }

        return topEntries;
    }

    function resetLeaderboard() external onlyOwner {
        delete leaderboard;
    }

function calculateLowestFee(address user) internal view returns (uint256) {
    uint256 lowestFee = feePercent; // Start with the default fee

    for (uint256 i = 0; i < nftConditions.length; i++) {
        NftCondition memory condition = nftConditions[i];
        uint256 userBalance = NftS(condition.nftAddress).balanceOf(user);

        if (
            userBalance >= condition.balanceThreshold &&
            condition.fee < lowestFee
        ) {
            lowestFee = condition.fee;
        }
    }

    return lowestFee;
}


    modifier noReentrancy() {
        require(!locked, "Reentrant call");
        locked = true;
        _;
        locked = false;
    }

    function getMyFeePercent(address user) external view returns (uint256) {
        return calculateLowestFee(user);
    }

    function calculateFeeBasedOnNFTConditions(
        address user,
        uint256 _amountIn
    ) internal view returns (uint256) {
        uint256 lowestFee = type(uint256).max;

        for (uint256 i = 0; i < nftConditions.length; i++) {
            NftCondition memory condition = nftConditions[i];
            uint256 userBalance = NftS(condition.nftAddress).balanceOf(user);

            if (
                userBalance >= condition.balanceThreshold &&
                condition.fee < lowestFee
            ) {
                lowestFee = condition.fee;
            }
        }

        if (lowestFee == 0) {
            return 0;
        } else if (lowestFee != type(uint256).max) {
            return (_amountIn * lowestFee) / 10000;
        } else {
            return (_amountIn * feePercent) / 10000;
        }
    }

    function swapTokens(
        address _tokenA,
        address _tokenB,
        uint256 _amountIn
    ) external payable {
        address[] memory path = new address[](2);
        if (_tokenA == address(0)) {
            path[0] = native;
            path[1] = _tokenB;
        } else if (_tokenB == address(0)) {
            path[0] = _tokenA;
            path[1] = native;
        } else {
            path[0] = _tokenA;
            path[1] = _tokenB;
        }

        _proceedToUniswap(_tokenA, _tokenB, _amountIn, path);
        emit TokensSwapped(msg.sender, _tokenA, _tokenB, _amountIn);
    }

    function customSwapTokens(
        address _tokenA,
        address _tokenB,
        uint256 _amountIn,
        address[] memory path
    ) external payable {
        if (_tokenA == address(0)) {
            require(
                native == path[0] && _tokenB == path[path.length - 1],
                "ZoomSwap: Path must be specified"
            );
        } else if (_tokenB == address(0)) {
            require(
                _tokenA == path[0] && native == path[path.length - 1],
                "ZoomSwap: Path must be specified"
            );
        } else {
            require(
                _tokenA == path[0] && _tokenB == path[path.length - 1],
                "ZoomSwap: Path must be specified"
            );
        }
        _proceedToUniswap(_tokenA, _tokenB, _amountIn, path);
    }

    function _proceedToUniswap(
        address _tokenA,
        address _tokenB,
        uint256 _amountIn,
        address[] memory path
    ) internal {
        require(_amountIn > 0, "ZoomSwap: amountIn must be greater than 0");
        uint256 fee = calculateFeeBasedOnNFTConditions(msg.sender, _amountIn);
        uint256 amountToSwap;

        if (_tokenA == address(0)) {
            require(_amountIn >= msg.value, "ZoomSwap: Insufficient ETH");
            if (fee > 0) {
                payable(feeAccount).transfer(fee);
            }
            amountToSwap = _amountIn - fee;
            _swapETHForTokens(amountToSwap, path);
        } else {
            IERC20(_tokenA).safeTransferFrom(
                msg.sender,
                address(this),
                _amountIn
            );

            if (fee > 0) {
                IERC20(_tokenA).safeTransfer(feeAccount, fee);
            }
            amountToSwap = _amountIn - fee;
            if (_tokenB == address(0)) {
                _swapTokensForETH(amountToSwap, path);
            } else {
                _swapTokensForTokens(amountToSwap, path);
            }
        }
    address lastToken = path[path.length - 1];
    if (lastToken == zoom) {
        uint amountReceived = _getReceivedAmount(path, amountToSwap);
        bool existingEntry = false;
        for (uint256 i = 0; i < leaderboard.length; i++) {
            if (leaderboard[i].user == msg.sender && leaderboard[i].token == lastToken) {
                leaderboard[i].amountReceived += amountReceived;
                existingEntry = true;
                break;
            }
        }

        if (!existingEntry) {
            leaderboard.push(LeaderboardEntry(msg.sender, lastToken, amountReceived));
        }
        }
    }

    function _getReceivedAmount(
        address[] memory path,
        uint256 amountIn
    ) internal view returns (uint256) {
        uint256[] memory outputs = IRouter(router).getAmountsOut(
            amountIn,
            path
        );
        return outputs[outputs.length - 1];
    }

    function _approve(address _token, uint _amount) internal {
        IERC20(_token).approve(address(router), _amount);
    }

    function _swapTokensForETH(uint _amount, address[] memory path) internal {
        IRouter(router).swapExactTokensForETH(
            _amount,
            0,
            path,
            msg.sender,
            block.timestamp
        );
    }

    function _swapETHForTokens(uint _amount, address[] memory path) internal {
        IRouter(router).swapExactETHForTokens{value: _amount}(
            0,
            path,
            msg.sender,
            block.timestamp
        );
    }

    function _swapTokensForTokens(
        uint _amount,
        address[] memory path
    ) internal {
        IRouter(router).swapExactTokensForTokens(
            _amount,
            0,
            path,
            msg.sender,
            block.timestamp
        );
    }

    function getOutputTokenAmount(
        uint inputAmount,
        address[] memory path
    ) external view returns (uint outputAmount) {
        uint[] memory outputs = IRouter(router).getAmountsOut(
            inputAmount,
            path
        );
        return outputs[outputs.length - 1];
    }



    //== General administrative things ==//

    function setFeePercent(uint _feePercent) external onlyOwner {
        feePercent = _feePercent;
    }

    function setFeeAccount(address _feeAccount) external onlyOwner {
        feeAccount = _feeAccount;
    }

    function setRouter(address _router) external onlyOwner {
        router = _router;
    }

    function addWhitelistedTokens(address[] memory _tokens) external onlyOwner {
        for (uint i = 0; i < _tokens.length; i++) {
            whitelistedTokens[_tokens[i]] = true;
            if (_tokens[i] != address(0)) {
                _approve(_tokens[i], type(uint256).max);
            }
        }
    }

    function removeWhitelistedTokens(
        address[] memory _tokens
    ) external onlyOwner {
        for (uint i = 0; i < _tokens.length; i++) {
            whitelistedTokens[_tokens[i]] = false;
        }
    }

    function approveTokens(
        address[] memory _tokens,
        uint[] memory _amounts
    ) external onlyOwner {
        for (uint i = 0; i < _tokens.length; i++) {
            _approve(_tokens[i], _amounts[i]);
        }
    }

    function emergencyWithdraw(
        address _token,
        uint _amount
    ) external onlyOwner {
        IERC20(_token).safeTransfer(msg.sender, _amount);
    }

    function emergencyWithdrawNative(uint _amount) external onlyOwner {
        payable(owner()).transfer(_amount);
    }

    function getDecimals(address token) internal view returns (uint8) {
        (bool success, bytes memory data) = token.staticcall(
            abi.encodeWithSignature("decimals()")
        );
        if (success && data.length >= 36) {
            return abi.decode(data, (uint8));
        } else {
            return 18;
        }
    }
}
