pragma solidity ^0.8.0;

contract InventoryAccessControl is AccessControl {
    bytes32 public constant MINTER_ROLE = 0x9f2df0fed2c77648de5860a4cc508cd0818c85b8b8a1ab4ceeef8d981c8956a6; // keccak256(abi.encodePacked("MINTER_ROLE"));
    bytes32 public constant MINTER_ROLE_ADMIN = 0x11c2020b6b4f4e00c2410234e0c72636b4739cf7cda4d8e24ef6b881350e6704; //keccak256(abi.encodePacked("MINTER_ROLE_ADMIN"));
    bytes32 public constant MANAGER_ROLE = 0x241ecf16d79d0f8dbfb92cbc07fe17840425976cf0667f022fe9877caa831b08; // keccak256(abi.encodePacked("MANAGER_ROLE"));

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()));
        _;
    }

    modifier onlyMinter() {
        require(hasRole(MINTER_ROLE, _msgSender()));
        _;
    }

    modifier onlyMinterAdmin() {
        require(hasRole(MINTER_ROLE_ADMIN, _msgSender()));
        _;
    }

    modifier onlyManager() {
        require(hasRole(MANAGER_ROLE, _msgSender()));
        _;
    }

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MINTER_ROLE_ADMIN, _msgSender());
        _setupRole(MANAGER_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
    }
}


// File contracts/token/ERC1155/TowerInventory.sol
pragma solidity ^0.8.0;





/*
 * @title TowerInventory
 * @dev This contract is used to manage the inventory of the tower related NFTs.
 */
contract TowerInventory is ERC1155Pausable, InventoryAccessControl {
    using ECDSA for bytes32;

    string public name;
    string public symbol;

    // EOA to verify the signature for minting tokens
    address public signer;
    // seed nonce while process minting tokens
    mapping(address => uint256) public seedNonce;

    mapping(uint256 => string) public tokenTypes; //  token type id => token type name

    /*
     * Events emitted by the contract for operations
     */
    event SetSigner(address indexed _signer, uint256 _blockTimestamp);
    event VerifiedMint(address indexed _sender, uint256[] _tokenIds, uint256[] _amounts);
    event InternalMint(address indexed _to, uint256[] _tokenIds, uint256[] _amounts);

    /*
     * Set signer that verifies the signature for minting tokens
     * @param _signer EOA to verify the signature for minting tokens
     */
    function setSigner(address _signer) external onlyAdmin {
        signer = _signer;
        emit SetSigner(_signer, block.timestamp);
    }

    /*
     * Get signer in the contract
     */
    function getSigner() external view onlyAdmin returns (address) {
        return signer;
    }

    /* Set base URL for the token metadata
     * Note: Only admin or manager can call this function.
     * @param _baseUrl Base URL for the token metadata
     */
    function setBaseUri(string memory _uri) external onlyAdmin onlyManager {
        _setURI(_uri);
    }

    /*
     * get url address for the type of token based on tokenId
     * @param _tokenId: token id to get the url address
     * @returns url address for the type of token based on tokenId
     */
    function uri(uint256 tokenId) public view virtual override returns (string memory) {
        string memory tokenUri = super.uri(tokenId);
        string memory tokenIdString = tokenTypes[tokenId];
        return string(abi.encodePacked(tokenUri, tokenIdString, ".json"));
    }

    /*
     * Set token type name for the token type ID
     * Note: Only admin or manager can call this function.
     * @param _tokenTypeId: token type id to set the token type name
     * @param _tokenTypeName: token type name to set for the token type id
     */
    function setTokenTypes(
        uint256 _tokenTypeId,
        string memory _tokenTypeName,
        string memory _uri
    ) external onlyAdmin onlyManager {
        _setURI(_uri);
        setOnlyTokenTypes(_tokenTypeId, _tokenTypeName);
    }

    function setOnlyTokenTypes(uint256 _tokenTypeId, string memory _tokenTypeName) internal {
        require(bytes(tokenTypes[_tokenTypeId]).length == 0, "Token Type already exists");
        _setTokenTypes(_tokenTypeId, _tokenTypeName);
    }

    function updateTokenTypes(uint256 _tokenTypeId, string memory _tokenTypeName) external onlyAdmin onlyManager {
        require(bytes(tokenTypes[_tokenTypeId]).length > 0, "Token Type already exists");
        _setTokenTypes(_tokenTypeId, _tokenTypeName);
    }

    function _setTokenTypes(uint256 _tokenTypeId, string memory _tokenTypeName) internal {
        tokenTypes[_tokenTypeId] = _tokenTypeName;
    }

    /*
     * Check if the call has valid signature generated from the signer
     * @param _tokenIds: token ids
     * @param _amounts: amounts of tokens to be minted
     * @param _sender: sender of the call
     * @param _signature: signature generated from the signer
     *
     * @returns true if the call has valid signature generated from the signer
     */
    function checkVerification(
        uint256[] memory _tokenIds,
        uint256[] memory _amounts,
        address _sender,
        bytes memory _signature
    ) public view returns (bool) {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            require(bytes(tokenTypes[_tokenIds[i]]).length > 0, "Token Type does not exist");
        }

        uint256 nonce = seedNonce[_sender];
        bytes32 hash = keccak256(abi.encodePacked(_sender, _tokenIds, _amounts, nonce));
        require(hash.toEthSignedMessageHash().recover(_signature) == signer, "Invalid Signature");
        return true;
    }

    /*
     * Batch mint tokens if minting is not done through backend verified signature
     * Note: Only minter or minter admin can mint
     * @param _tokenIds: token ids
     * @param _amounts: amounts of tokens to be minted
     * @param _sender: sender of the call
     *
     * @emit InternalMint: emit event when minting tokens
     */
    function batchMint(
        uint256[] memory _tokenIds,
        uint256[] memory _amounts,
        address _to
    ) external onlyMinter onlyMinterAdmin {
        _mintBatch(_to, _tokenIds, _amounts, "0x");
        emit InternalMint(_to, _tokenIds, _amounts);
    }

    /*
     * Batch mint tokens with specified amount to destination addresses
     * Note: Only minter or minter admin can mint
     * @param _to: receivers of the minted tokens
     * @param _tokenIds: token ids
     * @param _amounts: amounts of tokens to be minted
     *
     * @emit InternalMint: emit event when minting tokens
     */
    function batchMintMultiple(
        address[] memory _to,
        uint256[][] memory _tokenIds,
        uint256[][] memory _amounts
    ) external onlyMinter onlyMinterAdmin {
        for (uint256 i = 0; i < _to.length; i++) {
            _mintBatch(_to[i], _tokenIds[i], _amounts[i], "0x");
            emit InternalMint(_to[i], _tokenIds[i], _amounts[i]);
        }
    }

    /*
     * Verify and mint the tokens based on the signature generated from backend
     * @param _tokenIds: token ids
     * @param _amounts: amounts of tokens to be minted
     * @param _sender: sender of the call
     * @param _signature: signature generated by the signer from backend
     */
    function verifyAndMint(
        uint256[] memory _tokenIds,
        uint256[] memory _amounts,
        address _sender,
        bytes memory _signature
    ) external whenNotPaused {
        require(checkVerification(_tokenIds, _amounts, _sender, _signature));

        seedNonce[_sender]++;
        if (_tokenIds.length > 1) {
            _mintBatch(_sender, _tokenIds, _amounts, "0x");
        } else {
            _mint(_sender, _tokenIds[0], _amounts[0], "0x");
        }
        emit VerifiedMint(_sender, _tokenIds, _amounts);
    }

    /* Pause the contract from minting or transfers
     * Note: Only admin or manager can call this function when not paused
     */
    function pause() external onlyAdmin onlyManager whenNotPaused {
        _pause();
    }

    /* Unpause the contract from minting or transfers
     * Note: Only admin or manager can call this function when paused
     */
    function unpause() external onlyAdmin onlyManager whenPaused {
        _unpause();
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /*
     * Constructor of the contract
     * @param _uri: base url for the token metadata
     */
    constructor(
        string memory _name,
        string memory _symbol,
        address _signer,
        string memory _uri
    ) ERC1155(_uri) InventoryAccessControl() {
        name = _name;
        symbol = _symbol;
        signer = _signer;

        _setTokenTypes(1, "gold-ticket");
        _setTokenTypes(2, "silver-ticket");
        _setTokenTypes(3, "bronze-ticket");
        _setTokenTypes(4, "miraculous-angel-female");
        _setTokenTypes(5, "divine-champion-male");
        _setTokenTypes(6, "sparkling-fairy-female");
        _setTokenTypes(7, "arch-mage-male");
        _setTokenTypes(8, "fantasy-healer-female");
        _setTokenTypes(9, "magical-warrior-male");
        _setTokenTypes(10, "assassin-male");
        _setTokenTypes(11, "assassin-female");
        _setTokenTypes(12, "captain-female");
        _setTokenTypes(13, "captain-male");
        _setTokenTypes(14, "soldier-female");
        _setTokenTypes(15, "solider-male");
        _setTokenTypes(16, "rouge-female");
        _setTokenTypes(17, "rouge-male");
        _setTokenTypes(18, "bard-female");
        _setTokenTypes(19, "bard-male");
        _setTokenTypes(20, "phantom-queen-female");
        _setTokenTypes(21, "soul-harvester-male");
        _setTokenTypes(22, "map-common");
        _setTokenTypes(23, "map-rare");
        _setTokenTypes(24, "map-epic");
        _setTokenTypes(25, "map-legendary");
    }
}
